import{_ as p,o as e,c as t,a as r}from"./app.303cfecc.js";const f=JSON.parse('{"title":"0 \u5F00\u7BC7\u8BCD | \u4E3A\u4EC0\u4E48\u4F60\u8981\u5B66\u4E60\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\uFF1F","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8BFE\u7A0B\u8BBE\u8BA1","slug":"\u8BFE\u7A0B\u8BBE\u8BA1","link":"#\u8BFE\u7A0B\u8BBE\u8BA1","children":[]},{"level":2,"title":"\u5199\u5728\u6700\u540E","slug":"\u5199\u5728\u6700\u540E","link":"#\u5199\u5728\u6700\u540E","children":[]}],"relativePath":"0002/00\u5F00\u7BC7/00-\u5F00\u7BC7\u8BCD-\u4E3A\u4EC0\u4E48\u4F60\u8981\u5B66\u4E60\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\uFF1F.md","lastUpdated":1680763907000}'),a={name:"0002/00\u5F00\u7BC7/00-\u5F00\u7BC7\u8BCD-\u4E3A\u4EC0\u4E48\u4F60\u8981\u5B66\u4E60\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\uFF1F.md"},s=r('<h1 id="_0-\u5F00\u7BC7\u8BCD-\u4E3A\u4EC0\u4E48\u4F60\u8981\u5B66\u4E60\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1" tabindex="-1">0 \u5F00\u7BC7\u8BCD | \u4E3A\u4EC0\u4E48\u4F60\u8981\u5B66\u4E60\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\uFF1F <a class="header-anchor" href="#_0-\u5F00\u7BC7\u8BCD-\u4E3A\u4EC0\u4E48\u4F60\u8981\u5B66\u4E60\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1" aria-hidden="true">#</a></h1><p>\u4F60\u597D\uFF0C\u6211\u662F\u5510\u626C\uFF0C\u73B0\u5728\u5728\u7F8E\u56FE\u516C\u53F8\u4EFB\u804C\u6280\u672F\u4E13\u5BB6\uFF0C\u8D1F\u8D23\u7F8E\u56FE\u79C0\u79C0\u793E\u533A\u7684\u7814\u53D1\u3001\u4F18\u5316\u548C\u8FD0\u884C\u7EF4\u62A4\u5DE5\u4F5C\u3002\u4ECE\u4E1A\u5341\u5E74\uFF0C\u6211\u4E00\u76F4\u5728\u4ECE\u4E8B\u793E\u533A\u7CFB\u7EDF\u7814\u53D1\u3001\u67B6\u6784\u8BBE\u8BA1\u3001\u7CFB\u7EDF\u4F18\u5316\u7684\u5DE5\u4F5C\uFF0C\u671F\u95F4\u53C2\u4E0E\u7814\u53D1\u8FC7\u4E09\u4E2A DAU \u8FC7\u5343\u4E07\u7684\u5927\u578B\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u3002\u5728\u8FD9\u4E09\u4E2A\u9879\u76EE\u4E2D\uFF0C\u6211\u53C2\u4E0E\u4E86\u4E1A\u52A1\u7CFB\u7EDF\u7684\u5F00\u53D1\u548C\u6539\u9020\uFF0C\u4E5F\u53C2\u4E0E\u548C\u4E3B\u5BFC\u8FC7\u50CF RPC \u6846\u67B6\u3001\u5206\u5E03\u5F0F\u6D88\u606F\u7CFB\u7EDF\u3001\u6CE8\u518C\u4E2D\u5FC3\u7B49\u4E2D\u95F4\u4EF6\u7CFB\u7EDF\u7684\u7814\u53D1\uFF0C\u5BF9\u4E8E\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u7684\u5404\u4E2A\u65B9\u9762\u90FD\u6709\u6240\u6D89\u730E\u3002</p><p>\u6211\u89C1\u8BC1\u4E86\u7CFB\u7EDF\u4ECE\u521D\u671F\u6784\u5EFA\uFF0C\u5230\u627F\u63A5\u9AD8\u5E76\u53D1\u5927\u6D41\u91CF\u7684\u5168\u8FC7\u7A0B\uFF0C\u5E76\u5728\u5176\u4E2D\u79EF\u7D2F\u4E86\u5927\u91CF\u7684\u7CFB\u7EDF\u6F14\u8FDB\u7ECF\u9A8C\u3002\u6211\u8BA4\u4E3A\uFF0C\u867D\u8BF4\u6BCF\u5BB6\u516C\u53F8\u6240\u5904\u7684\u884C\u4E1A\u4E0D\u540C\uFF0C\u4E1A\u52A1\u573A\u666F\u4E0D\u540C\uFF0C\u4F46\u662F\u8BBE\u8BA1\u548C\u4F18\u5316\u7684\u601D\u60F3\u5374\u662F\u4E07\u53D8\u4E0D\u79BB\u5176\u5B97\u3002</p><p>\u8FD9\u4E9B\u7ECF\u9A8C\u662F\u4E00\u4E2A\u4E2A\u7684\u201C\u5C0F\u5957\u8DEF\u201D\uFF0C\u5B83\u4EEC\u76F8\u4E92\u8054\u7CFB\uFF0C\u5F62\u6210\u4E00\u5957\u6307\u5F15\u6211\u4EEC\u8FDB\u884C\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u7684\u77E5\u8BC6\u4F53\u7CFB\uFF0C\u5176\u4E2D\u5305\u62EC\u4E86\u7406\u8BBA\u77E5\u8BC6\u7684\u8BB2\u89E3\u3001\u95EE\u9898\u573A\u666F\u7684\u4ECB\u7ECD\u3001\u95EE\u9898\u5206\u6790\u7684\u8FC7\u7A0B\uFF0C\u4EE5\u53CA\u89E3\u51B3\u95EE\u9898\u7684\u601D\u8DEF\u3002\u5F53\u4F60\u638C\u63E1\u8FD9\u4E9B\u201C\u5957\u8DEF\u201D\u4E4B\u540E\uFF0C\u5C31\u80FD\u660E\u786E\u5730\u77E5\u9053\uFF0C\u7CFB\u7EDF\u5904\u4E8E\u67D0\u4E00\u4E2A\u9636\u6BB5\u65F6\uFF0C\u53EF\u80FD\u4F1A\u9762\u4E34\u7684\u95EE\u9898\uFF0C\u7136\u540E\u53CA\u65F6\u627E\u5230\u67B6\u6784\u5347\u7EA7\u4F18\u5316\u7684\u601D\u8DEF\u89E3\u51B3\u8FD9\u4E9B\u95EE\u9898\uFF0C\u63D0\u5347\u7CFB\u7EDF\u6027\u80FD\u3002</p><p>\u4ECE\u4ECA\u5929\u8D77\uFF0C\u6211\u4F1A\u5728\u201C\u6781\u5BA2\u65F6\u95F4\u201D\u4E0A\u5206\u4EAB\u8FD9\u4E9B\u201C\u5957\u8DEF\u201D\uFF0C\u548C\u4F60\u4E00\u8D77\u5206\u6790\u95EE\u9898\u539F\u56E0\uFF0C\u63A2\u8BA8\u89E3\u51B3\u65B9\u6848\uFF0C\u8BA9\u4F60\u5B66\u6709\u6240\u7528\uFF01</p><p><strong>\u4E3A\u4EC0\u4E48\u8981\u5B66\u4E60\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\uFF1F</strong></p><p>\u5728\u89E3\u7B54\u201C\u4E3A\u4EC0\u4E48\u8981\u5B66\u4E60\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u201D\u4E4B\u524D\uFF0C\u6211\u60F3\u8BA9\u4F60\u601D\u8003\u51E0\u4E2A\u95EE\u9898\uFF1A</p><ul><li>\u5728\u5FAE\u535A\u4E2D\uFF0C\u660E\u661F\u52A8\u8F84\u62E5\u6709\u51E0\u5343\u4E07\u751A\u81F3\u4E0A\u4EBF\u7684\u7C89\u4E1D\uFF0C\u4F60\u8981\u600E\u4E48\u4FDD\u8BC1\u660E\u661F\u53D1\u5E03\u7684\u5185\u5BB9\u8BA9\u7C89\u4E1D\u5B9E\u65F6\u5730\u770B\u5230\u5462\uFF1F</li><li>\u6DD8\u5B9D\u53CC\u5341\u4E00\uFF0C\u5F53\u4F60\u548C\u4E0A\u4E07\u4EBA\u4E00\u8D77\u62A2\u8D2D\u4E00\u4EF6\u6027\u4EF7\u6BD4\u8D85\u9AD8\u7684\u8863\u670D\u65F6\uFF0C\u600E\u4E48\u4FDD\u8BC1\u8863\u670D\u4E0D\u4F1A\u8D85\u5356\uFF1F</li><li>\u6625\u8FD0\u65F6\u6211\u4EEC\u90FD\u4F1A\u53BB 12306 \u8BA2\u8D2D\u706B\u8F66\u7968\uFF0C\u4EE5\u524D\u5728\u62A2\u7968\u65F6\u7ECF\u5E38\u9047\u5230\u9875\u9762\u6253\u4E0D\u5F00\u7684\u60C5\u51B5\uFF0C\u90A3\u4E48\u5982\u679C\u4F60\u6765\u8BBE\u8BA1 12306 \u7CFB\u7EDF\uFF0C\u8981\u5982\u4F55\u4FDD\u8BC1\u5728\u5343\u4E07\u4EBA\u8BBF\u95EE\u7684\u540C\u65F6\u4E5F\u80FD\u652F\u6301\u6B63\u5E38\u62A2\u7968\u5462\uFF1F</li></ul><p>\u8FD9\u4E9B\u95EE\u9898\u662F\u4F60\u5728\u8BBE\u8BA1\u548C\u5B9E\u73B0\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u65F6\u7ECF\u5E38\u4F1A\u9047\u5230\u7684\u75DB\u70B9\u95EE\u9898\uFF0C\u90FD\u6D89\u53CA\u5982\u4F55\u5728\u9AD8\u5E76\u53D1\u573A\u666F\u4E0B\u505A\u5230\u9AD8\u6027\u80FD\u548C\u9AD8\u53EF\u7528\uFF0C\u638C\u63E1\u8FD9\u4E9B\u5185\u5BB9\uFF0C\u4F60\u5F00\u53D1\u7684\u4EA7\u54C1\u53EF\u4EE5\u4E3A\u7528\u6237\u63D0\u4F9B\u66F4\u597D\u7684\u4F7F\u7528\u4F53\u9A8C\uFF0C\u4F60\u7684\u6280\u672F\u80FD\u529B\u4E5F\u80FD\u6709\u4E00\u4E2A\u8D28\u7684\u53D8\u5316\u3002</p><p><strong>\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u77E5\u8BC6\uFF0C\u662F\u4F60\u83B7\u53D6\u5927\u5382 Offer \u5FC5\u4E0D\u53EF\u5C11\u7684\u5229\u5668</strong></p><p>\u4E0D\u53EF\u5426\u8BA4\u7684\u662F\uFF0C\u76EE\u524D\u7684\u7ECF\u6D4E\u5F62\u52BF\u4E0D\u597D\uFF0C\u5F88\u591A\u516C\u53F8\uFF08\u6BD4\u5982\u963F\u91CC\u3001\u817E\u8BAF\u3001\u4ECA\u65E5\u5934\u6761\uFF09\u4E00\u65B9\u9762\u5728\u51CF\u5C11\u62DB\u8058\u7684\u4EBA\u5458\u6570\u91CF\uFF0C\u53E6\u4E00\u65B9\u9762\u4E5F\u671F\u671B\u82B1\u8D39\u4E86\u4EBA\u529B\u6210\u672C\u4E4B\u540E\u53EF\u4EE5\u7ED9\u516C\u53F8\u5E26\u6765\u66F4\u5927\u7684\u4EF7\u503C\u3002\u90A3\u4E48\u5BF9\u4E8E\u516C\u53F8\u6765\u8BF4\uFF0C\u4EC5\u4EC5\u61C2\u5F97 CRUD \u7684\u7A0B\u5E8F\u5458\u5C31\u4E0D\u5982\u6709\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u7ECF\u9A8C\u7684\u7A0B\u5E8F\u5458\u6709\u5438\u5F15\u529B\u4E86\u3002</p><p>\u6240\u4EE5\u5F53\u4F60\u53BB\u9762\u8BD5\u65F6\uFF0C\u9762\u8BD5\u5B98\u4F1A\u8981\u6C42\u4F60\u6709\u9AD8\u5E76\u53D1\u8BBE\u8BA1\u7ECF\u9A8C\uFF0C\u6709\u7684\u9762\u8BD5\u5B98\u4F1A\u8BE2\u95EE\u4F60\u7684\u7CFB\u7EDF\u5728\u906D\u9047\u767E\u4E07\u5E76\u53D1\u65F6\u53EF\u80FD\u6709\u54EA\u4E9B\u74F6\u9888\u70B9\uFF0C\u4EE5\u53CA\u6709\u4EC0\u4E48\u4F18\u5316\u601D\u8DEF\u7B49\u95EE\u9898\uFF0C\u4E3A\u7684\u5C31\u662F\u68C0\u9A8C\u4F60\u662F\u5426\u771F\u7684\u4E86\u89E3\u8FD9\u65B9\u9762\u7684\u5185\u5BB9\u3002</p><p>\u90A3\u4E48\u8FDB\u4E0D\u4E86\u5927\u5382\uFF0C\u6CA1\u6709\u9AD8\u5E76\u53D1\u7684\u573A\u666F\uFF0C\u8FD9\u4E9B\u8BBE\u8BA1\u7684\u7ECF\u9A8C\u53C8\u8981\u4ECE\u4F55\u5904\u6765\u5462\uFF1F\u8FD9\u5C31\u662F\u9E21\u751F\u86CB\u86CB\u751F\u9E21\u7684\u95EE\u9898\u4E86\u3002\u6211\u80FD\u80AF\u5B9A\u7684\u662F\uFF0C\u5F53\u4F60\u5B66\u4E60\u8FD9\u95E8\u8BFE\u7A0B\uFF0C\u638C\u63E1\u4E86\u8FD9\u65B9\u9762\u7684\u6280\u672F\u4E4B\u540E\uFF0C\u5927\u5382\u7684 Offer \u5C06\u4E0D\u518D\u9065\u4E0D\u53EF\u53CA\u3002</p><p><strong>\u4E0D\u8981\u56FF\u4E8E\u516C\u53F8\u73B0\u6709\u7684\u4E1A\u52A1\u573A\u666F\uFF0C\u4F60\u7684\u80FD\u529B\uFF0C\u7EDD\u4E0D\u6B62\u4E8E\u6B64</strong></p><p>\u90A3\u4F60\u53EF\u80FD\u4F1A\u8BF4\uFF1A\u201C\u6211\u5728\u5C0F\u516C\u53F8\u5DE5\u4F5C\uFF0C\u5C0F\u516C\u53F8\u7684\u7CFB\u7EDF\u5E76\u53D1\u4E0D\u9AD8\uFF0C\u6D41\u91CF\u4E5F\u4E0D\u5927\uFF0C\u5B66\u4E60\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u4F3C\u4E4E\u6709\u4E9B\u591A\u6B64\u4E00\u4E3E\u3002\u201D\u4F46\u6211\u60F3\u8BF4\u7684\u662F\uFF0C<strong>\u516C\u53F8\u4E1A\u52A1\u6D41\u91CF\u5E73\u7A33\uFF0C\u5E76\u4E0D\u8868\u793A\u4E0D\u4F1A\u9047\u5230\u4E00\u4E9B\u9AD8\u5E76\u53D1\u7684\u9700\u6C42\u573A\u666F\u3002</strong></p><p>\u5C31\u62FF\u7535\u5546\u7CFB\u7EDF\u4E2D\u7684\u4E0B\u5355\u6D41\u7A0B\u8BBE\u8BA1\u6280\u672F\u65B9\u6848\u4E3A\u4F8B\u3002\u5728\u6BCF\u79D2\u53EA\u6709\u4E00\u6B21\u8C03\u7528\u7684\u7CFB\u7EDF\u4E2D\uFF0C\u4F60\u53EA\u9700\u8981\u5173\u6CE8\u4E1A\u52A1\u903B\u8F91\u672C\u8EAB\u5C31\u597D\u4E86\uFF1A\u67E5\u8BE2\u5E93\u5B58\u662F\u5426\u5145\u8DB3\uFF0C\u5982\u679C\u5145\u8DB3\uFF0C\u5C31\u53EF\u4EE5\u5230\u6570\u636E\u5E93\u4E2D\u751F\u6210\u8BA2\u5355\uFF0C\u6210\u529F\u540E\u9501\u5B9A\u5E93\u5B58\uFF0C\u7136\u540E\u8FDB\u5165\u652F\u4ED8\u6D41\u7A0B\u3002</p><p><img src="https://static001.geekbang.org/resource/image/2d/f3/2d95823d39676e18a43ab3328ce0d0f3.jpg?wh=1142*288" alt=""></p><p>\u8FD9\u4E2A\u6D41\u7A0B\u975E\u5E38\u6E05\u6670\uFF0C\u5B9E\u73B0\u4E5F\u7B80\u5355\uFF0C\u4F46\u5982\u679C\u8981\u505A\u4E00\u6B21\u79D2\u6740\u7684\u6D3B\u52A8\uFF0C\u914D\u5408\u4E00\u4E9B\u8FD0\u8425\u7684\u63A8\u5E7F\uFF0C\u4F60\u4F1A\u53D1\u73B0\u4E0B\u5355\u64CD\u4F5C\u7684\u8C03\u7528\u91CF\u53EF\u80FD\u8FBE\u5230\u6BCF\u79D2 10000 \u6B21\uFF01</p><p>10000 \u6B21\u8BF7\u6C42\u540C\u65F6\u67E5\u8BE2\u5E93\u5B58\uFF0C\u662F\u5426\u4F1A\u628A\u5E93\u5B58\u7CFB\u7EDF\u62D6\u57AE\uFF1F\u5982\u679C\u8BF7\u6C42\u5168\u90E8\u901A\u8FC7\uFF0C\u90A3\u4E48\u5C31\u8981\u540C\u65F6\u751F\u6210 10000 \u6B21\u8BA2\u5355\uFF0C\u6570\u636E\u5E93\u80FD\u5426\u6297\u4F4F\uFF1F\u5982\u679C\u6297\u4E0D\u4F4F\uFF0C\u6211\u4EEC\u8981\u5982\u4F55\u505A\uFF1F\u8FD9\u4E9B\u95EE\u9898\u90FD\u53EF\u80FD\u51FA\u73B0\uFF0C\u5E76\u8BA9\u4E4B\u524D\u7684\u65B9\u6848\u4E0D\u518D\u9002\u7528\uFF0C\u6B64\u65F6\u4F60\u5C31\u9700\u8981\u8BBE\u8BA1\u65B0\u7684\u65B9\u6848\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u540C\u6837\u662F\u7F13\u5B58\u7684\u4F7F\u7528\uFF0C\u5728\u4F4E\u5E76\u53D1\u4E0B\u4F60\u53EA\u9700\u8981\u4E86\u89E3\u57FA\u672C\u7684\u4F7F\u7528\u65B9\u5F0F\uFF0C\u4F46\u5728\u9AD8\u5E76\u53D1\u573A\u666F\u4E0B\u4F60\u9700\u8981\u5173\u6CE8\u7F13\u5B58\u547D\u4E2D\u7387\uFF0C\u5982\u4F55\u5E94\u5BF9\u7F13\u5B58\u7A7F\u900F\uFF0C\u5982\u4F55\u907F\u514D\u96EA\u5D29\uFF0C\u5982\u4F55\u89E3\u51B3\u7F13\u5B58\u4E00\u81F4\u6027\u7B49\u95EE\u9898\uFF0C\u8FD9\u5C31\u589E\u52A0\u4E86\u8BBE\u8BA1\u65B9\u6848\u7684\u590D\u6742\u5EA6\uFF0C\u5BF9\u8BBE\u8BA1\u8005\u80FD\u529B\u7684\u8981\u6C42\u4E5F\u4F1A\u66F4\u9AD8\u3002<strong>\u6240\u4EE5\uFF0C\u4E3A\u4E86\u907F\u514D\u9047\u5230\u95EE\u9898\u65F6\u624B\u5FD9\u811A\u4E71\uFF0C\u4F60\u6709\u5FC5\u8981\u63D0\u524D\u50A8\u5907\u8DB3\u591F\u591A\u7684\u9AD8\u5E76\u53D1\u77E5\u8BC6\uFF0C\u4ECE\u800C\u5177\u5907\u968F\u65F6\u5E94\u5BF9\u53EF\u80FD\u51FA\u73B0\u7684\u9AD8\u5E76\u53D1\u9700\u6C42\u573A\u666F\u7684\u80FD\u529B\u3002</strong></p><p>\u6211\u8EAB\u8FB9\u6709\u5F88\u591A\u5728\u5C0F\u516C\u53F8\u6253\u62FC\u95EF\u8361\uFF0C\u5C0F\u6709\u5EFA\u6811\u7684\u670B\u53CB\uFF0C\u4ED6\u4EEC\u65E0\u4E00\u4E0D\u7ECF\u5386\u8FC7\u4F4E\u8C37\u671F\uFF0C\u53C8\u4E00\u4E00\u5F00\u62D3\u4E86\u4E00\u7247\u5929\u5730\uFF0C\u7A76\u5176\u539F\u56E0\uFF0C\u662F\u56E0\u4E3A\u4ED6\u4EEC\u6CA1\u6709\u5C06\u76EE\u5149\u653E\u5728\u73B0\u6709\u7684\u4E1A\u52A1\u573A\u666F\u4E2D\uFF0C\u800C\u662F\u4FDD\u6301\u7740\u5BF9\u4E8E\u65B0\u6280\u672F\u7684\u597D\u5947\u5FC3\uFF0C\u65F6\u523B\u5173\u6CE8\u4E1A\u754C\u65B0\u6280\u672F\u7684\u5B9E\u73B0\u539F\u7406\uFF0C\u601D\u8003\u5982\u4F55\u4F7F\u7528\u6280\u672F\u6765\u89E3\u51B3\u4E1A\u52A1\u4E0A\u7684\u95EE\u9898\u3002</p><p>\u4ED6\u4EEC\u867D\u7136\u6027\u683C\u5F88\u4E0D\u540C\uFF0C\u4F46\u4E0D\u7518\u4E8E\u73B0\u72B6\uFF0C\u7A81\u7834\u81EA\u5DF1\u7684\u4FE1\u5FF5\u5374\u662F\u4E00\u81F4\u7684\u3002<strong>\u6211\u76F8\u4FE1\uFF0C\u4F60\u4E5F\u4E00\u5B9A\u5982\u6B64\u3002\u6240\u4EE5\u5B8C\u6210\u4E1A\u52A1\u9700\u6C42\uFF0C\u89E3\u51B3\u4EA7\u54C1\u95EE\u9898\u4E0D\u5E94\u8BE5\u662F\u4F60\u6700\u7EC8\u7684\u76EE\u6807\uFF0C\u63D0\u5347\u6280\u672F\u80FD\u529B\u548C\u6280\u672F\u89C6\u91CE\u624D\u5E94\u662F\u4F60\u59CB\u7EC8\u4E0D\u53D8\u7684\u8FFD\u6C42\u3002</strong></p><p><strong>\u8BA1\u7B97\u673A\u9886\u57DF\u91CC\u867D\u7136\u77E5\u8BC6\u70B9\u5E9E\u6742\uFF0C\u4F46\u5F88\u591A\u6838\u5FC3\u601D\u60F3\u90FD\u662F\u76F8\u901A\u7684</strong></p><p>\u4E3E\u4E2A\u4F8B\u5B50\uFF0C\u6D88\u606F\u961F\u5217\u662F\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u4E2D\u5E38\u89C1\u7684\u4E00\u79CD\u7EC4\u4EF6\uFF0C\u5B83\u53EF\u4EE5\u5C06\u6D88\u606F\u751F\u4EA7\u65B9\u548C\u6D88\u8D39\u65B9\u89E3\u8026\uFF0C\u51CF\u5C11\u7A81\u53D1\u6D41\u91CF\u5BF9\u4E8E\u7CFB\u7EDF\u7684\u51B2\u51FB\u3002\u4F46\u5982\u679C\u4F60\u7684\u7CFB\u7EDF\u6CA1\u6709\u90A3\u4E48\u9AD8\u7684\u6D41\u91CF\uFF0C\u4F60\u5C31\u6C38\u8FDC\u4E0D\u4F1A\u4F7F\u7528\u6D88\u606F\u961F\u5217\u4E86\u5417\uFF1F\u5F53\u7136\u4E0D\u662F\u3002</p><p>\u7CFB\u7EDF\u6A21\u5757\u8981\u505A\u5230\u9AD8\u5185\u805A\u3001\u4F4E\u8026\u5408\uFF0C\u8FD9\u662F\u7CFB\u7EDF\u7684\u57FA\u672C\u8BBE\u8BA1\u601D\u60F3\uFF0C\u548C\u662F\u5426\u9AD8\u5E76\u53D1\u65E0\u5173\uFF0C\u800C\u6D88\u606F\u961F\u5217\u4F5C\u4E3A\u4E3B\u8981\u7684\u7CFB\u7EDF\u89E3\u8026\u65B9\u5F0F\uFF0C\u5E94\u8BE5\u662F\u4F60\u6280\u672F\u767E\u5B9D\u56CA\u4E2D\u4E00\u4EF6\u4E0D\u53EF\u6216\u7F3A\u7684\u5236\u80DC\u6CD5\u5B9D\u3002</p><p>\u53C8\u6BD4\u5982\uFF0C\u7F13\u5B58\u6280\u672F\u8574\u542B\u7684\u662F\u7A7A\u95F4\u6362\u65F6\u95F4\u7684\u601D\u60F3\uFF1B\u538B\u7F29\u4F53\u73B0\u7684\u662F\u65F6\u95F4\u6362\u7A7A\u95F4\u7684\u601D\u60F3\uFF1B\u5206\u5E03\u5F0F\u601D\u60F3\u4E5F\u6700\u521D\u4F53\u73B0\u5728 CPU \u7684\u8BBE\u8BA1\u548C\u5B9E\u73B0\u4E0A\u2026\u2026\u8FD9\u4E9B\u5185\u5BB9\uFF0C\u90FD\u662F\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u4E2D\u7684\u5185\u5BB9\uFF0C\u800C\u6211\u5E0C\u671B\u5728\u8FD9\u4E2A\u8BFE\u7A0B\u4E2D\uFF0C\u5E2E\u4F60\u628A\u63E1\u8FD9\u4E9B\u6838\u5FC3\u601D\u60F3\uFF0C\u8BA9\u4F60\u89E6\u7C7B\u65C1\u901A\uFF0C\u4E3E\u4E00\u53CD\u4E09\u3002</p><p>\u6240\u4EE5\uFF0C\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u65E0\u8BBA\u662F\u5BF9\u4E8E\u521D\u5165\u804C\u573A\u7684\u5DE5\u7A0B\u5E08\u4E86\u89E3\u57FA\u672C\u7CFB\u7EDF\u8BBE\u8BA1\u601D\u60F3\uFF0C\u8FD8\u662F\u5BF9\u4E8E\u6709\u4E00\u5B9A\u5DE5\u4F5C\u7ECF\u9A8C\u7684\u540C\u5B66\u5B8C\u5584\u81EA\u8EAB\u6280\u80FD\u6811\uFF0C\u4E3A\u672A\u6765\u53EF\u80FD\u9047\u89C1\u7684\u7CFB\u7EDF\u95EE\u9898\u505A\u597D\u6280\u672F\u50A8\u5907\uFF0C\u90FD\u6709\u5F88\u5927\u7684\u5E2E\u52A9\u3002</p><p>\u4E5F\u8BB8\u4F60\u4F1A\u62C5\u5FC3\u77E5\u8BC6\u70B9\u4E0D\u6210\u4F53\u7CFB\uFF1B\u62C5\u5FC3\u53EA\u8BB2\u7406\u8BBA\uFF0C\u6CA1\u6709\u5B9E\u9645\u7684\u573A\u666F\uFF1B\u62C5\u5FC3\u53EA\u6709\u7A7A\u6D1E\u7684\u4ECB\u7ECD\uFF0C\u6CA1\u6709\u5E72\u8D27\u3002\u653E\u5FC3\uFF01\u6211\u540C\u6837\u8003\u8651\u4E86\u8FD9\u4E9B\u95EE\u9898\u5E76\u5728\u53CD\u590D\u601D\u8003\u4E4B\u540E\uFF0C<strong>\u51B3\u5B9A\u4EE5\u4E00\u4E2A\u865A\u62DF\u7684\u7CFB\u7EDF\u4E3A\u4E3B\u7EBF\uFF0C\u8BB2\u89E3\u5728\u6D41\u91CF\u548C\u5E76\u53D1\u4E0D\u65AD\u63D0\u5347\u7684\u60C5\u51B5\u4E0B\u5982\u4F55\u4E00\u6B65\u6B65\u5730\u4F18\u5316\u5B83</strong>\uFF0C\u5E76\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\u7A7F\u63D2\u7740\u8BB2\u89E3\u77E5\u8BC6\u70B9\uFF0C\u8FD9\u6837\u901A\u8FC7\u573A\u666F\u3001\u539F\u7406\u3001\u5B9E\u8DF5\u76F8\u7ED3\u5408\u7684\u65B9\u5F0F\uFF0C\u6765\u5E2E\u52A9\u4F60\u66F4\u5FEB\u3001\u66F4\u6DF1\u5165\u5730\u7406\u89E3\u548C\u6D88\u5316\u3002</p><p>\u603B\u4F53\u6765\u8BF4\uFF0C\u5B66\u5B8C\u8FD9\u6B21\u8BFE\u7A0B\uFF0C\u4F60\u4F1A\u6709\u4E09\u4E2A\u6536\u83B7\uFF1A</p><ul><li>\u638C\u63E1\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u7684\u201C\u5957\u8DEF\u201D\uFF1B</li><li>\u7406\u89E3\u57FA\u672C\u7684\u7CFB\u7EDF\u8BBE\u8BA1\u601D\u60F3\uFF0C\u5BF9\u65B0\u7684\u77E5\u8BC6\u89E6\u7C7B\u65C1\u901A\uFF0C\u4E3E\u4E00\u53CD\u4E09\uFF1B</li><li>\u7A81\u7834\u6280\u672F\u7684\u74F6\u9888\uFF0C\u7A81\u7834\u6240\u5904\u5E73\u53F0\u7684\u9650\u5236\uFF0C\u5177\u5907\u4E00\u4E2A\u4F18\u79C0\u67B6\u6784\u5E08\u7684\u8D44\u8D28\u3002</li></ul><h2 id="\u8BFE\u7A0B\u8BBE\u8BA1" tabindex="-1">\u8BFE\u7A0B\u8BBE\u8BA1 <a class="header-anchor" href="#\u8BFE\u7A0B\u8BBE\u8BA1" aria-hidden="true">#</a></h2><p>\u6211\u5C06\u8BFE\u7A0B\u5212\u5206\u4E86\u4E09\u4E2A\u6A21\u5757\u6765\u8BB2\u89E3\uFF0C\u5206\u522B\u662F\uFF1A\u57FA\u7840\u7BC7\u3001\u6F14\u8FDB\u7BC7\u548C\u5B9E\u6218\u7BC7\u3002</p><p>\u57FA\u7840\u7BC7\u4E3B\u8981\u662F\u4E00\u4E9B\u57FA\u672C\u7684\u9AD8\u5E76\u53D1\u67B6\u6784\u8BBE\u8BA1\u7406\u5FF5\uFF0C\u4F60\u53EF\u4EE5\u628A\u5B83\u770B\u4F5C\u6574\u4E2A\u8BFE\u7A0B\u7684\u4E00\u4E2A\u603B\u7EB2\uFF0C\u5EFA\u7ACB\u5BF9\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u7684\u521D\u6B65\u8BA4\u8BC6\u3002</p><p>\u6F14\u8FDB\u7BC7\u662F\u6574\u4E2A\u8BFE\u7A0B\u7684\u6838\u5FC3\uFF0C\u4E3B\u8981\u8BB2\u89E3\u7CFB\u7EDF\u652F\u6301\u9AD8\u5E76\u53D1\u7684\u65B9\u6CD5\u3002\u6211\u4F1A\u7528\u4E00\u4E2A\u865A\u62DF\u7684\u7CFB\u7EDF\uFF0C\u5E26\u4F60\u5206\u6790\u5F53\u968F\u7740\u524D\u7AEF\u5E76\u53D1\u589E\u52A0\uFF0C\u8FD9\u4E2A\u7CFB\u7EDF\u7684\u53D8\u5316\uFF0C\u4EE5\u53CA\u4F60\u4F1A\u9047\u5230\u7684\u4E00\u7CFB\u5217\u75DB\u70B9\u95EE\u9898\u3002\u6BD4\u5982\u6570\u636E\u67E5\u8BE2\u7684\u6027\u80FD\u74F6\u9888\uFF0C\u7F13\u5B58\u7684\u9AD8\u53EF\u7528\u95EE\u9898\uFF0C\u7136\u540E\u4ECE\u6570\u636E\u5E93\u3001\u7F13\u5B58\u3001\u6D88\u606F\u961F\u5217\u3001\u5206\u5E03\u5F0F\u670D\u52A1\u548C\u7EF4\u62A4\u8FD9\u4E94\u4E2A\u89D2\u5EA6\u6765\u5C55\u5F00\uFF0C\u9488\u5BF9\u95EE\u9898\u5BFB\u627E\u89E3\u51B3\u65B9\u6848\uFF0C<strong>\u8BA9\u4F60\u7F6E\u8EAB\u5176\u4E2D\uFF0C\u771F\u771F\u5207\u5207\u5730\u8D70\u4E00\u904D\u7CFB\u7EDF\u6F14\u8FDB\u7684\u9053\u8DEF\u3002</strong></p><p>\u5B9E\u6218\u7BC7\u5C06\u4EE5\u4E24\u4E2A\u5B9E\u9645\u6848\u4F8B\uFF0C\u5E26\u4F60\u5E94\u7528\u5B66\u5230\u7684\u77E5\u8BC6\u5E94\u5BF9\u9AD8\u5E76\u53D1\u5927\u6D41\u91CF\u7684\u51B2\u51FB\u3002</p><p>\u4E00\u4E2A\u6848\u4F8B\u662F\u5982\u4F55\u8BBE\u8BA1\u627F\u62C5\u6BCF\u79D2\u51E0\u5341\u4E07\u6B21\u7528\u6237\u672A\u8BFB\u6570\u8BF7\u6C42\u7684\u7CFB\u7EDF\u3002\u4E4B\u6240\u4EE5\u9009\u62E9\u5B83\uFF0C\u662F\u56E0\u4E3A\u5728\u5927\u90E8\u5206\u7684\u7CFB\u7EDF\u4E2D\u672A\u8BFB\u6570\u90FD\u4F1A\u662F\u8BF7\u6C42\u91CF\u6700\u5927\u3001\u5E76\u53D1\u6700\u9AD8\u7684\u670D\u52A1\uFF0C\u5728\u5FAE\u535A\u65F6 QPS \u4F1A\u8FBE\u5230\u6BCF\u79D2 50 \u4E07\u6B21\u3002\u540C\u65F6\uFF0C\u672A\u8BFB\u6570\u7CFB\u7EDF\u7684\u4E1A\u52A1\u903B\u8F91\u6BD4\u8F83\u7B80\u5355\uFF0C\u5728\u4F60\u4E86\u89E3\u8BBE\u8BA1\u65B9\u6848\u7684\u65F6\u5019\u4E5F\u4E0D\u9700\u8981\u9884\u5148\u5BF9\u4E1A\u52A1\u903B\u8F91\u6709\u6DF1\u5165\u4E86\u89E3\uFF1B\u53E6\u4E00\u4E2A\u4F8B\u5B50\u662F\u4FE1\u606F\u6D41\u7CFB\u7EDF\u7684\u8BBE\u8BA1\uFF0C\u5B83\u662F\u793E\u533A\u793E\u4EA4\u4EA7\u54C1\u4E2D\u7684\u6838\u5FC3\u7CFB\u7EDF\uFF0C\u4E1A\u52A1\u903B\u8F91\u590D\u6742\u4E14\u8BF7\u6C42\u91CF\u5927\uFF0C\u65B9\u6848\u4E2D\u51E0\u4E4E\u6D89\u53CA\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u7684\u5168\u90E8\u5185\u5BB9\u3002</p><p>\u4E0B\u9762\u662F\u8FD9\u4E2A\u8BFE\u7A0B\u7684\u76EE\u5F55\uFF0C\u4F60\u80FD\u5FEB\u901F\u4E86\u89E3\u6574\u4E2A\u8BFE\u7A0B\u7684\u77E5\u8BC6\u4F53\u7CFB\u3002</p><p><img src="https://static001.geekbang.org/resource/image/d3/8f/d3c0bc67109122412ab5e5c1c112128f.jpg?wh=750*3928" alt=""></p><h2 id="\u5199\u5728\u6700\u540E" tabindex="-1">\u5199\u5728\u6700\u540E <a class="header-anchor" href="#\u5199\u5728\u6700\u540E" aria-hidden="true">#</a></h2><p>\u8BFE\u7A0B\u4ECE\u539F\u7406\u5230\u5B9E\u6218\uFF0C\u4EE5\u6848\u4F8B\u4F5C\u4E3A\u4E3B\u7EBF\uFF0C\u6DB5\u76D6\u4E86\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u7684\u6574\u4E2A\u77E5\u8BC6\u4F53\u7CFB\u3002\u53EA\u8981\u4F60\u4E00\u6B65\u4E00\u6B65\u5730\u575A\u6301\u5B66\u4E60\uFF0C\u8BFE\u540E\u591A\u52A0\u601D\u8003\uFF0C\u591A\u7EC3\u4E60\uFF0C\u76F8\u4FE1\u4F60\u7684\u7CFB\u7EDF\u8BBE\u8BA1\u80FD\u529B\u4E00\u5B9A\u80FD\u591F\u5F97\u5230\u5F88\u5927\u7684\u63D0\u5347\uFF0C\u804C\u4E1A\u53D1\u5C55\u8DEF\u5F84\u4E5F\u4F1A\u8D70\u5F97\u6108\u52A0\u5BBD\u9614\u3002</p><p>\u6700\u540E\uFF0C\u6B22\u8FCE\u4F60\u5728\u7559\u8A00\u533A\u548C\u6211\u8BF4\u4E00\u8BF4\u81EA\u5DF1\u7684\u60C5\u51B5\uFF0C\u60F3\u8981\u4E86\u89E3\u9AD8\u5E76\u53D1\u7684\u54EA\u4E9B\u5185\u5BB9\uFF0C\u6216\u8005\u5728\u9AD8\u5E76\u53D1\u65B9\u9762\u5B58\u5728\u7684\u4E00\u4E9B\u56F0\u60D1\uFF0C\u4E00\u662F\u65B9\u4FBF\u6211\u5728\u540E\u9762\u7684\u8BB2\u89E3\u4E2D\u6709\u6240\u4FA7\u91CD\u5730\u5C55\u5F00\u4ECB\u7ECD\uFF0C\u4E00\u662F\u5E0C\u671B\u4F60\u5728\u8DDF\u7740\u6211\u4E00\u8D77\u5B66\u5B8C\u8FD9\u4E2A\u8BFE\u7A0B\u540E\uFF0C\u518D\u56DE\u987E\u6B64\u524D\u7684\u95EE\u9898\uFF0C\u80FD\u591F\u53D1\u73B0\u81EA\u5DF1\u5207\u5B9E\u5730\u6210\u957F\u4E0E\u63D0\u9AD8\uFF0C\u8FD9\u662F\u6211\u6700\u5E0C\u671B\u770B\u5230\u7684\u3002</p>',41),n=[s];function _(i,o,l,c,d,g){return e(),t("div",null,n)}const u=p(a,[["render",_]]);export{f as __pageData,u as default};
