import{_ as e,o as a,c as p,a as t}from"./app.303cfecc.js";const g=JSON.parse('{"title":"01 \u9AD8\u5E76\u53D1\u7CFB\u7EDF\uFF1A\u5B83\u7684\u901A\u7528\u8BBE\u8BA1\u65B9\u6CD5\u662F\u4EC0\u4E48\uFF1F","description":"","frontmatter":{},"headers":[{"level":3,"title":"Scale-up vs Scale-out","slug":"scale-up-vs-scale-out","link":"#scale-up-vs-scale-out","children":[]},{"level":3,"title":"\u4F7F\u7528\u7F13\u5B58\u63D0\u5347\u6027\u80FD","slug":"\u4F7F\u7528\u7F13\u5B58\u63D0\u5347\u6027\u80FD","link":"#\u4F7F\u7528\u7F13\u5B58\u63D0\u5347\u6027\u80FD","children":[]},{"level":3,"title":"\u5F02\u6B65\u5904\u7406","slug":"\u5F02\u6B65\u5904\u7406","link":"#\u5F02\u6B65\u5904\u7406","children":[]},{"level":3,"title":"\u8BFE\u7A0B\u5C0F\u7ED3","slug":"\u8BFE\u7A0B\u5C0F\u7ED3","link":"#\u8BFE\u7A0B\u5C0F\u7ED3","children":[]},{"level":3,"title":"\u4E00\u8BFE\u4E00\u601D","slug":"\u4E00\u8BFE\u4E00\u601D","link":"#\u4E00\u8BFE\u4E00\u601D","children":[]}],"relativePath":"0002/01\u57FA\u7840/01-\u9AD8\u5E76\u53D1\u7CFB\u7EDF\uFF1A\u5B83\u7684\u901A\u7528\u8BBE\u8BA1\u65B9\u6CD5\u662F\u4EC0\u4E48\uFF1F.md","lastUpdated":1680763907000}'),l={name:"0002/01\u57FA\u7840/01-\u9AD8\u5E76\u53D1\u7CFB\u7EDF\uFF1A\u5B83\u7684\u901A\u7528\u8BBE\u8BA1\u65B9\u6CD5\u662F\u4EC0\u4E48\uFF1F.md"},s=t('<h1 id="_01-\u9AD8\u5E76\u53D1\u7CFB\u7EDF-\u5B83\u7684\u901A\u7528\u8BBE\u8BA1\u65B9\u6CD5\u662F\u4EC0\u4E48" tabindex="-1">01 \u9AD8\u5E76\u53D1\u7CFB\u7EDF\uFF1A\u5B83\u7684\u901A\u7528\u8BBE\u8BA1\u65B9\u6CD5\u662F\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#_01-\u9AD8\u5E76\u53D1\u7CFB\u7EDF-\u5B83\u7684\u901A\u7528\u8BBE\u8BA1\u65B9\u6CD5\u662F\u4EC0\u4E48" aria-hidden="true">#</a></h1><p>\u6211\u4EEC\u77E5\u9053\uFF0C\u9AD8\u5E76\u53D1\u4EE3\u8868\u7740\u5927\u6D41\u91CF\uFF0C\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u7684\u9B45\u529B\u5C31\u5728\u4E8E\u6211\u4EEC\u80FD\u591F\u51ED\u501F\u81EA\u5DF1\u7684\u806A\u660E\u624D\u667A\u8BBE\u8BA1\u5DE7\u5999\u7684\u65B9\u6848\uFF0C\u4ECE\u800C\u62B5\u6297\u5DE8\u5927\u6D41\u91CF\u7684\u51B2\u51FB\uFF0C\u5E26\u7ED9\u7528\u6237\u66F4\u597D\u7684\u4F7F\u7528\u4F53\u9A8C\u3002\u8FD9\u4E9B\u65B9\u6848\u597D\u4F3C\u80FD\u64CD\u7EB5\u6D41\u91CF\uFF0C\u8BA9\u6D41\u91CF\u66F4\u52A0\u5E73\u7A33\u5730\u88AB\u7CFB\u7EDF\u4E2D\u7684\u670D\u52A1\u548C\u7EC4\u4EF6\u5904\u7406\u3002</p><p>\u6765\u505A\u4E2A\u7B80\u5355\u7684\u6BD4\u55BB\u5427\u3002</p><p>\u4ECE\u53E4\u81F3\u4ECA\uFF0C\u957F\u6C5F\u548C\u9EC4\u6CB3\u6D41\u57DF\u6C34\u60A3\u4E0D\u65AD\uFF0C\u8FDC\u53E4\u65F6\u671F\u5927\u79B9\u66FE\u62D3\u5BBD\u6CB3\u9053\uFF0C\u6E05\u9664\u6DE4\u6C99\u8BA9\u6D41\u6C34\u66F4\u52A0\u987A\u7545\uFF1B\u90FD\u6C5F\u5830\u4F5C\u4E3A\u53F2\u4E0A\u6700\u6210\u529F\u7684\u6CBB\u6C34\u6848\u4F8B\u4E4B\u4E00\uFF0C\u7528\u5F15\u6D41\u5C06\u5CB7\u6C5F\u4E4B\u6C34\u5206\u6D41\u5230\u591A\u4E2A\u652F\u6D41\u4E2D\uFF0C\u4EE5\u5206\u62C5\u6C34\u6D41\u538B\u529B\uFF1B\u4E09\u95E8\u5CE1\u548C\u845B\u6D32\u575D\u901A\u8FC7\u5EFA\u9020\u6C34\u5E93\u5C06\u6C34\u5F15\u5165\u6C34\u5E93\u5148\u5B58\u50A8\u8D77\u6765\uFF0C\u7136\u540E\u518D\u60F3\u529E\u6CD5\u628A\u6C34\u5E93\u4E2D\u7684\u6C34\u7F13\u7F13\u5730\u6392\u51FA\u53BB\uFF0C\u4EE5\u6B64\u63D0\u9AD8\u4E0B\u6E38\u7684\u6297\u6D2A\u80FD\u529B\u3002</p><p>\u800C\u6211\u4EEC\u5728\u5E94\u5BF9\u9AD8\u5E76\u53D1\u5927\u6D41\u91CF\u65F6\u4E5F\u4F1A\u91C7\u7528\u7C7B\u4F3C\u201C\u62B5\u5FA1\u6D2A\u6C34\u201D\u7684\u65B9\u6848\uFF0C\u5F52\u7EB3\u8D77\u6765\u5171\u6709\u4E09\u79CD\u65B9\u6CD5\u3002</p><ul><li>Scale-out\uFF08\u6A2A\u5411\u6269\u5C55\uFF09\uFF1A\u5206\u800C\u6CBB\u4E4B\u662F\u4E00\u79CD\u5E38\u89C1\u7684\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u65B9\u6CD5\uFF0C\u91C7\u7528\u5206\u5E03\u5F0F\u90E8\u7F72\u7684\u65B9\u5F0F\u628A\u6D41\u91CF\u5206\u6D41\u5F00\uFF0C\u8BA9\u6BCF\u4E2A\u670D\u52A1\u5668\u90FD\u627F\u62C5\u4E00\u90E8\u5206\u5E76\u53D1\u548C\u6D41\u91CF\u3002</li><li>\u7F13\u5B58\uFF1A\u4F7F\u7528\u7F13\u5B58\u6765\u63D0\u9AD8\u7CFB\u7EDF\u7684\u6027\u80FD\uFF0C\u5C31\u597D\u6BD4\u7528\u201C\u62D3\u5BBD\u6CB3\u9053\u201D\u7684\u65B9\u5F0F\u62B5\u6297\u9AD8\u5E76\u53D1\u5927\u6D41\u91CF\u7684\u51B2\u51FB\u3002</li><li>\u5F02\u6B65\uFF1A\u5728\u67D0\u4E9B\u573A\u666F\u4E0B\uFF0C\u672A\u5904\u7406\u5B8C\u6210\u4E4B\u524D\u6211\u4EEC\u53EF\u4EE5\u8BA9\u8BF7\u6C42\u5148\u8FD4\u56DE\uFF0C\u5728\u6570\u636E\u51C6\u5907\u597D\u4E4B\u540E\u518D\u901A\u77E5\u8BF7\u6C42\u65B9\uFF0C\u8FD9\u6837\u53EF\u4EE5\u5728\u5355\u4F4D\u65F6\u95F4\u5185\u5904\u7406\u66F4\u591A\u7684\u8BF7\u6C42\u3002</li></ul><p>\u7B80\u5355\u4ECB\u7ECD\u4E86\u8FD9\u4E09\u79CD\u65B9\u6CD5\u4E4B\u540E\uFF0C\u6211\u518D\u8BE6\u7EC6\u5730\u5E26\u4F60\u4E86\u89E3\u4E00\u4E0B\uFF0C\u8FD9\u6837\u5F53\u4F60\u5728\u8BBE\u8BA1\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u65F6\u5C31\u53EF\u4EE5\u6709\u8003\u8651\u7684\u65B9\u5411\u4E86\u3002\u5F53\u7136\u4E86\uFF0C\u8FD9\u4E09\u79CD\u65B9\u6CD5\u4F1A\u7EC6\u5316\u51FA\u66F4\u591A\u7684\u5185\u5BB9\uFF0C\u6211\u4F1A\u5728\u540E\u9762\u7684\u8BFE\u7A0B\u4E2D\u6DF1\u5165\u8BB2\u89E3\u3002</p><p>\u9996\u5148\uFF0C\u6211\u4EEC\u5148\u6765\u4E86\u89E3\u7B2C\u4E00\u79CD\u65B9\u6CD5\uFF1AScale-out\u3002</p><h3 id="scale-up-vs-scale-out" tabindex="-1">Scale-up vs Scale-out <a class="header-anchor" href="#scale-up-vs-scale-out" aria-hidden="true">#</a></h3><p>\u8457\u540D\u7684\u201C\u6469\u5C14\u5B9A\u5F8B\u201D\u662F\u7531 Intel \u7684\u521B\u59CB\u4EBA\u4E4B\u4E00\u6208\u767B\xB7\u6469\u5C14\u4E8E 1965 \u5E74\u63D0\u51FA\u7684\u3002\u8FD9\u4E2A\u5B9A\u5F8B\u63D0\u5230\u96C6\u6210\u7535\u8DEF\u4E0A\u53EF\u5BB9\u7EB3\u7684\u6676\u4F53\u7BA1\u7684\u6570\u91CF\u7EA6\u6BCF\u9694\u4E24\u5E74\u4F1A\u589E\u52A0\u4E00\u500D\u3002</p><p>\u540E\u6765\uFF0CIntel \u9996\u5E2D\u6267\u884C\u5B98\u5927\u536B\xB7\u8C6A\u65AF\u63D0\u51FA\u201C18 \u4E2A\u6708\u201D\u7684\u8BF4\u6CD5\uFF0C\u5373\u9884\u8BA1 18 \u4E2A\u6708\u4F1A\u5C06\u82AF\u7247\u7684\u6027\u80FD\u63D0\u5347\u4E00\u500D\uFF0C\u8FD9\u4E2A\u8BF4\u6CD5\u5E7F\u4E3A\u6D41\u4F20\u3002</p><p>\u6469\u5C14\u5B9A\u5F8B\u867D\u7136\u63CF\u8FF0\u7684\u662F\u82AF\u7247\u7684\u53D1\u5C55\u901F\u5EA6\uFF0C\u4F46\u6211\u4EEC\u53EF\u4EE5\u5EF6\u4F38\u4E3A\u6574\u4F53\u7684\u786C\u4EF6\u6027\u80FD\uFF0C\u4ECE 20 \u4E16\u7EAA\u540E\u534A\u53F6\u5F00\u59CB\uFF0C\u8BA1\u7B97\u673A\u786C\u4EF6\u7684\u6027\u80FD\u662F\u6307\u6570\u7EA7\u6F14\u8FDB\u7684\u3002</p><p>\u76F4\u5230\u73B0\u5728\uFF0C\u6469\u5C14\u5B9A\u5F8B\u4F9D\u7136\u751F\u6548\uFF0C\u5728\u534A\u4E2A\u4E16\u7EAA\u4EE5\u6765\u7684 CPU \u53D1\u5C55\u8FC7\u7A0B\u4E2D\uFF0C\u82AF\u7247\u5382\u5546\u9760\u7740\u5728\u6709\u9650\u9762\u79EF\u4E0A\u505A\u66F4\u5C0F\u7684\u6676\u4F53\u7BA1\u7684\u9ED1\u79D1\u6280\uFF0C\u5927\u5E45\u5EA6\u5730\u63D0\u5347\u7740\u82AF\u7247\u7684\u6027\u80FD\u3002\u4ECE\u7B2C\u4E00\u4EE3\u96C6\u6210\u7535\u8DEF\u4E0A\u53EA\u6709\u5341\u51E0\u4E2A\u6676\u4F53\u7BA1\uFF0C\u5230\u73B0\u5728\u4E00\u4E2A\u82AF\u7247\u4E0A\u52A8\u8F84\u51E0\u5341\u4EBF\u6676\u4F53\u7BA1\u7684\u6570\u91CF\uFF0C\u6469\u5C14\u5B9A\u5F8B\u6307\u5F15\u7740\u82AF\u7247\u5382\u5546\u5B8C\u6210\u4E86\u6280\u672F\u4E0A\u7684\u98DE\u8DC3\u3002</p><p>\u4F46\u662F\u6709\u4E13\u5BB6\u9884\u6D4B\uFF0C\u6469\u5C14\u5B9A\u5F8B\u53EF\u80FD\u5728\u672A\u6765\u51E0\u5E74\u4E4B\u5185\u4E0D\u518D\u751F\u6548\uFF0C\u539F\u56E0\u662F\u76EE\u524D\u7684\u82AF\u7247\u6280\u672F\u5DF2\u7ECF\u505A\u5230\u4E86 5nm \u7EA7\u522B\uFF0C\u5728\u5DE5\u827A\u4E0A\u53EF\u4EE5\u7A81\u7834\u7684\u7A7A\u95F4\u4E0D\u5927\uFF0C\u53EF\u80FD\u8FBE\u4E0D\u5230\u6469\u5C14\u5B9A\u5F8B\u63D0\u5230\u7684\u6BCF 18 \u4E2A\u6708\u7FFB\u4E00\u756A\u7684\u901F\u5EA6\u4E86\u3002\u6B64\u65F6\uFF0C\u53CC\u6838\u548C\u591A\u6838\u6280\u672F\u7684\u4EA7\u751F\u62EF\u6551\u4E86\u6469\u5C14\u5B9A\u5F8B\uFF0C\u8FD9\u4E9B\u6280\u672F\u7684\u601D\u8DEF\u662F\u5C06\u591A\u4E2A CPU \u6838\u5FC3\u538B\u5728\u4E00\u4E2A\u82AF\u7247\u4E0A\uFF0C\u4ECE\u800C\u5927\u5927\u63D0\u5347 CPU \u7684\u5E76\u884C\u5904\u7406\u80FD\u529B\u3002</p><p>\u6211\u4EEC\u5728\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u4E0A\u4E5F\u6CBF\u7528\u4E86\u540C\u6837\u7684\u601D\u8DEF\uFF0C\u5C06\u7C7B\u4F3C\u8FFD\u9010\u6469\u5C14\u5B9A\u5F8B\u4E0D\u65AD\u63D0\u5347 CPU \u6027\u80FD\u7684\u65B9\u6848\u53EB\u505A Scale-up\uFF08\u7EB5\u5411\u6269\u5C55\uFF09\uFF0C\u628A\u7C7B\u4F3C CPU \u591A\u6838\u5FC3\u7684\u65B9\u6848\u53EB\u505A Scale-out\uFF0C\u8FD9\u4E24\u79CD\u601D\u8DEF\u5728\u5B9E\u73B0\u65B9\u5F0F\u4E0A\u662F\u5B8C\u5168\u4E0D\u540C\u7684\u3002</p><ul><li>Scale-up \u901A\u8FC7\u8D2D\u4E70\u6027\u80FD\u66F4\u597D\u7684\u786C\u4EF6\u6765\u63D0\u5347\u7CFB\u7EDF\u7684\u5E76\u53D1\u5904\u7406\u80FD\u529B\uFF0C\u6BD4\u65B9\u8BF4\u76EE\u524D\u7CFB\u7EDF 4 \u6838 4G \u6BCF\u79D2\u53EF\u4EE5\u5904\u7406 200 \u6B21\u8BF7\u6C42\uFF0C\u90A3\u4E48\u5982\u679C\u8981\u5904\u7406 400 \u6B21\u8BF7\u6C42\u5462\uFF1F\u5F88\u7B80\u5355\uFF0C\u6211\u4EEC\u628A\u673A\u5668\u7684\u786C\u4EF6\u63D0\u5347\u5230 8 \u6838 8G\uFF08\u786C\u4EF6\u8D44\u6E90\u7684\u63D0\u5347\u53EF\u80FD\u4E0D\u662F\u7EBF\u6027\u7684\uFF0C\u8FD9\u91CC\u4EC5\u4E3A\u53C2\u8003\uFF09\u3002</li><li>Scale-out \u5219\u662F\u53E6\u5916\u4E00\u4E2A\u601D\u8DEF\uFF0C\u5B83\u901A\u8FC7\u5C06\u591A\u4E2A\u4F4E\u6027\u80FD\u7684\u673A\u5668\u7EC4\u6210\u4E00\u4E2A\u5206\u5E03\u5F0F\u96C6\u7FA4\u6765\u5171\u540C\u62B5\u5FA1\u9AD8\u5E76\u53D1\u6D41\u91CF\u7684\u51B2\u51FB\u3002\u6CBF\u7528\u521A\u624D\u7684\u4F8B\u5B50\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u4E24\u53F0 4 \u6838 4G \u7684\u673A\u5668\u6765\u5904\u7406\u90A3 400 \u6B21\u8BF7\u6C42\u3002</li></ul><p>**\u90A3\u4E48\u4EC0\u4E48\u65F6\u5019\u9009\u62E9 Scale-up\uFF0C\u4EC0\u4E48\u65F6\u5019\u9009\u62E9 Scale-out \u5462\uFF1F**\u4E00\u822C\u6765\u8BB2\uFF0C\u5728\u6211\u4EEC\u7CFB\u7EDF\u8BBE\u8BA1\u521D\u671F\u4F1A\u8003\u8651\u4F7F\u7528 Scale-up \u7684\u65B9\u5F0F\uFF0C\u56E0\u4E3A\u8FD9\u79CD\u65B9\u6848\u8DB3\u591F\u7B80\u5355\uFF0C\u6240\u8C13\u80FD\u7528\u5806\u780C\u786C\u4EF6\u89E3\u51B3\u7684\u95EE\u9898\u5C31\u7528\u786C\u4EF6\u6765\u89E3\u51B3\uFF0C\u4F46\u662F\u5F53\u7CFB\u7EDF\u5E76\u53D1\u8D85\u8FC7\u4E86\u5355\u673A\u7684\u6781\u9650\u65F6\uFF0C\u6211\u4EEC\u5C31\u8981\u4F7F\u7528 Scale-out \u7684\u65B9\u5F0F\u3002</p><p>Scale-out \u867D\u7136\u80FD\u591F\u7A81\u7834\u5355\u673A\u7684\u9650\u5236\uFF0C\u4F46\u4E5F\u4F1A\u5F15\u5165\u4E00\u4E9B\u590D\u6742\u95EE\u9898\u3002\u6BD4\u5982\uFF0C\u5982\u679C\u67D0\u4E2A\u8282\u70B9\u51FA\u73B0\u6545\u969C\u5982\u4F55\u4FDD\u8BC1\u6574\u4F53\u53EF\u7528\u6027\uFF1F\u5F53\u591A\u4E2A\u8282\u70B9\u6709\u72B6\u6001\u9700\u8981\u540C\u6B65\u65F6\u5982\u4F55\u4FDD\u8BC1\u72B6\u6001\u4FE1\u606F\u5728\u4E0D\u540C\u8282\u70B9\u7684\u4E00\u81F4\u6027\uFF1F\u5982\u4F55\u505A\u5230\u4F7F\u7528\u65B9\u65E0\u611F\u77E5\u7684\u589E\u52A0\u548C\u5220\u9664\u8282\u70B9\uFF1F\u5176\u4E2D\u6BCF\u4E00\u4E2A\u95EE\u9898\u90FD\u6D89\u53CA\u5F88\u591A\u7684\u77E5\u8BC6\u70B9\uFF0C\u6211\u4F1A\u5728\u540E\u9762\u7684\u8BFE\u7A0B\u4E2D\u6DF1\u5165\u5730\u8BB2\u89E3\uFF0C\u8FD9\u91CC\u6682\u65F6\u4E0D\u5C55\u5F00\u4E86\u3002</p><p>\u8BF4\u5B8C\u4E86 Scale-out\uFF0C\u6211\u4EEC\u518D\u6765\u770B\u770B\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u7684\u53E6\u4E00\u79CD\u65B9\u6CD5\uFF1A<strong>\u7F13\u5B58</strong>\u3002</p><h3 id="\u4F7F\u7528\u7F13\u5B58\u63D0\u5347\u6027\u80FD" tabindex="-1">\u4F7F\u7528\u7F13\u5B58\u63D0\u5347\u6027\u80FD <a class="header-anchor" href="#\u4F7F\u7528\u7F13\u5B58\u63D0\u5347\u6027\u80FD" aria-hidden="true">#</a></h3><p>Web 2.0 \u662F\u7F13\u5B58\u7684\u65F6\u4EE3\uFF0C\u8FD9\u4E00\u70B9\u6BCB\u5EB8\u7F6E\u7591\u3002\u7F13\u5B58\u904D\u5E03\u5728\u7CFB\u7EDF\u8BBE\u8BA1\u7684\u6BCF\u4E2A\u89D2\u843D\uFF0C\u4ECE\u64CD\u4F5C\u7CFB\u7EDF\u5230\u6D4F\u89C8\u5668\uFF0C\u4ECE\u6570\u636E\u5E93\u5230\u6D88\u606F\u961F\u5217\uFF0C\u4EFB\u4F55\u7565\u5FAE\u590D\u6742\u7684\u670D\u52A1\u548C\u7EC4\u4EF6\u4E2D\u4F60\u90FD\u53EF\u4EE5\u770B\u5230\u7F13\u5B58\u7684\u5F71\u5B50\u3002\u6211\u4EEC\u4F7F\u7528\u7F13\u5B58\u7684\u4E3B\u8981\u4F5C\u7528\u662F\u63D0\u5347\u7CFB\u7EDF\u7684\u8BBF\u95EE\u6027\u80FD\uFF0C\u5728\u9AD8\u5E76\u53D1\u7684\u573A\u666F\u4E0B\u5C31\u53EF\u4EE5\u652F\u6491\u66F4\u591A\u7528\u6237\u7684\u540C\u65F6\u8BBF\u95EE\u3002</p><p>\u90A3\u4E48\u4E3A\u4EC0\u4E48\u7F13\u5B58\u53EF\u4EE5\u5927\u5E45\u5EA6\u63D0\u5347\u7CFB\u7EDF\u7684\u6027\u80FD\u5462\uFF1F\u6211\u4EEC\u77E5\u9053\u6570\u636E\u662F\u653E\u5728\u6301\u4E45\u5316\u5B58\u50A8\u4E2D\u7684\uFF0C\u4E00\u822C\u7684\u6301\u4E45\u5316\u5B58\u50A8\u90FD\u662F\u4F7F\u7528\u78C1\u76D8\u4F5C\u4E3A\u5B58\u50A8\u4ECB\u8D28\u7684\uFF0C\u800C\u666E\u901A\u78C1\u76D8\u6570\u636E\u7531\u673A\u68B0\u624B\u81C2\u3001\u78C1\u5934\u3001\u8F6C\u8F74\u3001\u76D8\u7247\u7EC4\u6210\uFF0C\u76D8\u7247\u53C8\u5206\u4E3A\u78C1\u9053\u3001\u67F1\u9762\u548C\u6247\u533A\uFF0C\u76D8\u7247\u6784\u9020\u56FE\u6211\u653E\u5728\u4E0B\u9762\u4E86\u3002</p><p>\u76D8\u7247\u662F\u5B58\u50A8\u4ECB\u8D28\uFF0C\u6BCF\u4E2A\u76D8\u7247\u88AB\u5212\u5206\u4E3A\u591A\u4E2A\u540C\u5FC3\u5706\uFF0C\u4FE1\u606F\u90FD\u88AB\u5B58\u50A8\u5728\u540C\u5FC3\u5706\u4E4B\u4E2D\uFF0C\u8FD9\u4E9B\u540C\u5FC3\u5706\u5C31\u662F\u78C1\u9053\u3002\u5728\u78C1\u76D8\u5DE5\u4F5C\u65F6\u76D8\u7247\u662F\u5728\u9AD8\u901F\u65CB\u8F6C\u7684\uFF0C\u673A\u68B0\u624B\u81C2\u9A71\u52A8\u78C1\u5934\u6CBF\u7740\u5F84\u5411\u79FB\u52A8\uFF0C\u5728\u78C1\u9053\u4E0A\u8BFB\u53D6\u6240\u9700\u8981\u7684\u6570\u636E\u3002\u6211\u4EEC\u628A\u78C1\u5934\u5BFB\u627E\u4FE1\u606F\u82B1\u8D39\u7684\u65F6\u95F4\u53EB\u505A\u5BFB\u9053\u65F6\u95F4\u3002</p><p><img src="https://static001.geekbang.org/resource/image/88/02/88a27d1b7a0f55917e59486137ab3002.jpg?wh=1142*764" alt=""></p><p>\u666E\u901A\u78C1\u76D8\u7684\u5BFB\u9053\u65F6\u95F4\u662F 10ms \u5DE6\u53F3\uFF0C\u800C\u76F8\u6BD4\u4E8E\u78C1\u76D8\u5BFB\u9053\u82B1\u8D39\u7684\u65F6\u95F4\uFF0CCPU \u6267\u884C\u6307\u4EE4\u548C\u5185\u5B58\u5BFB\u5740\u7684\u65F6\u95F4\u90FD\u662F\u5728 ns\uFF08\u7EB3\u79D2\uFF09\u7EA7\u522B\uFF0C\u4ECE\u5343\u5146\u7F51\u5361\u4E0A\u8BFB\u53D6\u6570\u636E\u7684\u65F6\u95F4\u662F\u5728\u03BCs\uFF08\u5FAE\u79D2\uFF09\u7EA7\u522B\u3002\u6240\u4EE5\u5728\u6574\u4E2A\u8BA1\u7B97\u673A\u4F53\u7CFB\u4E2D\u78C1\u76D8\u662F\u6700\u6162\u7684\u4E00\u73AF\uFF0C\u751A\u81F3\u6BD4\u5176\u5B83\u7684\u7EC4\u4EF6\u8981\u6162\u51E0\u4E2A\u6570\u91CF\u7EA7\u3002\u56E0\u6B64\u6211\u4EEC\u901A\u5E38\u4F7F\u7528\u4EE5\u5185\u5B58\u4F5C\u4E3A\u5B58\u50A8\u4ECB\u8D28\u7684\u7F13\u5B58\uFF0C\u4EE5\u6B64\u63D0\u5347\u6027\u80FD\u3002</p><p>\u5F53\u7136\uFF0C\u7F13\u5B58\u7684\u8BED\u4E49\u5DF2\u7ECF\u4E30\u5BCC\u4E86\u5F88\u591A\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5C06\u4EFB\u4F55\u964D\u4F4E\u54CD\u5E94\u65F6\u95F4\u7684\u4E2D\u95F4\u5B58\u50A8\u90FD\u79F0\u4E3A\u7F13\u5B58\u3002\u7F13\u5B58\u7684\u601D\u60F3\u904D\u5E03\u5F88\u591A\u8BBE\u8BA1\u9886\u57DF\uFF0C\u6BD4\u5982\u5728\u64CD\u4F5C\u7CFB\u7EDF\u4E2D CPU \u6709\u591A\u7EA7\u7F13\u5B58\uFF0C\u6587\u4EF6\u6709 Page Cache \u7F13\u5B58\uFF0C\u4F60\u5E94\u8BE5\u6709\u6240\u4E86\u89E3\u3002</p><h3 id="\u5F02\u6B65\u5904\u7406" tabindex="-1">\u5F02\u6B65\u5904\u7406 <a class="header-anchor" href="#\u5F02\u6B65\u5904\u7406" aria-hidden="true">#</a></h3><p><strong>\u5F02\u6B65</strong>\u4E5F\u662F\u4E00\u79CD\u5E38\u89C1\u7684\u9AD8\u5E76\u53D1\u8BBE\u8BA1\u65B9\u6CD5\uFF0C\u6211\u4EEC\u5728\u5F88\u591A\u6587\u7AE0\u548C\u6F14\u8BB2\u4E2D\u90FD\u80FD\u542C\u5230\u8FD9\u4E2A\u540D\u8BCD\uFF0C\u4E0E\u4E4B\u5171\u540C\u51FA\u73B0\u7684\u8FD8\u6709\u5B83\u7684\u53CD\u4E49\u8BCD\uFF1A\u540C\u6B65\u3002\u6BD4\u5982\u5206\u5E03\u5F0F\u670D\u52A1\u6846\u67B6 Dubbo \u4E2D\u6709\u540C\u6B65\u65B9\u6CD5\u8C03\u7528\u548C\u5F02\u6B65\u65B9\u6CD5\u8C03\u7528\uFF0CIO \u6A21\u578B\u4E2D\u6709\u540C\u6B65 IO \u548C\u5F02\u6B65 IO\u3002</p><p><strong>\u90A3\u4E48\u4EC0\u4E48\u662F\u540C\u6B65\uFF0C\u4EC0\u4E48\u662F\u5F02\u6B65\u5462</strong>\uFF1F\u4EE5\u65B9\u6CD5\u8C03\u7528\u4E3A\u4F8B\uFF0C\u540C\u6B65\u8C03\u7528\u4EE3\u8868\u8C03\u7528\u65B9\u8981\u963B\u585E\u7B49\u5F85\u88AB\u8C03\u7528\u65B9\u6CD5\u4E2D\u7684\u903B\u8F91\u6267\u884C\u5B8C\u6210\u3002\u8FD9\u79CD\u65B9\u5F0F\u4E0B\uFF0C\u5F53\u88AB\u8C03\u7528\u65B9\u6CD5\u54CD\u5E94\u65F6\u95F4\u8F83\u957F\u65F6\uFF0C\u4F1A\u9020\u6210\u8C03\u7528\u65B9\u957F\u4E45\u7684\u963B\u585E\uFF0C\u5728\u9AD8\u5E76\u53D1\u4E0B\u4F1A\u9020\u6210\u6574\u4F53\u7CFB\u7EDF\u6027\u80FD\u4E0B\u964D\u751A\u81F3\u53D1\u751F\u96EA\u5D29\u3002</p><p>\u5F02\u6B65\u8C03\u7528\u6070\u6070\u76F8\u53CD\uFF0C\u8C03\u7528\u65B9\u4E0D\u9700\u8981\u7B49\u5F85\u65B9\u6CD5\u903B\u8F91\u6267\u884C\u5B8C\u6210\u5C31\u53EF\u4EE5\u8FD4\u56DE\u6267\u884C\u5176\u4ED6\u7684\u903B\u8F91\uFF0C\u5728\u88AB\u8C03\u7528\u65B9\u6CD5\u6267\u884C\u5B8C\u6BD5\u540E\u518D\u901A\u8FC7\u56DE\u8C03\u3001\u4E8B\u4EF6\u901A\u77E5\u7B49\u65B9\u5F0F\u5C06\u7ED3\u679C\u53CD\u9988\u7ED9\u8C03\u7528\u65B9\u3002</p><p>\u5F02\u6B65\u8C03\u7528\u5728\u5927\u89C4\u6A21\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u4E2D\u88AB\u5927\u91CF\u4F7F\u7528\uFF0C<strong>\u6BD4\u5982\u6211\u4EEC\u719F\u77E5\u7684 12306 \u7F51\u7AD9</strong>\u3002\u5F53\u6211\u4EEC\u8BA2\u7968\u65F6\uFF0C\u9875\u9762\u4F1A\u663E\u793A\u7CFB\u7EDF\u6B63\u5728\u6392\u961F\uFF0C\u8FD9\u4E2A\u63D0\u793A\u5C31\u4EE3\u8868\u7740\u7CFB\u7EDF\u5728\u5F02\u6B65\u5904\u7406\u6211\u4EEC\u7684\u8BA2\u7968\u8BF7\u6C42\u3002\u5728 12306 \u7CFB\u7EDF\u4E2D\u67E5\u8BE2\u4F59\u7968\u3001\u4E0B\u5355\u548C\u66F4\u6539\u4F59\u7968\u72B6\u6001\u90FD\u662F\u6BD4\u8F83\u8017\u65F6\u7684\u64CD\u4F5C\uFF0C\u53EF\u80FD\u6D89\u53CA\u591A\u4E2A\u5185\u90E8\u7CFB\u7EDF\u7684\u4E92\u76F8\u8C03\u7528\uFF0C\u5982\u679C\u662F\u540C\u6B65\u8C03\u7528\u5C31\u4F1A\u50CF 12306 \u521A\u521A\u4E0A\u7EBF\u65F6\u90A3\u6837\uFF0C\u9AD8\u5CF0\u671F\u6C38\u8FDC\u4E0D\u53EF\u80FD\u4E0B\u5355\u6210\u529F\u3002</p><p>\u800C\u91C7\u7528\u5F02\u6B65\u7684\u65B9\u5F0F\uFF0C\u540E\u7AEF\u5904\u7406\u65F6\u4F1A\u628A\u8BF7\u6C42\u4E22\u5230\u6D88\u606F\u961F\u5217\u4E2D\uFF0C\u540C\u65F6\u5FEB\u901F\u54CD\u5E94\u7528\u6237\uFF0C\u544A\u8BC9\u7528\u6237\u6211\u4EEC\u6B63\u5728\u6392\u961F\u5904\u7406\uFF0C\u7136\u540E\u91CA\u653E\u51FA\u8D44\u6E90\u6765\u5904\u7406\u66F4\u591A\u7684\u8BF7\u6C42\u3002\u8BA2\u7968\u8BF7\u6C42\u5904\u7406\u5B8C\u4E4B\u540E\uFF0C\u518D\u901A\u77E5\u7528\u6237\u8BA2\u7968\u6210\u529F\u6216\u8005\u5931\u8D25\u3002</p><p>\u5904\u7406\u903B\u8F91\u540E\u79FB\u5230\u5F02\u6B65\u5904\u7406\u7A0B\u5E8F\u4E2D\uFF0CWeb \u670D\u52A1\u7684\u538B\u529B\u5C0F\u4E86\uFF0C\u8D44\u6E90\u5360\u7528\u7684\u5C11\u4E86\uFF0C\u81EA\u7136\u5C31\u80FD\u63A5\u6536\u66F4\u591A\u7684\u7528\u6237\u8BA2\u7968\u8BF7\u6C42\uFF0C\u7CFB\u7EDF\u627F\u53D7\u9AD8\u5E76\u53D1\u7684\u80FD\u529B\u4E5F\u5C31\u63D0\u5347\u4E86\u3002</p><p><img src="https://static001.geekbang.org/resource/image/07/09/0756d48f746590894b6e96ae4e4f7609.jpg?wh=1142*459" alt=""></p><p>\u65E2\u7136\u6211\u4EEC\u4E86\u89E3\u4E86\u8FD9\u4E09\u79CD\u65B9\u6CD5\uFF0C\u90A3\u4E48\u662F\u4E0D\u662F\u610F\u5473\u7740\u5728\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u4E2D\uFF0C\u5F00\u53D1\u4E00\u4E2A\u7CFB\u7EDF\u65F6\u8981\u628A\u8FD9\u4E9B\u65B9\u6CD5\u90FD\u7528\u4E0A\u5462\uFF1F\u5F53\u7136\u4E0D\u662F\uFF0C\u7CFB\u7EDF\u7684\u8BBE\u8BA1\u662F\u4E0D\u65AD\u6F14\u8FDB\u7684\u3002</p><p><strong>\u7F57\u9A6C\u4E0D\u662F\u4E00\u5929\u5EFA\u6210\u7684\uFF0C\u7CFB\u7EDF\u7684\u8BBE\u8BA1\u4E5F\u662F\u5982\u6B64</strong>\u3002\u4E0D\u540C\u91CF\u7EA7\u7684\u7CFB\u7EDF\u6709\u4E0D\u540C\u7684\u75DB\u70B9\uFF0C\u4E5F\u5C31\u6709\u4E0D\u540C\u7684\u67B6\u6784\u8BBE\u8BA1\u7684\u4FA7\u91CD\u70B9\u3002<strong>\u5982\u679C\u90FD\u6309\u7167\u767E\u4E07\u3001\u5343\u4E07\u5E76\u53D1\u6765\u8BBE\u8BA1\u7CFB\u7EDF\uFF0C\u7535\u5546\u4E00\u5F8B\u5411\u6DD8\u5B9D\u770B\u9F50\uFF0CIM \u5168\u90FD\u5B66\u4E60\u5FAE\u4FE1\u548C QQ\uFF0C\u90A3\u4E48\u8FD9\u4E9B\u7CFB\u7EDF\u7684\u547D\u8FD0\u4E00\u5B9A\u662F\u706D\u4EA1\u3002</strong></p><p>\u56E0\u4E3A\u6DD8\u5B9D\u3001\u5FAE\u4FE1\u7684\u7CFB\u7EDF\u867D\u7136\u80FD\u591F\u89E3\u51B3\u540C\u65F6\u767E\u4E07\u3001\u5343\u4E07\u4EBA\u540C\u65F6\u5728\u7EBF\u7684\u9700\u6C42\uFF0C\u4F46\u5176\u5185\u90E8\u7684\u590D\u6742\u7A0B\u5EA6\u4E5F\u8FDC\u975E\u6211\u4EEC\u80FD\u591F\u60F3\u8C61\u7684\u3002\u76F2\u76EE\u5730\u8FFD\u4ECE\u53EA\u80FD\u8BA9\u6211\u4EEC\u7684\u67B6\u6784\u590D\u6742\u4E0D\u582A\uFF0C\u6700\u7EC8\u96BE\u4EE5\u7EF4\u62A4\u3002\u5C31\u62FF\u4ECE\u5355\u4F53\u67B6\u6784\u5F80\u670D\u52A1\u5316\u6F14\u8FDB\u6765\u8BF4\uFF0C\u6DD8\u5B9D\u4E5F\u662F\u5728\u7ECF\u5386\u4E86\u591A\u5E74\u7684\u53D1\u5C55\u540E\uFF0C\u53D1\u73B0\u7CFB\u7EDF\u6574\u4F53\u7684\u6269\u5C55\u80FD\u529B\u51FA\u73B0\u95EE\u9898\u65F6\uFF0C\u5F00\u59CB\u542F\u52A8\u670D\u52A1\u5316\u6539\u9020\u9879\u76EE\u7684\u3002</p><p>\u6211\u4E4B\u524D\u4E5F\u8E29\u8FC7\u4E00\u4E9B\u5751\uFF0C\u53C2\u4E0E\u7684\u4E00\u4E2A\u521B\u4E1A\u9879\u76EE\u5728\u521D\u59CB\u9636\u6BB5\u5C31\u91C7\u7528\u4E86\u670D\u52A1\u5316\u7684\u67B6\u6784\uFF0C\u4F46\u7531\u4E8E\u5F53\u65F6\u4EBA\u529B\u6709\u9650\uFF0C\u56E2\u961F\u6280\u672F\u79EF\u7D2F\u4E0D\u8DB3\uFF0C\u56E0\u6B64\u5728\u5B9E\u9645\u9879\u76EE\u5F00\u53D1\u8FC7\u7A0B\u4E2D\uFF0C\u53D1\u73B0\u65E0\u6CD5\u9A7E\u9A6D\u5982\u6B64\u590D\u6742\u7684\u67B6\u6784\uFF0C\u4E5F\u51FA\u73B0\u4E86\u95EE\u9898\u96BE\u4EE5\u5B9A\u4F4D\u3001\u7CFB\u7EDF\u6574\u4F53\u6027\u80FD\u4E0B\u964D\u7B49\u591A\u65B9\u9762\u7684\u95EE\u9898\uFF0C\u751A\u81F3\u8FDE\u7CFB\u7EDF\u5B95\u673A\u4E86\u90FD\u5F88\u96BE\u8FFD\u67E5\u5230\u6839\u672C\u539F\u56E0\uFF0C\u6700\u540E\u4E0D\u5F97\u4E0D\u628A\u670D\u52A1\u505A\u6574\u5408\uFF0C\u56DE\u5F52\u5230\u7B80\u5355\u7684\u5355\u4F53\u67B6\u6784\u4E2D\u3002</p><p>\u6240\u4EE5\u6211\u5EFA\u8BAE\u4E00\u822C\u7CFB\u7EDF\u7684\u6F14\u8FDB\u8FC7\u7A0B\u5E94\u8BE5\u9075\u5FAA\u4E0B\u9762\u7684\u601D\u8DEF\uFF1A</p><ul><li>\u6700\u7B80\u5355\u7684\u7CFB\u7EDF\u8BBE\u8BA1\u6EE1\u8DB3\u4E1A\u52A1\u9700\u6C42\u548C\u6D41\u91CF\u73B0\u72B6\uFF0C\u9009\u62E9\u6700\u719F\u6089\u7684\u6280\u672F\u4F53\u7CFB\u3002</li><li>\u968F\u7740\u6D41\u91CF\u7684\u589E\u52A0\u548C\u4E1A\u52A1\u7684\u53D8\u5316\u4FEE\u6B63\u67B6\u6784\u4E2D\u5B58\u5728\u95EE\u9898\u7684\u70B9\uFF0C\u5982\u5355\u70B9\u95EE\u9898\u3001\u6A2A\u5411\u6269\u5C55\u95EE\u9898\u3001\u6027\u80FD\u65E0\u6CD5\u6EE1\u8DB3\u9700\u6C42\u7684\u7EC4\u4EF6\u3002\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C\u9009\u62E9\u793E\u533A\u6210\u719F\u7684\u3001\u56E2\u961F\u719F\u6089\u7684\u7EC4\u4EF6\u5E2E\u52A9\u6211\u4EEC\u89E3\u51B3\u95EE\u9898\uFF0C\u5728\u793E\u533A\u6CA1\u6709\u5408\u9002\u89E3\u51B3\u65B9\u6848\u7684\u524D\u63D0\u4E0B\u624D\u4F1A\u81EA\u5DF1\u9020\u8F6E\u5B50\u3002</li><li>\u5F53\u5BF9\u67B6\u6784\u7684\u5C0F\u4FEE\u5C0F\u8865\u65E0\u6CD5\u6EE1\u8DB3\u9700\u6C42\u65F6\uFF0C\u8003\u8651\u91CD\u6784\u3001\u91CD\u5199\u7B49\u5927\u7684\u8C03\u6574\u65B9\u5F0F\u4EE5\u89E3\u51B3\u73B0\u6709\u7684\u95EE\u9898\u3002</li></ul><p>\u4EE5\u6DD8\u5B9D\u4E3A\u4F8B\uFF0C\u5F53\u65F6\u5728\u4E1A\u52A1\u4ECE 0 \u5230 1 \u7684\u9636\u6BB5\u662F\u901A\u8FC7\u8D2D\u4E70\u7684\u65B9\u5F0F\u5FEB\u901F\u642D\u5EFA\u4E86\u7CFB\u7EDF\u3002\u800C\u540E\uFF0C\u968F\u7740\u6D41\u91CF\u7684\u589E\u957F\uFF0C\u6DD8\u5B9D\u505A\u4E86\u4E00\u7CFB\u5217\u7684\u6280\u672F\u6539\u9020\u6765\u63D0\u5347\u9AD8\u5E76\u53D1\u5904\u7406\u80FD\u529B\uFF0C\u6BD4\u5982\u6570\u636E\u5E93\u5B58\u50A8\u5F15\u64CE\u4ECE MyISAM \u8FC1\u79FB\u5230 InnoDB\uFF0C\u6570\u636E\u5E93\u505A\u5206\u5E93\u5206\u8868\uFF0C\u589E\u52A0\u7F13\u5B58\uFF0C\u542F\u52A8\u4E2D\u95F4\u4EF6\u7814\u53D1\u7B49\u3002</p><p>\u5F53\u8FD9\u4E9B\u90FD\u65E0\u6CD5\u6EE1\u8DB3\u65F6\u5C31\u8003\u8651\u5BF9\u6574\u4F53\u67B6\u6784\u505A\u5927\u89C4\u6A21\u91CD\u6784\uFF0C\u6BD4\u5982\u8BF4\u8457\u540D\u7684\u201C\u4E94\u5F69\u77F3\u201D\u9879\u76EE\u8BA9\u6DD8\u5B9D\u7684\u67B6\u6784\u4ECE\u5355\u4F53\u6F14\u8FDB\u4E3A\u670D\u52A1\u5316\u67B6\u6784\u3002\u6B63\u662F\u901A\u8FC7\u9010\u6B65\u7684\u6280\u672F\u6F14\u8FDB\uFF0C\u6DD8\u5B9D\u624D\u8FDB\u5316\u51FA\u5982\u4ECA\u627F\u62C5\u8FC7\u4EBF QPS \u7684\u6280\u672F\u67B6\u6784\u3002</p><p>\u5F52\u6839\u7ED3\u5E95\u4E00\u53E5\u8BDD\uFF1A<strong>\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u7684\u6F14\u8FDB\u5E94\u8BE5\u662F\u5FAA\u5E8F\u6E10\u8FDB\uFF0C\u4EE5\u89E3\u51B3\u7CFB\u7EDF\u4E2D\u5B58\u5728\u7684\u95EE\u9898\u4E3A\u76EE\u7684\u548C\u9A71\u52A8\u529B\u7684\u3002</strong></p><h3 id="\u8BFE\u7A0B\u5C0F\u7ED3" tabindex="-1">\u8BFE\u7A0B\u5C0F\u7ED3 <a class="header-anchor" href="#\u8BFE\u7A0B\u5C0F\u7ED3" aria-hidden="true">#</a></h3><p>\u5728\u4ECA\u5929\u7684\u8BFE\u7A0B\u4E2D\uFF0C\u6211\u5E26\u7740\u4F60\u4E86\u89E3\u4E86\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u8BBE\u8BA1\u7684\u4E09\u79CD\u901A\u7528\u65B9\u6CD5\uFF1A<strong>Scale-out\u3001\u7F13\u5B58\u548C\u5F02\u6B65</strong>\u3002\u8FD9\u4E09\u79CD\u65B9\u6CD5\u53EF\u4EE5\u5728\u505A\u65B9\u6848\u8BBE\u8BA1\u65F6\u7075\u6D3B\u5730\u8FD0\u7528\uFF0C\u4F46\u5B83\u4E0D\u662F\u5177\u4F53\u5B9E\u65BD\u7684\u65B9\u6848\uFF0C\u800C\u662F\u4E09\u79CD\u601D\u60F3\uFF0C\u5728\u5B9E\u9645\u8FD0\u7528\u4E2D\u4F1A\u5343\u53D8\u4E07\u5316\u3002</p><p>\u5C31\u62FF Scale-out \u6765\u8BF4\uFF0C\u6570\u636E\u5E93\u4E00\u4E3B\u591A\u4ECE\u3001\u5206\u5E93\u5206\u8868\u3001\u5B58\u50A8\u5206\u7247\u90FD\u662F\u5B83\u7684\u5B9E\u9645\u5E94\u7528\u65B9\u6848\u3002\u800C\u6211\u4EEC\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u5728\u5E94\u5BF9\u9AD8\u5E76\u53D1\u5927\u6D41\u91CF\u7684\u65F6\u5019\uFF0C\u7CFB\u7EDF\u662F\u53EF\u4EE5\u901A\u8FC7\u589E\u52A0\u673A\u5668\u6765\u627F\u62C5\u6D41\u91CF\u51B2\u51FB\u7684\uFF0C\u81F3\u4E8E\u8981\u91C7\u7528\u4EC0\u4E48\u6837\u7684\u65B9\u6848\u8FD8\u662F\u8981\u5177\u4F53\u95EE\u9898\u5177\u4F53\u5206\u6790\u3002</p><h3 id="\u4E00\u8BFE\u4E00\u601D" tabindex="-1">\u4E00\u8BFE\u4E00\u601D <a class="header-anchor" href="#\u4E00\u8BFE\u4E00\u601D" aria-hidden="true">#</a></h3><p>\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u6F14\u8FDB\u662F\u4E00\u4E2A\u6E10\u8FDB\u7684\u8FC7\u7A0B\uFF0C\u5E76\u975E\u4E00\u8E74\u800C\u5C31\u7684\uFF0C\u90A3\u4E48\u4F60\u5728\u7CFB\u7EDF\u6F14\u8FDB\u8FC7\u7A0B\u4E2D\u79EF\u7D2F\u4E86\u54EA\u4E9B\u7ECF\u9A8C\u53C8\u8E29\u5230\u4E86\u54EA\u4E9B\u5751\u5462\uFF1F\u6B22\u8FCE\u5728\u7559\u8A00\u533A\u4E0E\u6211\u4E00\u540C\u4EA4\u6D41\u3002</p>',48),r=[s];function i(n,c,o,d,h,u){return a(),p("div",null,r)}const S=e(l,[["render",i]]);export{g as __pageData,S as default};
