import{_ as e,o as a,c as p,a as t}from"./app.303cfecc.js";const u=JSON.parse('{"title":"05 | \u7CFB\u7EDF\u8BBE\u8BA1\u76EE\u6807\uFF08\u4E09\uFF09\uFF1A\u5982\u4F55\u8BA9\u7CFB\u7EDF\u6613\u4E8E\u6269\u5C55\uFF1F","description":"","frontmatter":{},"headers":[{"level":3,"title":"\u4E3A\u4EC0\u4E48\u63D0\u5347\u6269\u5C55\u6027\u4F1A\u5F88\u590D\u6742","slug":"\u4E3A\u4EC0\u4E48\u63D0\u5347\u6269\u5C55\u6027\u4F1A\u5F88\u590D\u6742","link":"#\u4E3A\u4EC0\u4E48\u63D0\u5347\u6269\u5C55\u6027\u4F1A\u5F88\u590D\u6742","children":[]},{"level":3,"title":"\u9AD8\u53EF\u6269\u5C55\u6027\u7684\u8BBE\u8BA1\u601D\u8DEF","slug":"\u9AD8\u53EF\u6269\u5C55\u6027\u7684\u8BBE\u8BA1\u601D\u8DEF","link":"#\u9AD8\u53EF\u6269\u5C55\u6027\u7684\u8BBE\u8BA1\u601D\u8DEF","children":[]},{"level":3,"title":"\u8BFE\u7A0B\u5C0F\u7ED3","slug":"\u8BFE\u7A0B\u5C0F\u7ED3","link":"#\u8BFE\u7A0B\u5C0F\u7ED3","children":[]},{"level":3,"title":"\u4E00\u8BFE\u4E00\u601D","slug":"\u4E00\u8BFE\u4E00\u601D","link":"#\u4E00\u8BFE\u4E00\u601D","children":[]}],"relativePath":"0002/01\u57FA\u7840/05-\u7CFB\u7EDF\u8BBE\u8BA1\u76EE\u6807\uFF08\u4E09\uFF09\uFF1A\u5982\u4F55\u8BA9\u7CFB\u7EDF\u6613\u4E8E\u6269\u5C55\uFF1F.md","lastUpdated":1680763907000}'),r={name:"0002/01\u57FA\u7840/05-\u7CFB\u7EDF\u8BBE\u8BA1\u76EE\u6807\uFF08\u4E09\uFF09\uFF1A\u5982\u4F55\u8BA9\u7CFB\u7EDF\u6613\u4E8E\u6269\u5C55\uFF1F.md"},i=t('<h1 id="_05-\u7CFB\u7EDF\u8BBE\u8BA1\u76EE\u6807-\u4E09-\u5982\u4F55\u8BA9\u7CFB\u7EDF\u6613\u4E8E\u6269\u5C55" tabindex="-1">05 | \u7CFB\u7EDF\u8BBE\u8BA1\u76EE\u6807\uFF08\u4E09\uFF09\uFF1A\u5982\u4F55\u8BA9\u7CFB\u7EDF\u6613\u4E8E\u6269\u5C55\uFF1F <a class="header-anchor" href="#_05-\u7CFB\u7EDF\u8BBE\u8BA1\u76EE\u6807-\u4E09-\u5982\u4F55\u8BA9\u7CFB\u7EDF\u6613\u4E8E\u6269\u5C55" aria-hidden="true">#</a></h1><p>\u4ECE\u67B6\u6784\u8BBE\u8BA1\u4E0A\u6765\u8BF4\uFF0C\u9AD8\u53EF\u6269\u5C55\u6027\u662F\u4E00\u4E2A\u8BBE\u8BA1\u7684\u6307\u6807\uFF0C\u5B83\u8868\u793A\u53EF\u4EE5\u901A\u8FC7\u589E\u52A0\u673A\u5668\u7684\u65B9\u5F0F\u6765\u7EBF\u6027\u63D0\u9AD8\u7CFB\u7EDF\u7684\u5904\u7406\u80FD\u529B\uFF0C\u4ECE\u800C\u627F\u62C5\u66F4\u9AD8\u7684\u6D41\u91CF\u548C\u5E76\u53D1\u3002</p><p>\u4F60\u53EF\u80FD\u4F1A\u95EE\uFF1A\u201C\u5728\u67B6\u6784\u8BBE\u8BA1\u4E4B\u521D\uFF0C\u4E3A\u4EC0\u4E48\u4E0D\u9884\u5148\u8003\u8651\u597D\u4F7F\u7528\u591A\u5C11\u53F0\u673A\u5668\uFF0C\u652F\u6301\u73B0\u6709\u7684\u5E76\u53D1\u5462\uFF1F\u201D\u8FD9\u4E2A\u95EE\u9898\u6211\u5728\u201C03 | \u7CFB\u7EDF\u8BBE\u8BA1\u76EE\u6807\uFF08\u4E00\uFF09\uFF1A\u5982\u4F55\u63D0\u5347\u7CFB\u7EDF\u6027\u80FD\uFF1F\u201D\u4E00\u8BFE\u4E2D\u63D0\u5230\u8FC7\uFF0C\u7B54\u6848\u662F\u5CF0\u503C\u7684\u6D41\u91CF\u4E0D\u53EF\u63A7\u3002</p><p>\u4E00\u822C\u6765\u8BF4\uFF0C\u57FA\u4E8E\u6210\u672C\u8003\u8651\uFF0C\u5728\u4E1A\u52A1\u5E73\u7A33\u671F\uFF0C\u6211\u4EEC\u4F1A\u9884\u7559 30%\uFF5E50% \u7684\u5197\u4F59\u4EE5\u5E94\u5BF9\u8FD0\u8425\u6D3B\u52A8\u6216\u8005\u63A8\u5E7F\u53EF\u80FD\u5E26\u6765\u7684\u5CF0\u503C\u6D41\u91CF\uFF0C\u4F46\u662F\u5F53\u6709\u4E00\u4E2A\u7A81\u53D1\u4E8B\u4EF6\u53D1\u751F\u65F6\uFF0C\u6D41\u91CF\u53EF\u80FD\u77AC\u95F4\u63D0\u5347\u5230 2\uFF5E3 \u500D\u751A\u81F3\u66F4\u9AD8\uFF0C\u6211\u4EEC\u8FD8\u662F\u4EE5\u5FAE\u535A\u4E3A\u4F8B\u3002</p><p>\u9E7F\u6657\u548C\u5173\u6653\u5F64\u4E92\u5708\u516C\u5E03\u604B\u60C5\uFF0C\u5927\u5BB6\u4F1A\u5728\u4E24\u4E2A\u4EBA\u7684\u5FAE\u535A\u4E0B\u9762\uFF0C\u6216\u56F4\u89C2\uFF0C\u6216\u4E92\u52A8\uFF0C\u5FAE\u535A\u7684\u6D41\u91CF\u77ED\u65F6\u95F4\u5185\u589E\u957F\u8FC5\u901F\uFF0C\u5FAE\u535A\u4FE1\u606F\u6D41\u4E5F\u77ED\u6682\u51FA\u73B0\u65E0\u6CD5\u5237\u51FA\u65B0\u7684\u6D88\u606F\u7684\u60C5\u51B5\u3002</p><p>\u90A3\u6211\u4EEC\u8981\u5982\u4F55\u5E94\u5BF9\u7A81\u53D1\u7684\u6D41\u91CF\u5462\uFF1F\u67B6\u6784\u7684\u6539\u9020\u5DF2\u7ECF\u6765\u4E0D\u53CA\u4E86\uFF0C\u6700\u5FEB\u7684\u65B9\u5F0F\u5C31\u662F\u5806\u673A\u5668\u3002\u4E0D\u8FC7\u6211\u4EEC\u9700\u8981\u4FDD\u8BC1\uFF0C\u6269\u5BB9\u4E86\u4E09\u500D\u7684\u673A\u5668\u4E4B\u540E\uFF0C\u76F8\u5E94\u7684\u6211\u4EEC\u7684\u7CFB\u7EDF\u4E5F\u80FD\u652F\u6491\u4E09\u500D\u7684\u6D41\u91CF\u3002\u6709\u7684\u4EBA\u53EF\u80FD\u4F1A\u4EA7\u751F\u7591\u95EE\uFF1A\u201C\u8FD9\u4E0D\u662F\u663E\u800C\u6613\u89C1\u7684\u5417\uFF1F\u5F88\u7B80\u5355\u554A\u3002\u201D\u771F\u7684\u662F\u8FD9\u6837\u5417\uFF1F\u6211\u4EEC\u6765\u770B\u770B\u505A\u8FD9\u4EF6\u4E8B\u513F\u96BE\u5728\u54EA\u513F\u3002</p><h3 id="\u4E3A\u4EC0\u4E48\u63D0\u5347\u6269\u5C55\u6027\u4F1A\u5F88\u590D\u6742" tabindex="-1">\u4E3A\u4EC0\u4E48\u63D0\u5347\u6269\u5C55\u6027\u4F1A\u5F88\u590D\u6742 <a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u63D0\u5347\u6269\u5C55\u6027\u4F1A\u5F88\u590D\u6742" aria-hidden="true">#</a></h3><p>\u5728\u4E0A\u4E00\u8BB2\u4E2D\uFF0C\u6211\u63D0\u5230\u53EF\u4EE5\u5728\u5355\u673A\u7CFB\u7EDF\u4E2D\u901A\u8FC7\u589E\u52A0\u5904\u7406\u6838\u5FC3\u7684\u65B9\u5F0F\uFF0C\u6765\u589E\u52A0\u7CFB\u7EDF\u7684\u5E76\u884C\u5904\u7406\u80FD\u529B\uFF0C\u4F46\u8FD9\u4E2A\u65B9\u5F0F\u5E76\u4E0D\u603B\u594F\u6548\u3002\u56E0\u4E3A\u5F53\u5E76\u884C\u7684\u4EFB\u52A1\u6570\u8F83\u591A\u65F6\uFF0C\u7CFB\u7EDF\u4F1A\u56E0\u4E3A\u4E89\u62A2\u8D44\u6E90\u800C\u8FBE\u5230\u6027\u80FD\u4E0A\u7684\u62D0\u70B9\uFF0C\u7CFB\u7EDF\u5904\u7406\u80FD\u529B\u4E0D\u5347\u53CD\u964D\u3002</p><p>\u800C\u5BF9\u4E8E\u7531\u591A\u53F0\u673A\u5668\u7EC4\u6210\u7684\u96C6\u7FA4\u7CFB\u7EDF\u6765\u8BF4\u4E5F\u662F\u5982\u6B64\u3002\u96C6\u7FA4\u7CFB\u7EDF\u4E2D\uFF0C\u4E0D\u540C\u7684\u7CFB\u7EDF\u5206\u5C42\u4E0A\u53EF\u80FD\u5B58\u5728\u4E00\u4E9B\u201C\u74F6\u9888\u70B9\u201D\uFF0C\u8FD9\u4E9B\u74F6\u9888\u70B9\u5236\u7EA6\u7740\u7CFB\u7EDF\u7684\u6A2A\u5411\u6269\u5C55\u80FD\u529B\u3002\u8FD9\u53E5\u8BDD\u6BD4\u8F83\u62BD\u8C61\uFF0C\u6211\u4E3E\u4E2A\u4F8B\u5B50\u4F60\u5C31\u660E\u767D\u4E86\u3002</p><p>\u6BD4\u65B9\u8BF4\uFF0C\u4F60\u7CFB\u7EDF\u7684\u6D41\u91CF\u662F\u6BCF\u79D2 1000 \u6B21\u8BF7\u6C42\uFF0C\u5BF9\u6570\u636E\u5E93\u7684\u8BF7\u6C42\u91CF\u4E5F\u662F\u6BCF\u79D2 1000 \u6B21\u3002\u5982\u679C\u6D41\u91CF\u589E\u52A0 10 \u500D\uFF0C\u867D\u7136\u7CFB\u7EDF\u53EF\u4EE5\u901A\u8FC7\u6269\u5BB9\u6B63\u5E38\u670D\u52A1\uFF0C\u6570\u636E\u5E93\u5374\u6210\u4E86\u74F6\u9888\u3002\u518D\u6BD4\u65B9\u8BF4\uFF0C\u5355\u673A\u7F51\u7EDC\u5E26\u5BBD\u662F 50Mbps\uFF0C\u90A3\u4E48\u5982\u679C\u6269\u5BB9\u5230 30 \u53F0\u673A\u5668\uFF0C\u524D\u7AEF\u8D1F\u8F7D\u5747\u8861\u7684\u5E26\u5BBD\u5C31\u8D85\u8FC7\u4E86\u5343\u5146\u5E26\u5BBD\u7684\u9650\u5236\uFF0C\u4E5F\u4F1A\u6210\u4E3A\u74F6\u9888\u70B9\u3002\u90A3\u4E48\uFF0C\u6211\u4EEC\u7684\u7CFB\u7EDF\u4E2D\u5B58\u5728\u54EA\u4E9B\u670D\u52A1\u4F1A\u6210\u4E3A\u5236\u7EA6\u7CFB\u7EDF\u6269\u5C55\u7684\u91CD\u8981\u56E0\u7D20\u5462\uFF1F</p><p>\u5176\u5B9E\uFF0C\u65E0\u72B6\u6001\u7684\u670D\u52A1\u548C\u7EC4\u4EF6\u66F4\u6613\u4E8E\u6269\u5C55\uFF0C\u800C\u50CF MySQL \u8FD9\u79CD\u5B58\u50A8\u670D\u52A1\u662F\u6709\u72B6\u6001\u7684\uFF0C\u5C31\u6BD4\u8F83\u96BE\u4EE5\u6269\u5C55\u3002\u56E0\u4E3A\u5411\u5B58\u50A8\u96C6\u7FA4\u4E2D\u589E\u52A0\u6216\u8005\u51CF\u5C11\u673A\u5668\u65F6\uFF0C\u4F1A\u6D89\u53CA\u5927\u91CF\u6570\u636E\u7684\u8FC1\u79FB\uFF0C\u800C\u4E00\u822C\u4F20\u7EDF\u7684\u5173\u7CFB\u578B\u6570\u636E\u5E93\u90FD\u4E0D\u652F\u6301\u3002\u8FD9\u5C31\u662F\u4E3A\u4EC0\u4E48\u63D0\u5347\u7CFB\u7EDF\u6269\u5C55\u6027\u4F1A\u5F88\u590D\u6742\u7684\u4E3B\u8981\u539F\u56E0\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u4ECE\u4F8B\u5B50\u4E2D\u4F60\u53EF\u4EE5\u770B\u5230\uFF0C\u6211\u4EEC\u9700\u8981\u7AD9\u5728\u6574\u4F53\u67B6\u6784\u7684\u89D2\u5EA6\uFF0C\u800C\u4E0D\u4EC5\u4EC5\u662F\u4E1A\u52A1\u670D\u52A1\u5668\u7684\u89D2\u5EA6\u6765\u8003\u8651\u7CFB\u7EDF\u7684\u6269\u5C55\u6027 \u3002<strong>\u6240\u4EE5\u8BF4\uFF0C\u6570\u636E\u5E93\u3001\u7F13\u5B58\u3001\u4F9D\u8D56\u7684\u7B2C\u4E09\u65B9\u3001\u8D1F\u8F7D\u5747\u8861\u3001\u4EA4\u6362\u673A\u5E26\u5BBD\u7B49\u7B49\u90FD\u662F\u7CFB\u7EDF\u6269\u5C55\u65F6\u9700\u8981\u8003\u8651\u7684\u56E0\u7D20</strong>\u3002\u6211\u4EEC\u8981\u77E5\u9053\u7CFB\u7EDF\u5E76\u53D1\u5230\u4E86\u67D0\u4E00\u4E2A\u91CF\u7EA7\u4E4B\u540E\uFF0C\u54EA\u4E00\u4E2A\u56E0\u7D20\u4F1A\u6210\u4E3A\u6211\u4EEC\u7684\u74F6\u9888\u70B9\uFF0C\u4ECE\u800C\u9488\u5BF9\u6027\u5730\u8FDB\u884C\u6269\u5C55\u3002</p><p>\u9488\u5BF9\u8FD9\u4E9B\u590D\u6742\u7684\u6269\u5C55\u6027\u95EE\u9898\uFF0C\u6211\u63D0\u70BC\u4E86\u4E00\u4E9B\u7CFB\u7EDF\u8BBE\u8BA1\u601D\u8DEF\uFF0C\u4F9B\u4F60\u4E86\u89E3\u3002</p><h3 id="\u9AD8\u53EF\u6269\u5C55\u6027\u7684\u8BBE\u8BA1\u601D\u8DEF" tabindex="-1">\u9AD8\u53EF\u6269\u5C55\u6027\u7684\u8BBE\u8BA1\u601D\u8DEF <a class="header-anchor" href="#\u9AD8\u53EF\u6269\u5C55\u6027\u7684\u8BBE\u8BA1\u601D\u8DEF" aria-hidden="true">#</a></h3><p><strong>\u62C6\u5206</strong>\u662F\u63D0\u5347\u7CFB\u7EDF\u6269\u5C55\u6027\u6700\u91CD\u8981\u7684\u4E00\u4E2A\u601D\u8DEF\uFF0C\u5B83\u4F1A\u628A\u5E9E\u6742\u7684\u7CFB\u7EDF\u62C6\u5206\u6210\u72EC\u7ACB\u7684\uFF0C\u6709\u5355\u4E00\u804C\u8D23\u7684\u6A21\u5757\u3002\u76F8\u5BF9\u4E8E\u5927\u7CFB\u7EDF\u6765\u8BF4\uFF0C\u8003\u8651\u4E00\u4E2A\u4E00\u4E2A\u5C0F\u6A21\u5757\u7684\u6269\u5C55\u6027\u5F53\u7136\u4F1A\u7B80\u5355\u4E00\u4E9B\u3002<strong>\u5C06\u590D\u6742\u7684\u95EE\u9898\u7B80\u5355\u5316\uFF0C\u8FD9\u5C31\u662F\u6211\u4EEC\u7684\u601D\u8DEF\u3002</strong></p><p>\u4F46\u5BF9\u4E8E\u4E0D\u540C\u7C7B\u578B\u7684\u6A21\u5757\uFF0C\u6211\u4EEC\u5728\u62C6\u5206\u4E0A\u9075\u5FAA\u7684\u539F\u5219\u662F\u4E0D\u4E00\u6837\u7684\u3002\u6211\u7ED9\u4F60\u4E3E\u4E00\u4E2A\u7B80\u5355\u7684\u4F8B\u5B50\uFF0C\u5047\u5982\u4F60\u8981\u8BBE\u8BA1\u4E00\u4E2A\u793E\u533A\uFF0C\u90A3\u4E48\u793E\u533A\u4F1A\u6709\u51E0\u4E2A\u6A21\u5757\u5462\uFF1F\u53EF\u80FD\u6709 5 \u4E2A\u6A21\u5757\u3002</p><ul><li>\u7528\u6237\uFF1A\u8D1F\u8D23\u7EF4\u62A4\u793E\u533A\u7528\u6237\u4FE1\u606F\uFF0C\u6CE8\u518C\uFF0C\u767B\u9646\u7B49\uFF1B</li><li>\u5173\u7CFB\uFF1A\u7528\u6237\u4E4B\u95F4\u5173\u6CE8\u3001\u597D\u53CB\u3001\u62C9\u9ED1\u7B49\u5173\u7CFB\u7684\u7EF4\u62A4\uFF1B</li><li>\u5185\u5BB9\uFF1A\u793E\u533A\u53D1\u7684\u5185\u5BB9\uFF0C\u5C31\u50CF\u670B\u53CB\u5708\u6216\u8005\u5FAE\u535A\u7684\u5185\u5BB9\uFF1B</li><li>\u8BC4\u8BBA\u3001\u8D5E\uFF1A\u7528\u6237\u53EF\u80FD\u4F1A\u6709\u7684\u4E24\u79CD\u5E38\u89C4\u4E92\u52A8\u64CD\u4F5C\uFF1B</li><li>\u641C\u7D22\uFF1A\u7528\u6237\u7684\u641C\u7D22\uFF0C\u5185\u5BB9\u7684\u641C\u7D22\u3002</li></ul><p>\u800C\u90E8\u7F72\u65B9\u5F0F\u6309\u7167\u6700\u7B80\u5355\u7684\u4E09\u5C42\u90E8\u7F72\u67B6\u6784\uFF0C\u8D1F\u8F7D\u5747\u8861\u8D1F\u8D23\u8BF7\u6C42\u7684\u5206\u53D1\uFF0C\u5E94\u7528\u670D\u52A1\u5668\u8D1F\u8D23\u4E1A\u52A1\u903B\u8F91\u7684\u5904\u7406\uFF0C\u6570\u636E\u5E93\u8D1F\u8D23\u6570\u636E\u7684\u5B58\u50A8\u843D\u5730\u3002\u8FD9\u65F6\uFF0C\u6240\u6709\u6A21\u5757\u7684\u4E1A\u52A1\u4EE3\u7801\u90FD\u6DF7\u5408\u5728\u4E00\u8D77\u4E86\uFF0C\u6570\u636E\u4E5F\u90FD\u5B58\u50A8\u5728\u4E00\u4E2A\u5E93\u91CC\u3002</p><p><img src="https://static001.geekbang.org/resource/image/58/a6/5803451931917e0806c37c39802410a6.jpg?wh=1142*920" alt=""></p><h4 id="_1-\u5B58\u50A8\u5C42\u7684\u6269\u5C55\u6027" tabindex="-1">1. \u5B58\u50A8\u5C42\u7684\u6269\u5C55\u6027 <a class="header-anchor" href="#_1-\u5B58\u50A8\u5C42\u7684\u6269\u5C55\u6027" aria-hidden="true">#</a></h4><p>\u65E0\u8BBA\u662F\u5B58\u50A8\u7684\u6570\u636E\u91CF\uFF0C\u8FD8\u662F\u5E76\u53D1\u8BBF\u95EE\u91CF\uFF0C\u4E0D\u540C\u7684\u4E1A\u52A1\u6A21\u5757\u4E4B\u95F4\u7684\u91CF\u7EA7\u76F8\u5DEE\u5F88\u5927\uFF0C\u6BD4\u5982\u8BF4\u6210\u719F\u793E\u533A\u4E2D\uFF0C\u5173\u7CFB\u7684\u6570\u636E\u91CF\u662F\u8FDC\u8FDC\u5927\u4E8E\u7528\u6237\u6570\u636E\u91CF\u7684\uFF0C\u4F46\u662F\u7528\u6237\u6570\u636E\u7684\u8BBF\u95EE\u91CF\u5374\u8FDC\u6BD4\u5173\u7CFB\u6570\u636E\u8981\u5927\u3002\u6240\u4EE5\u5047\u5982\u5B58\u50A8\u76EE\u524D\u7684\u74F6\u9888\u70B9\u662F\u5BB9\u91CF\uFF0C\u90A3\u4E48\u6211\u4EEC\u53EA\u9700\u8981\u9488\u5BF9\u5173\u7CFB\u6A21\u5757\u7684\u6570\u636E\u505A\u62C6\u5206\u5C31\u597D\u4E86\uFF0C\u800C\u4E0D\u9700\u8981\u62C6\u5206\u7528\u6237\u6A21\u5757\u7684\u6570\u636E\u3002<strong>\u6240\u4EE5\u5B58\u50A8\u62C6\u5206\u9996\u5148\u8003\u8651\u7684\u7EF4\u5EA6\u662F\u4E1A\u52A1\u7EF4\u5EA6\u3002</strong></p><p>\u62C6\u5206\u4E4B\u540E\uFF0C\u8FD9\u4E2A\u7B80\u5355\u7684\u793E\u533A\u7CFB\u7EDF\u5C31\u6709\u4E86\u7528\u6237\u5E93\u3001\u5185\u5BB9\u5E93\u3001\u8BC4\u8BBA\u5E93\u3001\u70B9\u8D5E\u5E93\u548C\u5173\u7CFB\u5E93\u3002\u8FD9\u4E48\u505A\u8FD8\u80FD\u9694\u79BB\u6545\u969C\uFF0C\u67D0\u4E00\u4E2A\u5E93\u201C\u6302\u4E86\u201D\u4E0D\u4F1A\u5F71\u54CD\u5230\u5176\u5B83\u7684\u6570\u636E\u5E93\u3002</p><p><img src="https://static001.geekbang.org/resource/image/5e/b6/5ee6e1350e2d4d5514a05032b10bd3b6.jpg?wh=1142*854" alt=""></p><p>\u6309\u7167\u4E1A\u52A1\u62C6\u5206\uFF0C\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u63D0\u5347\u4E86\u7CFB\u7EDF\u7684\u6269\u5C55\u6027\uFF0C\u4F46\u7CFB\u7EDF\u8FD0\u884C\u65F6\u95F4\u957F\u4E86\u4E4B\u540E\uFF0C\u5355\u4E00\u7684\u4E1A\u52A1\u6570\u636E\u5E93\u5728\u5BB9\u91CF\u548C\u5E76\u53D1\u8BF7\u6C42\u91CF\u4E0A\u4ECD\u7136\u4F1A\u8D85\u8FC7\u5355\u673A\u7684\u9650\u5236\u3002<strong>\u8FD9\u65F6\uFF0C\u6211\u4EEC\u5C31\u9700\u8981\u9488\u5BF9\u6570\u636E\u5E93\u505A\u7B2C\u4E8C\u6B21\u62C6\u5206\u3002</strong></p><p>\u8FD9\u6B21\u62C6\u5206\u662F\u6309\u7167\u6570\u636E\u7279\u5F81\u505A\u6C34\u5E73\u7684\u62C6\u5206\uFF0C\u6BD4\u5982\u8BF4\u6211\u4EEC\u53EF\u4EE5\u7ED9\u7528\u6237\u5E93\u589E\u52A0\u4E24\u4E2A\u8282\u70B9\uFF0C\u7136\u540E\u6309\u7167\u67D0\u4E9B\u7B97\u6CD5\u5C06\u7528\u6237\u7684\u6570\u636E\u62C6\u5206\u5230\u8FD9\u4E09\u4E2A\u5E93\u91CC\u9762\uFF0C\u5177\u4F53\u7684\u7B97\u6CD5\u6211\u4F1A\u5728\u540E\u9762\u8BB2\u8FF0\u6570\u636E\u5E93\u5206\u5E93\u5206\u8868\u65F6\u548C\u4F60\u7EC6\u8BF4\u3002</p><p>\u6C34\u5E73\u62C6\u5206\u4E4B\u540E\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5\u8BA9\u6570\u636E\u5E93\u7A81\u7834\u5355\u673A\u7684\u9650\u5236\u4E86\u3002\u4F46\u8FD9\u91CC\u8981\u6CE8\u610F\uFF0C\u6211\u4EEC\u4E0D\u80FD\u968F\u610F\u5730\u589E\u52A0\u8282\u70B9\uFF0C\u56E0\u4E3A\u4E00\u65E6\u589E\u52A0\u8282\u70B9\u5C31\u9700\u8981\u624B\u52A8\u5730\u8FC1\u79FB\u6570\u636E\uFF0C\u6210\u672C\u8FD8\u662F\u5F88\u9AD8\u7684\u3002\u6240\u4EE5\u57FA\u4E8E\u957F\u8FDC\u7684\u8003\u8651\uFF0C\u6211\u4EEC\u6700\u597D\u4E00\u6B21\u6027\u589E\u52A0\u8DB3\u591F\u7684\u8282\u70B9\u4EE5\u907F\u514D\u9891\u7E41\u7684\u6269\u5BB9\u3002</p><p>\u5F53\u6570\u636E\u5E93\u6309\u7167\u4E1A\u52A1\u548C\u6570\u636E\u7EF4\u5EA6\u62C6\u5206\u4E4B\u540E\uFF0C\u6211\u4EEC\u5C3D\u91CF\u4E0D\u8981\u4F7F\u7528\u4E8B\u52A1\u3002\u56E0\u4E3A\u5F53\u4E00\u4E2A\u4E8B\u52A1\u4E2D\u540C\u65F6\u66F4\u65B0\u4E0D\u540C\u7684\u6570\u636E\u5E93\u65F6\uFF0C\u9700\u8981\u4F7F\u7528\u4E8C\u9636\u6BB5\u63D0\u4EA4\uFF0C\u6765\u534F\u8C03\u6240\u6709\u6570\u636E\u5E93\u8981\u4E48\u5168\u90E8\u66F4\u65B0\u6210\u529F\uFF0C\u8981\u4E48\u5168\u90E8\u66F4\u65B0\u5931\u8D25\u3002\u8FD9\u4E2A\u534F\u8C03\u7684\u6210\u672C\u4F1A\u968F\u7740\u8D44\u6E90\u7684\u6269\u5C55\u4E0D\u65AD\u5347\u9AD8\uFF0C\u6700\u7EC8\u8FBE\u5230\u65E0\u6CD5\u627F\u53D7\u7684\u7A0B\u5EA6\u3002</p><p>\u8BF4\u5B8C\u4E86\u5B58\u50A8\u5C42\u7684\u6269\u5C55\u6027\uFF0C\u6211\u4EEC\u6765\u770B\u770B\u4E1A\u52A1\u5C42\u662F\u5982\u4F55\u505A\u5230\u6613\u4E8E\u6269\u5C55\u7684\u3002</p><h4 id="_2-\u4E1A\u52A1\u5C42\u7684\u6269\u5C55\u6027" tabindex="-1">2. \u4E1A\u52A1\u5C42\u7684\u6269\u5C55\u6027 <a class="header-anchor" href="#_2-\u4E1A\u52A1\u5C42\u7684\u6269\u5C55\u6027" aria-hidden="true">#</a></h4><p>\u6211\u4EEC\u4E00\u822C\u4F1A\u4ECE\u4E09\u4E2A\u7EF4\u5EA6\u8003\u8651\u4E1A\u52A1\u5C42\u7684\u62C6\u5206\u65B9\u6848\uFF0C\u5B83\u4EEC\u5206\u522B\u662F\uFF1A\u4E1A\u52A1\u7EF4\u5EA6\uFF0C\u91CD\u8981\u6027\u7EF4\u5EA6\u548C\u8BF7\u6C42\u6765\u6E90\u7EF4\u5EA6\u3002</p><p>\u9996\u5148\uFF0C\u6211\u4EEC\u9700\u8981\u628A\u76F8\u540C\u4E1A\u52A1\u7684\u670D\u52A1\u62C6\u5206\u6210\u5355\u72EC\u7684\u4E1A\u52A1\u6C60\uFF0C\u6BD4\u65B9\u8BF4\u4E0A\u9762\u7684\u793E\u533A\u7CFB\u7EDF\u4E2D\uFF0C\u6211\u4EEC\u53EF\u4EE5\u6309\u7167\u4E1A\u52A1\u7684\u7EF4\u5EA6\u62C6\u5206\u6210\u7528\u6237\u6C60\u3001\u5185\u5BB9\u6C60\u3001\u5173\u7CFB\u6C60\u3001\u8BC4\u8BBA\u6C60\u3001\u70B9\u8D5E\u6C60\u548C\u641C\u7D22\u6C60\u3002</p><p>\u6BCF\u4E2A\u4E1A\u52A1\u4F9D\u8D56\u72EC\u81EA\u7684\u6570\u636E\u5E93\u8D44\u6E90\uFF0C\u4E0D\u4F1A\u4F9D\u8D56\u5176\u5B83\u4E1A\u52A1\u7684\u6570\u636E\u5E93\u8D44\u6E90\u3002\u8FD9\u6837\u5F53\u67D0\u4E00\u4E2A\u4E1A\u52A1\u7684\u63A5\u53E3\u6210\u4E3A\u74F6\u9888\u65F6\uFF0C\u6211\u4EEC\u53EA\u9700\u8981\u6269\u5C55\u4E1A\u52A1\u7684\u6C60\u5B50\uFF0C\u4EE5\u53CA\u786E\u8BA4\u4E0A\u4E0B\u6E38\u7684\u4F9D\u8D56\u65B9\u5C31\u53EF\u4EE5\u4E86\uFF0C\u8FD9\u6837\u5C31\u5927\u5927\u51CF\u5C11\u4E86\u6269\u5BB9\u7684\u590D\u6742\u5EA6\u3002</p><p><img src="https://static001.geekbang.org/resource/image/a6/b5/a62e9add7797fc8e55c06fa8a21065b5.jpg?wh=1142*664" alt=""></p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u6211\u4EEC\u8FD8\u53EF\u4EE5\u6839\u636E\u4E1A\u52A1\u63A5\u53E3\u7684\u91CD\u8981\u7A0B\u5EA6\uFF0C\u628A\u4E1A\u52A1\u5206\u4E3A\u6838\u5FC3\u6C60\u548C\u975E\u6838\u5FC3\u6C60\u3002\u6253\u4E2A\u6BD4\u65B9\uFF0C\u5C31\u5173\u7CFB\u6C60\u800C\u8A00\uFF0C\u5173\u6CE8\u3001\u53D6\u6D88\u5173\u6CE8\u63A5\u53E3\u76F8\u5BF9\u91CD\u8981\u4E00\u4E9B\uFF0C\u53EF\u4EE5\u653E\u5728\u6838\u5FC3\u6C60\u91CC\u9762\uFF1B\u62C9\u9ED1\u548C\u53D6\u6D88\u62C9\u9ED1\u7684\u64CD\u4F5C\u5C31\u76F8\u5BF9\u4E0D\u90A3\u4E48\u91CD\u8981\uFF0C\u53EF\u4EE5\u653E\u5728\u975E\u6838\u5FC3\u6C60\u91CC\u9762\u3002\u8FD9\u6837\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4F18\u5148\u4FDD\u8BC1\u6838\u5FC3\u6C60\u7684\u6027\u80FD\uFF0C\u5F53\u6574\u4F53\u6D41\u91CF\u4E0A\u5347\u65F6\u4F18\u5148\u6269\u5BB9\u6838\u5FC3\u6C60\uFF0C\u964D\u7EA7\u90E8\u5206\u975E\u6838\u5FC3\u6C60\u7684\u63A5\u53E3\uFF0C\u4ECE\u800C\u4FDD\u8BC1\u6574\u4F53\u7CFB\u7EDF\u7684\u7A33\u5B9A\u6027\u3002</p><p><img src="https://static001.geekbang.org/resource/image/ce/28/ce6e856238d8af7059c44b3a47eced28.jpg?wh=1142*499" alt=""></p><p>\u6700\u540E\uFF0C\u4F60\u8FD8\u53EF\u4EE5\u6839\u636E\u63A5\u5165\u5BA2\u6237\u7AEF\u7C7B\u578B\u7684\u4E0D\u540C\u505A\u4E1A\u52A1\u6C60\u7684\u62C6\u5206\u3002\u6BD4\u5982\u8BF4\uFF0C\u670D\u52A1\u4E8E\u5BA2\u6237\u7AEF\u63A5\u53E3\u7684\u4E1A\u52A1\u53EF\u4EE5\u5B9A\u4E49\u4E3A\u5916\u7F51\u6C60\uFF0C\u670D\u52A1\u4E8E\u5C0F\u7A0B\u5E8F\u6216\u8005 HTML5 \u9875\u9762\u7684\u4E1A\u52A1\u53EF\u4EE5\u5B9A\u4E49\u4E3A H5 \u6C60\uFF0C\u670D\u52A1\u4E8E\u5185\u90E8\u5176\u5B83\u90E8\u95E8\u7684\u4E1A\u52A1\u53EF\u4EE5\u5B9A\u4E49\u4E3A\u5185\u7F51\u6C60\uFF0C\u7B49\u7B49\u3002</p><h3 id="\u8BFE\u7A0B\u5C0F\u7ED3" tabindex="-1">\u8BFE\u7A0B\u5C0F\u7ED3 <a class="header-anchor" href="#\u8BFE\u7A0B\u5C0F\u7ED3" aria-hidden="true">#</a></h3><p>\u672C\u8282\u8BFE\u6211\u5E26\u4F60\u4E86\u89E3\u4E86\u63D0\u5347\u7CFB\u7EDF\u6269\u5C55\u6027\u7684\u590D\u6742\u5EA6\u4EE5\u53CA\u7CFB\u7EDF\u62C6\u5206\u7684\u601D\u8DEF\u3002\u62C6\u5206\u770B\u8D77\u6765\u6BD4\u8F83\u7B80\u5355\uFF0C\u53EF\u662F\u4EC0\u4E48\u65F6\u5019\u505A\u62C6\u5206\uFF0C\u5982\u4F55\u505A\u62C6\u5206\u8FD8\u662F\u6709\u5F88\u591A\u7EC6\u8282\u8003\u8651\u7684\u3002</p><p>\u672A\u505A\u62C6\u5206\u7684\u7CFB\u7EDF\u867D\u7136\u53EF\u6269\u5C55\u6027\u4E0D\u5F3A\uFF0C\u4F46\u662F\u5374\u8DB3\u591F\u7B80\u5355\uFF0C\u65E0\u8BBA\u662F\u7CFB\u7EDF\u5F00\u53D1\u8FD8\u662F\u8FD0\u884C\u7EF4\u62A4\u90FD\u4E0D\u9700\u8981\u6295\u5165\u5F88\u5927\u7684\u7CBE\u529B\u3002\u62C6\u5206\u4E4B\u540E\uFF0C\u9700\u6C42\u5F00\u53D1\u9700\u8981\u6A2A\u8DE8\u591A\u4E2A\u7CFB\u7EDF\u591A\u4E2A\u5C0F\u56E2\u961F\uFF0C\u6392\u67E5\u95EE\u9898\u4E5F\u9700\u8981\u6D89\u53CA\u591A\u4E2A\u7CFB\u7EDF\uFF0C\u8FD0\u884C\u7EF4\u62A4\u4E0A\uFF0C\u53EF\u80FD\u6BCF\u4E2A\u5B50\u7CFB\u7EDF\u90FD\u9700\u8981\u6709\u4E13\u4EBA\u6765\u8D1F\u8D23\uFF0C\u5BF9\u4E8E\u56E2\u961F\u662F\u4E00\u4E2A\u6BD4\u8F83\u5927\u7684\u8003\u9A8C\u3002\u8FD9\u4E2A\u8003\u9A8C\u662F\u6211\u4EEC\u5FC5\u987B\u8981\u7ECF\u5386\u7684\u4E00\u4E2A\u5927\u574E\uFF0C\u9700\u8981\u6211\u4EEC\u505A\u597D\u51C6\u5907\u3002</p><h3 id="\u4E00\u8BFE\u4E00\u601D" tabindex="-1">\u4E00\u8BFE\u4E00\u601D <a class="header-anchor" href="#\u4E00\u8BFE\u4E00\u601D" aria-hidden="true">#</a></h3><p>\u5728\u4ECA\u5929\u7684\u8BFE\u7A0B\u4E2D\uFF0C\u6211\u4EEC\u8C08\u5230\u4E86\u4F20\u7EDF\u5173\u7CFB\u578B\u6570\u636E\u5E93\u7684\u53EF\u6269\u5C55\u6027\u662F\u5F88\u5DEE\u7684\uFF0C\u90A3\u4E48\u5728\u4F60\u770B\u6765\uFF0C\u5E38\u89C1\u7684 NoSQL \u6570\u636E\u5E93\u662F\u5982\u4F55\u89E3\u51B3\u6269\u5C55\u6027\u7684\u95EE\u9898\u5462\uFF1F\u6B22\u8FCE\u5728\u7559\u8A00\u533A\u548C\u6211\u4E00\u8D77\u8BA8\u8BBA\u3002</p>',41),s=[i];function n(d,h,c,l,_,o){return a(),p("div",null,s)}const b=e(r,[["render",n]]);export{u as __pageData,b as default};
