import{_ as e,o as t,c as p,a as _}from"./app.303cfecc.js";const N=JSON.parse('{"title":"\u5F53\u95EE\u5230\u9879\u76EE\u7ECF\u5386\u65F6\uFF0C\u9762\u8BD5\u5B98\u7A76\u7ADF\u60F3\u8981\u4E86\u89E3\u4EC0\u4E48\uFF1F","description":"","frontmatter":{},"headers":[],"relativePath":"0002/04\u6F14\u8FDB-\u6D88\u606F\u961F\u5217/20-\u5F53\u95EE\u5230\u9879\u76EE\u7ECF\u5386\u65F6\uFF0C\u9762\u8BD5\u5B98\u7A76\u7ADF\u60F3\u8981\u4E86\u89E3\u4EC0\u4E48\uFF1F.md","lastUpdated":1680763907000}'),a={name:"0002/04\u6F14\u8FDB-\u6D88\u606F\u961F\u5217/20-\u5F53\u95EE\u5230\u9879\u76EE\u7ECF\u5386\u65F6\uFF0C\u9762\u8BD5\u5B98\u7A76\u7ADF\u60F3\u8981\u4E86\u89E3\u4EC0\u4E48\uFF1F.md"},o=_('<h1 id="\u5F53\u95EE\u5230\u9879\u76EE\u7ECF\u5386\u65F6-\u9762\u8BD5\u5B98\u7A76\u7ADF\u60F3\u8981\u4E86\u89E3\u4EC0\u4E48" tabindex="-1">\u5F53\u95EE\u5230\u9879\u76EE\u7ECF\u5386\u65F6\uFF0C\u9762\u8BD5\u5B98\u7A76\u7ADF\u60F3\u8981\u4E86\u89E3\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#\u5F53\u95EE\u5230\u9879\u76EE\u7ECF\u5386\u65F6-\u9762\u8BD5\u5B98\u7A76\u7ADF\u60F3\u8981\u4E86\u89E3\u4EC0\u4E48" aria-hidden="true">#</a></h1><p>\u4F60\u597D\uFF0C\u6211\u662F\u5510\u626C\uFF0C\u4ECA\u5929\u662F\u6211\u4EEC\u9762\u8BD5\u73B0\u573A\u7684\u7B2C\u4E8C\u671F\uFF0C\u6211\u60F3\u8DDF\u4F60\u63A2\u8BA8\u7684\u8BDD\u9898\u662F: \u5F53\u95EE\u5230\u9879\u76EE\u7ECF\u5386\u65F6\uFF0C\u9762\u8BD5\u5B98\u7A76\u7ADF\u60F3\u8981\u4E86\u89E3\u4EC0\u4E48?</p><p>\u5728\u4E0A\u4E00\u671F\u7684\u9762\u8BD5\u73B0\u573A\u4E2D\uFF0C\u6211\u66FE\u7ECF\u63D0\u5230\uFF0C\u4ECB\u7ECD\u8FC7\u5F80\u7684\u9879\u76EE\u7ECF\u5386\u662F\u9762\u8BD5\u8FC7\u7A0B\u4E2D\u4E00\u4E2A\u5F88\u91CD\u8981\u7684\u7EC4\u6210\u90E8\u5206\uFF0C\u76F8\u4FE1\u4F60\u5728\u4EE5\u5F80\u7684\u9762\u8BD5\u4E2D\u4E5F\u4E00\u5B9A\u6DF1\u6709\u4F53\u4F1A\u3002\u4ECE\u9879\u76EE\u7ECF\u5386\u4E2D\uFF0C\u9762\u8BD5\u5B98\u4E0D\u4EC5\u53EF\u4EE5\u4E86\u89E3\u5230\u9879\u76EE\u7814\u53D1\u7684\u6574\u4F53\u8FC7\u7A0B\uFF0C\u4E5F\u80FD\u591F\u4ECE\u4E2D\u4E86\u89E3\u5230\u4F60\u7684\u6280\u672F\u65B9\u6848\u8BBE\u8BA1\u601D\u8DEF\uFF0C\u6392\u67E5\u7EBF\u4E0A\u95EE\u9898\u7684\u80FD\u529B\uFF0C\u4EE5\u53CA\u5BF9\u4E8E\u9879\u76EE\u67B6\u6784\u6F14\u8FDB\u65B9\u5411\u7684\u628A\u63E1\u90A3\u4E48\uFF0C\u5728\u4ECB\u7ECD\u81EA\u5DF1\u7684\u9879\u76EE\u7ECF\u5386\u65F6\uFF0C\u4F60\u8981\u5982\u4F55\u4ECB\u7ECD\u624D\u80FD\u6293\u4F4F\u9762\u8BD5\u5B98\u5185\u5FC3\u7684\u75DB\u70B9\u5462? \u5728\u56DE\u7B54\u8FD9\u4E2A\u95EE\u9898\u4E4B\u524D\uFF0C\u6211\u8FD8\u662F\u7ED9\u4F60\u5206\u4EAB\u4E00\u4E2A\u6211\u9762\u8BD5\u5019\u9009\u4EBA\u7684\u4F8B\u5B50</p><p>\u5019\u9009\u4EBA\u867D\u7136\u5DE5\u4F5C\u5E74\u9650\u4E0D\u957F\uFF0C\u7ECF\u5386\u7684\u9879\u76EE\u4E5F\u53EA\u6709\u4E00\u6BB5\uFF0C\u4F46\u662F\u8FD9\u6BB5\u7ECF\u5386\u662F\u67D0\u4E2A\u5927\u5382\u8033\u719F\u80FD\u8BE6\u7684\u6210\u529F\u9879\u76EE,\u6709\u7740\u5F88\u9AD8\u7684\u5E76\u53D1\u8BF7\u6C42\u91CF\u3002\u6211\u731C\u6D4B\u4ED6\u5E94\u8BE5\u5728\u9879\u76EE\u4E2D\u53D7\u5230\u4E86\u6BD4\u8F83\u597D\u7684\u953B\u70BC\uFF0C\u53EF\u662F\u5F53\u6211\u9080\u7EA6\u4E86\u9762\u8BD5\u4E4B\u540E\uFF0C\u51FA\u73B0\u4E86\u95EE\u9898\u3002</p><p>\u76F8\u4FE1\u4F60\u80FD\u731C\u5230\uFF0C\u8FD9\u4E2A\u5019\u9009\u4EBA\u6700\u7EC8\u6CA1\u6709\u901A\u8FC7\u9762\u8BD5</p><p>\u672C\u6765\uFF0C\u9AD8\u5E76\u53D1\u7684\u793E\u533A\u7CFB\u7EDF\u662F\u4ED6\u7684\u9879\u76EE\u4EAE\u70B9\u53EF\u662F\u5728\u4ECB\u7ECD\u65F6\uFF0C\u4ED6\u5374\u4E0D\u4E86\u89E3\u9879\u76EE\u7684\u6838\u5FC3\u6570\u636E\u6307\u6807\uFF0C\u4E5F\u5BF9\u7CFB\u7EDF\u7684\u8C03\u4F18\u548C\u7591\u96BE\u95EE\u9898\u7684\u6392\u67E5\u77E5\u4E4B\u751A\u5C11\uFF0C\u81EA\u7136\u4E0D\u4F1A\u901A\u8FC7\u9762\u8BD5\u3002\u90A3\u4E48\u6211\u4ECE\u9762\u8BD5\u5019\u9009\u4EBA\u7684\u7ECF\u9A8C\u4E2D\u7ED9\u4F60\u603B\u7ED3\u51FA\u4E00\u4E9B\uFF0C\u4ECB\u7ECD\u9879\u76EE\u7ECF\u5386\u7684\u5173\u952E\u6CE8\u610F\u70B9\uFF0C\u671F\u671B\u4F60\u80FD\u5728\u4ECA\u540E\u7684\u9762\u8BD5\u4E2D\u53EF\u4EE5\u501F\u9274\u3002</p><p>\u5176\u5B9E\uFF0C\u5728\u4ECB\u7ECD\u9879\u76EE\u7ECF\u5386\u65F6\u8BB2\u4E00\u4E9B\u9879\u76EE\u80CC\u666F\u662F\u53EF\u4EE5\u7684\uFF0C\u4E5F\u662F\u5FC5\u8981\u7684\uFF0C\u8FD9\u6837\u53EF\u4EE5\u8BA9\u9762\u8BD5\u5B98\u5BF9\u9879\u76EE\u6709\u4E00\u4E2A\u5168\u666F\u7684\u8BA4\u8BC6\u3002\u4F46\u8FD9\u4E9B\u80CC\u666F\u77E5\u8BC6\u4E0D\u5B9C\u8FC7\u591A\uFF0C\u56E0\u4E3A\u6BD5\u7ADF\u662F\u6280\u672F\u9762\u8BD5\uFF0C\u9762\u8BD5\u5B98\u9700\u8981\u5728 1\u4E2A\u5C0F\u65F6\u5DE6\u53F3\u5BF9\u4F60\u7684\u6280\u672F\u80FD\u529B\u6709\u6B63\u786E\u7684\u5224\u65AD\uFF0C\u6240\u4EE5\u4F60\u4ECB\u7ECD\u9879\u76EE\u7ECF\u5386\u65F6\uFF0C\u6700\u597D\u4ECE\u6280\u672F\u89D2\u5EA6\u51FA\u53D1\u3002\u6BD4\u5982\uFF0C\u5982\u679C\u4F60\u505A\u4E86\u4E00\u4E2A\u793E\u533A\u7684\u9879\u76EE\u90A3\u4E48\u9879\u76EE\u7684\u6574\u4F53\u67B6\u6784\u662F\u4E00\u4F53\u5316\u7684\u8FD8\u662F\u670D\u52A1\u5316\u7684? \u62C6\u5206\u6210\u4E86\u54EA\u4E9B\u670D\u52A1? \u670D\u52A1\u4E4B\u95F4\u662F\u901A\u8FC7\u4EC0\u4E48\u534F\u8BAE\uFF0C\u53C8\u662F\u600E\u6837\u4EA4\u4E92\u7684\uFF0C\u4F7F\u7528\u4E86\u54EA\u4E9B\u5F00\u6E90\u7684\u7EC4\u4EF6\uFF0C\u505A\u7EC4\u4EF6\u9009\u578B\u65F6\u7ECF\u8FC7\u4E86\u54EA\u4E9B\u8003\u8651\u7B49\u7B49\u8FD9\u6837\u65B9\u4FBF\u9762\u8BD5\u5B98\u5728\u8FD9\u4E9B\u6280\u672F\u70B9\u4E0A\u505A\u4E00\u4E9B\u6DF1\u5165\u7684\u5C55\u5F00\u3002</p><p>\u8FD9\u91CC\u6709\u4E00\u4E2A\u5C0F\u7A8D\u95E8\uFF0C\u4F60\u53EF\u4EE5\u6709\u610F\u5730\u5F15\u5BFC\u9762\u8BD5\u5B98\uFF0C\u95EE\u4E00\u4E9B\u4F60\u60F3\u8981\u4ED6\u95EE\u7684\u95EE\u9898\uFF0C\u6BD4\u65B9\u8BF4\uFF0C\u5982\u679C\u4F60\u5BF9 Kafka\u3001RocketMQ \u7B49\u6D88\u606F\u961F\u5217\u6709\u6BD4\u8F83\u6DF1\u5165\u7684\u4E86\u89E3\uFF0C\u4F60\u53EF\u4EE5\u591A\u8BB2\u8BB2\u8FD9\u65B9\u9762\u7684\u5185\u5BB9\u5177\u4F53\u6765\u8BF4\uFF0C\u4F7F\u7528\u54EA\u4E9B\u6D88\u606F\u961F\u5217\u89E3\u51B3\u4E86\u54EA\u65B9\u9762\u7684\u95EE\u9898\uFF0C\u5728\u5176\u4E2D\u9047\u5230\u4E86\u54EA\u4E9B\u5751\uFF0C\u53C8\u662F\u600E\u4E48\u4F18\u5316\u7684\u3002\u8FD9\u6837\u4E00\u6765\uFF0C\u4F60\u5C31\u53EF\u4EE5\u628A\u9762\u8BD5\u5B98\u5F15\u5165\u4E00\u4E2A\u4F60\u6BD4\u8F83\u719F\u6089\u7684\u9886\u57DF\uFF0C\u9762\u8BD5\u6210\u529F\u7684\u51E0\u7387\u5C31\u5927\u5927\u589E\u52A0\u4E86\u3002</p><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u4F60\u5728\u9879\u76EE\u4ECB\u7ECD\u4E2D\u8981\u591A\u7A81\u51FA\u4E2A\u4EBA\u5BF9\u9879\u76EE\u7684\u8D21\u732E\uFF0C\u800C\u4E0D\u4EC5\u4EC5\u662F\u9879\u76EE\u672C\u8EAB\u7684\u5E76\u53D1\u6709\u591A\u9AD8\uFF0C\u6280\u672F\u65B9\u6848\u8BBE\u8BA1\u7684\u6709\u591A\u5389\u5BB3\u3002\u7684\u786E\uFF0C\u6210\u529F\u7684\u9879\u76EE\u786E\u5B9E\u53EF\u4EE5\u7ED9\u4F60\u5E26\u6765\u6BD4\u8F83\u5927\u7684\u7EA2\u5229\uFF0C\u4F46\u4F60\u6BD5\u7ADF\u53EA\u662F\u8FD9\u4E2A\u9879\u76EE\u4E2D\u7684\u4E00\u9897\u87BA\u4E1D\u9489\uFF0C\u505A\u51FA\u7684\u8D21\u732E\u4E5F\u662F\u6709\u9650\u7684\u3002\u5728\u9762\u8BD5\u4E2D\uFF0C\u9762\u8BD5\u5B98\u66F4\u5173\u6CE8\u4F60\u4E2A\u4EBA\u5BF9\u6574\u4E2A\u9879\u76EE\u505A\u4E86\u4EC0\u4E48\u6837\u7684\u6539\u53D8\u4E3A\u6B64\uFF0C\u6211\u5EFA\u8BAE\u4F60\u4ECE\u4E0B\u9762\u4E09\u4E2A\u70B9\u51FA\u53D1\u56DE\u7B54:</p><ul><li>\u9488\u5BF9\u590D\u6742\u7684\u9700\u6C42\u4F60\u8BBE\u8BA1\u4E86\u54EA\u4E9B\u65B9\u6848\uFF0C\u8FD9\u4E9B\u65B9\u6848\u4E2D\u6280\u672F\u7684\u96BE\u70B9\u662F\u4EC0\u4E48\uFF0C\u4F60\u5728\u65B9\u6848\u8BBE\u8BA1\u4E2D\u662F\u5982\u4F55\u89E3\u51B3\u7684?</li><li>\u5728\u9879\u76EE\u4E2D\u9047\u5230\u4E86\u54EA\u4E9B\u7591\u96BE\u8BE1\u5F02\u7684\u95EE\u9898\uFF0C\u4F60\u6392\u67E5\u95EE\u9898\u7684\u601D\u8DEF\u662F\u600E\u6837\u7684?</li><li>\u5728\u9879\u76EE\u8FD0\u7EF4\u8FC7\u7A0B\u4E2D\u51FA\u73B0\u4E86\u54EA\u4E9B\u6027\u80FD\u65B9\u9762\u7684\u95EE\u9898\uFF0C\u4F60\u53C8\u662F\u600E\u4E48\u6765\u4F18\u5316\u7684?</li></ul><p>\u5F53\u7136\u4E86\uFF0C\u5982\u679C\u4F60\u60F3\u7528\u8FD9\u4E09\u70B9\u8BA9\u9762\u8BD5\u5B98\u773C\u524D-\u4EAE\uFF0C\u90A3\u4E48\u5C31\u9700\u8981\u5728\u5E73\u65F6\u7684\u5DE5\u4F5C\u4E2D\u591A\u79EF\u7D2F\u6848\u4F8B\u3002\u800C\u4E0D\u662F\u53EA\u7ED9\u51FA\u201C \u9047\u5230\u95EE\u9898\u5C31\u52A0\u65E5\u5FD7\u6392\u67E5\u4EE3\u7801\u201D \u201C\u6027\u80FD\u51FA\u73B0\u95EE\u9898\u5C31\u52A0\u7D22\u5F15\u3001\u52A0\u7F13\u5B58\u201D\u8FD9\u6837\u7B80\u5355\u7684\u7B54\u6848\u4E86\u3002</p><p>\u9488\u5BF9\u6211\u63D0\u5230\u7684\u8FD9\u4E2A\u95EE\u9898\uFF0C\u6211\u4E5F\u60F3\u7ED9\u4F60\u5206\u4EAB\u4E00\u4E2A\u4E4B\u524D\u9762\u8BD5\u7684\u6848\u4F8B\u3002\u8FD9\u4E2A\u6848\u4F8B\u6D89\u53CA\u6392\u67E5\u4E00\u6B21\u81EA\u7814\u7684 RPC \u6846\u67B6\u7684\u6545\u969C\uFF0C\u56DE\u7B54\u7684\u601D\u8DEF\u5982\u4E0B: \u9879\u76EE\u4E2D\u4F7F\u7528\u7684 RPC \u6846\u67B6\u5B58\u5728\u67D0\u4E2A Bug\uFF0C\u5076\u53D1\u5730\u51FA\u73B0 RPC \u5BA2\u6237\u7AEF\u53D1\u9001\u7ED9 RPC \u670D\u52A1\u8282\u70B9\u7684\u6240\u6709\u5FC3\u8DF3\u5305\u5168\u90E8\u8D85\u65F6\uFF0C\u4ECE\u800C\u8BA9 RPC \u5BA2\u6237\u7AEF\u8BA4\u4E3A\u670D\u52A1\u8282\u70B9\u5DF2\u7ECF\u53D1\u751F\u6545\u969C\uFF0C\u7136\u540E RPC \u5BA2\u6237\u7AEF\u5C31\u4E0D\u4F1A\u5C06\u6D41\u91CF\u53D1\u9001\u7ED9\u8FD9\u4E2A\u670D\u52A1\u8282\u70B9\u3002\u7531\u4E8E\u9879\u76EE\u4E2D\u4F7F\u7528\u4E86 Netty3 \u6846\u67B6\uFF0C\u6211\u5C31\u7406\u4E86\u4E00\u904DNetty3 \u7684\u8FDE\u63A5\u8FC7\u7A0B\u7684\u4EE3\u7801\uFF0C\u786E\u8BA4\u662F Boss \u7EBF\u7A0B\u6216\u8005 NioWorker \u7EBF\u7A0B\u67D0\u4E2A\u5730\u65B9\u963B\u585E\u4E86\u3002 \u7136\u540E\uFF0C\u6211\u901A\u8FC7 strace (\u7CFB\u7EDF\u8C03\u7528\u6293\u53D6\u5DE5\u5177)\u6293\u53D6\u90A3\u6BB5\u65F6\u95F4\u7684\u7CFB\u7EDF\u8C03\u7528\uFF0C\u53D1\u73B0 Boss \u7EBF\u7A0B\u4E2D\u6D89\u53CA\u7684\u7CFB\u7EDF\u8C03\u7528\u5168\u90E8\u5B8C\u6210\u4E86\uFF0C\u5374\u6CA1\u6709\u53D1\u73B0\u628Asocket fd (\u6587\u4EF6\u53E5\u67C4) \u6CE8\u518C\u7ED9 NioWorker \u7684selectorfd (\u6587\u4EF6\u53E5\u67C4) \u7684\u7CFB\u7EDF\u8C03\u7528\uFF0C\u4E8E\u662F\u786E\u8BA4\u662F NioWorker \u7684\u95EE\u9898\u3002</p><p>\u540E\u6765\u6211\u6253\u5370\u4E86 jvm thread stack trace (Java\u7684\u7EBF\u7A0B\u5806\u6808)\uFF0C\u53D1\u73B0\u6240\u6709\u7684 NioWorker \u7EBF\u7A0B\u963B\u585E\u5728 Object.wait\uFF0C\u4EE3\u7801\u4F4D\u7F6E\u662F Class.forName\uFF0C\u8FD9\u6837\u5C31\u5BFC\u81F4\u6240\u6709\u7684 NioWorker \u4E0D\u518D\u5904\u7406\u65B0\u7684 Register task (\u6CE8\u518C\u4EFB\u52A1)\uFF0CNettyClientBootstrap \u4E2D\u6240\u6709\u7684 ConnectFuture \u7684\u72B6\u6001\u81EA\u7136\u4E0D\u88AB\u6807\u8BB0\uFF0C\u6700\u7EC8\u5BFC\u81F4\u4E0A\u5C42\u6240\u6709\u7684\u8FDE\u63A5\u8D85\u65F6\u3002</p><p>\u518D\u5F80\u4E0B\u8FFD\u67E5\u540E\uFF0C\u6211\u53D1\u73B0 JDK 6 \u4E2D\u786E\u5B9E\u5B58\u5728\u7C7B\u521D\u59CB\u5316\u8FC7\u7A0B\u4E2D\u51FA\u73B0\u6B7B\u9501\u7684 Bug\uFF0C\u5728\u5347\u7EA7\u5230JDK 7\u4E4B\u540E\u95EE\u9898\u5C31\u5F97\u5230\u89E3\u51B3 \u4F60\u770B\uFF0C\u672C\u6765\u662F\u4E00\u4E2A\u5076\u53D1\u7684 RPC \u6846\u67B6\u5C42\u7684\u95EE\u9898\u800C\u4F60\u5728\u8FFD\u67E5\u8FD9\u4E2A Bug \u7684\u8FC7\u7A0B\u4E2D\u6D89\u53CA\u5230\u4E86Netty \u7F51\u7EDC\u7F16\u7A0B\u3001\u7CFB\u7EDF\u8C03\u7528\u8FFD\u8E2A\uFF0C\u548C\u591A\u7EBF\u7A0B\u9501\u8BCA\u65AD\u7B49\u77E5\u8BC6\uFF0C\u9762\u8BD5\u5B98\u5728\u542C\u5230\u8FD9\u6837\u7684\u6848\u4F8B\u4E4B\u540E\u4E5F\u4F1A\u8BA4\u53EF\u4F60\u6392\u67E5\u95EE\u9898\u7684\u601D\u8DEF\u548C\u80FD\u529B\u3002</p><p>\u6211\u8FD8\u60F3\u7ED9\u4F60\u7684\u5EFA\u8BAE\u662F\uFF0C\u5728\u4ECB\u7ECD\u9879\u76EE\u65F6\uFF0C\u4F60\u8981\u591A\u7A81\u51FA\u9879\u76EE\u4E2D\u7684\u4EAE\u70B9\uFF0C\u6BD4\u5982\uFF0C\u4F60\u7684\u9879\u76EE\u4E2D\u6838\u5FC3\u7CFB\u7EDF\u7684\u8BF7\u6C42\u91CF\u662F 10 \u4E07\u6B21 /\u79D2\uFF0C\u6570\u636E\u91CF\u662F\u4EBF\u7EA7\u522B\uFF0C\u5728\u9879\u76EE\u7814\u53D1\u8FC7\u7A0B\u4E2D\u4F60\u4F7F\u7528\u4E86 Linuxprofile \u5DE5\u5177\u6392\u67E5\u7CFB\u7EDF\u8D1F\u8F7D\u9AD8\u7684\u95EE\u9898\uFF0C\u6216\u8005\u4F7F\u7528\u5E03\u9686\u8FC7\u6EE4\u5668\u89E3\u51B3\u7F13\u5B58\u7A7F\u900F\u7684\u95EE\u9898\u7684\u3002\u90A3\u4E48\u4F60\u5728\u56DE\u7B54\u65F6\uFF0C\u8BF7\u6C42\u91CF\u548C\u6570\u636E\u91CF\u8FD9\u4E9B\u6570\u636E\u4F1A\u662F\u4E2A\u4EAE\u70B9\uFF0C\u8FD9\u4E9B\u6392\u67E5\u95EE\u9898\u548C\u89E3\u51B3\u95EE\u9898\u7684\u65B9\u6CD5\u4E5F\u5C06\u662F\u4EAE\u70B9\u3002\u4E0D\u8FC7\uFF0C\u8FD9\u4E00\u70B9\u662F\u9488\u5BF9\u5177\u6709\u9AD8\u8BF7\u6C42\u91CF\u7684\u9879\u76EE\u6765\u8BF4\u7684\uFF0C\u90A3\u4E48\uFF0C\u5982\u679C\u4F60\u7684\u9879\u76EE\u4E2D\u6CA1\u6709\u90A3\u4E48\u9AD8\u7684\u8BF7\u6C42\u91CF\uFF0C\u4F60\u8981\u600E\u4E48\u56DE\u7B54\u5462?</p><p>\u6211\u7684\u5EFA\u8BAE\u662F\uFF0C\u4F60\u53EF\u4EE5\u7ED3\u5408\u81EA\u5DF1\u4E86\u89E3\u5230\u7684\u9AD8\u5E76\u53D1\u7684\u77E5\u8BC6\u804A\u4E00\u804A\uFF0C\u5982\u679C\u9879\u76EE\u5230\u8FBE\u4E86\u67D0\u4E00\u4E2A\u91CF\u7EA7\u53EF\u4EE5\u5728\u67B6\u6784\u4E0A\u505A\u54EA\u4E9B\u7684\u6539\u9020\u548C\u4F18\u5316\u3002\u6BD4\u65B9\u8BF4\uFF0C\u4F60\u505A\u4E00\u4E2A\u76F4\u64AD\u7684\u9879\u76EE\uFF0C\u5E73\u65F6\u7684\u6D41\u91CF\u4E5F\u4E0D\u9AD8\uFF0C\u7A81\u7136\u8BF7\u5230\u4E86\u6768\u5E42\u8FD9\u6837\u7684\u6D41\u91CF\u5927\u5496\u6765\u505A\u76F4\u64AD......\u90A3\u4E48\u4F60\u5C31\u53EF\u4EE5\u629B\u51FA\u8FD9\u4E2A\u5047\u8BBE\uFF0C\u7136\u540E\u548C\u9762\u8BD5\u5B98\u804A\u804A\u4EE5\u4E0B\u51E0\u70B9:</p><ul><li>\u5982\u4F55\u4F7F\u7528\u591A\u7EA7\u7F13\u5B58\u5E94\u5BF9\u6768\u5E42\u76F4\u64AD\u95F4\u7684\u6781\u7AEF\u70ED\u70B9\u8BF7\u6C42;</li><li>\u51E0\u4E07\u751A\u81F3\u51E0\u5341\u4E07\u4EBA\u540C\u65F6\u5728\u76F4\u64AD\u95F4\u5185\u53D1\u8A00\u65F6\u8981\u5982\u4F55\u4F18\u5316\u6D88\u606F\u7684\u5EF6\u8FDF;</li><li>\u5982\u4F55\u901A\u8FC7\u538B\u6D4B\u8BC4\u4F30\u76EE\u524D\u7CFB\u7EDF\u627F\u8F7D\u6D41\u91CF\u7684\u80FD\u529B\uFF0C\u4EE5\u4FBF\u660E\u786E\u5982\u4F55\u5236\u5B9A\u6269\u5BB9\u7684\u8BA1\u5212\u3002</li></ul><p>\u8FD9\u6837\uFF0C\u9762\u8BD5\u5B98\u4E0D\u4EC5\u53EF\u4EE5\u4E86\u89E3\u4F60\u5728\u5E94\u5BF9\u9AD8\u5E76\u53D1\u5927\u6D41\u91CF\u7684\u601D\u8DEF\uFF0C\u4E5F\u4F1A\u8BA4\u4E3A\u4F60\u5BF9\u7CFB\u7EDF\u67B6\u6784\u7684\u672B\u6765\u53D1\u5C55\u662F\u6709\u89C4\u5212\uFF0C\u6709\u60F3\u6CD5\u7684\u3002</p><p>\u603B\u7684\u6765\u8BF4\uFF0C\u9762\u8BD5\u662F\u53CC\u65B9\u7684\u201C \u535A\u5F08\u201D\uFF0C\u4F60\u4E0D\u80FD\u88AB\u52A8\u5730\u63A5\u53D7\u9762\u8BD5\u5B98\u7684\u8BE2\u95EE\uFF0C\u800C\u662F\u8981\u5FAA\u5FAA\u5584\u8BF1\u626C\u957F\u907F\u77ED\uFF0C\u5728\u4ECB\u7ECD\u9879\u76EE\u7ECF\u5386\u65F6\uFF0C\u591A\u4E89\u53D6\u4E3B\u52A8\u628A\u9762\u8BD5\u5B98\u5F15\u5230\u81EA\u5DF1\u64C5\u957F\u7684\u9886\u57DF;\u5728\u5E73\u65F6\u7684\u5DE5\u4F5C\u4E2D\u8981\u591A\u5173\u6CE8\u7CFB\u7EDF\u7684\u6027\u80FD\u6307\u6807\uFF0C\u591A\u53BB\u53C2\u4E0E\u540C\u4E8B\u6392\u67E5\u6027\u80FD\u95EE\u9898\uFF0C\u89E3\u51B3\u8BE1\u5F02 Bug \u7684\u8FC7\u7A0B\uFF0C\u4E0E\u4ED6\u4EEC\u8BA8\u8BBA\u7CFB\u7EDF\u4F18\u5316\u7684\u601D\u8DEF\u3002\u8FD9\u4E9B\u7ECF\u5386\u4F1A\u6210\u4E3A\u4F60\u9762\u8BD5\u7684\u7D20\u6750\uFF0C\u8BA9\u9762\u8BD5\u5B98\u5BF9\u4F60\u773C\u524D\u4E00\u4EAE\uFF0C\u5F53\u7136\u4F60\u4E5F\u9700\u8981\u591A\u5173\u6CE8\u4E1A\u754C\u7684\u6280\u672F\u53D1\u5C55\uFF0C\u591A\u601D\u8003\u65B0\u7684\u6280\u672F\u5728\u4F60\u7684\u9879\u76EE\u4E2D\u6709\u54EA\u4E9B\u5E94\u7528\u7684\u573A\u666F\uFF0C\u8FD9\u6837\u4E5F\u53EF\u4EE5\u8BA9\u9762\u8BD5\u5B98\u89C9\u5F97\u4F60\u7684\u6280\u672F\u89C6\u91CE\u6BD4\u8F83\u5382\u9614\uFF0C\u5177\u5907\u72EC\u7ACB\u601D\u8003\u7684\u80FD\u529B\u3002</p><p>\u597D\u4E86\uFF0C\u9762\u8BD5\u73B0\u573A\u7B2C\u4E8C\u671F\u5230\u8FD9\u91CC\u5C31\u7ED3\u675F\u4E86\uFF0C\u65E2\u7136\u6211\u63D0\u5230\u4E86\u9879\u76EE\u7ECF\u5386\uFF0C\u90A3\u4E48\uFF0C\u4F60\u5728\u9762\u8BD5\u7684\u8FC7\u7A0B\u4E2D\u662F\u5982\u4F55\u8BB2\u8FF0\u81EA\u5DF1\u7684\u9879\u76EE\u7ECF\u5386\u7684\u5462? \u9762\u8BD5\u5B98\u53C8\u9488\u5BF9\u4F60\u7684\u9879\u76EE\u7ECF\u5386\u95EE\u4E86\u54EA\u4E9B\u95EE\u9898\u5462? \u4F60\u7684\u56DE\u7B54\u662F\u5426\u5F97\u5230\u4E86\u9762\u8BD5\u5B98\u7684\u80AF\u5B9A\u5462? \u6B22\u8FCE\u5728\u7559\u8A00\u533A\u7559\u4E0B\u4F60\u7684\u56F0\u60D1\uFF0C\u6211\u4F1A\u548C\u4F60\u4E00\u8D77\u8BA8\u8BBA\u603B\u7ED3\u3002</p><p>\u4E0B\u671F\u89C1\u3002</p>',21),r=[o];function i(s,l,c,n,d,u){return t(),p("div",null,r)}const h=e(a,[["render",i]]);export{N as __pageData,h as default};
