import{_ as t,o as p,c as o,a as r}from"./app.303cfecc.js";const u=JSON.parse('{"title":"\u7ED3\u675F\u8BED | \u5B66\u4E0D\u53EF\u4EE5\u5DF2","description":"","frontmatter":{},"headers":[],"relativePath":"0002/08\u7ED3\u675F/\u7ED3\u675F\u8BED-\u5B66\u4E0D\u53EF\u4EE5\u5DF2.md","lastUpdated":1680763907000}'),s={name:"0002/08\u7ED3\u675F/\u7ED3\u675F\u8BED-\u5B66\u4E0D\u53EF\u4EE5\u5DF2.md"},n=r('<h1 id="\u7ED3\u675F\u8BED-\u5B66\u4E0D\u53EF\u4EE5\u5DF2" tabindex="-1">\u7ED3\u675F\u8BED | \u5B66\u4E0D\u53EF\u4EE5\u5DF2 <a class="header-anchor" href="#\u7ED3\u675F\u8BED-\u5B66\u4E0D\u53EF\u4EE5\u5DF2" aria-hidden="true">#</a></h1><p>\u6211\u77E5\u9053\uFF0C\u6709\u4E00\u4E9B\u540C\u5B66\u5E0C\u671B\u591A\u4E00\u4E9B\u5B9E\u8DF5\u7684\u6848\u4F8B\u5206\u6790\uFF0C\u6211\u662F\u8FD9\u6837\u601D\u8003\u7684\uFF0C\u53E4\u4EBA\u5E38\u8BF4\u201C\u6E90\u4E0D\u6DF1\u800C\u671B\u6D41\u4E4B\u8FDC\uFF0C\u6839\u4E0D\u56FA\u800C\u6C42\u6728\u4E4B\u957F\uFF0C\u4E0D\u53EF\u201D\u3002\u4E00\u4E9B\u7406\u8BBA\u57FA\u7840\u662F\u5FC5\u8981\u7684\uFF0C\u5982\u6C34\u4E4B\u6E90\u3001\u6811\u4E4B\u6839\uFF0C\u662F\u4E0D\u80FD\u8DE8\u8D8A\u7684\u3002\u53E6\u5916\uFF0C\u4E00\u4E2A\u5B9E\u8DF5\u6848\u4F8B\u4E0D\u80FD\u5B8C\u5168\u6DB5\u76D6\u4E00\u4E2A\u7406\u8BBA\uFF0C\u76F8\u53CD\u4E00\u4E2A\u7406\u8BBA\u53EF\u4EE5\u652F\u6491\u5F88\u591A\u7684\u5B9E\u8DF5\u6848\u4F8B\u3002\u6B63\u6240\u8C13\u6388\u4E4B\u4EE5\u9C7C\u4E0D\u5982\u6388\u4E4B\u4EE5\u6E14\uFF0C\u6211\u4EEC\u4E0A\u6570\u5B66\u8BFE\u4E0D\u4E5F\u662F\u8981\u5148\u8BB2\u516C\u5F0F\u7684\u6765\u6E90\uFF0C\u518D\u89E3\u51B3\u5B9E\u9645\u95EE\u9898\u5417\uFF1F\u76F8\u4FE1\u5BF9\u7406\u8BBA\u77E5\u8BC6\u6D3B\u5B66\u6D3B\u7528\u540E\uFF0C\u4F60\u5728\u5B9E\u9645\u5DE5\u4F5C\u4E2D\uFF0C\u4F1A\u6536\u83B7\u96BE\u80FD\u53EF\u8D35\u7684\u7ECF\u9A8C\u8D22\u5BCC\uFF0C\u4E5F\u4F1A\u505A\u51FA\u66F4\u597D\u7684\u6280\u672F\u65B9\u6848\u3002</p><p>\u56DE\u987E\u8FD9\u4E9B\u5E74\u7684\u5DE5\u4F5C\uFF0C\u6211\u60F3\u548C\u4F60\u5206\u4EAB\u51E0\u70B9\u6211\u4E2A\u4EBA\u7684\u770B\u6CD5\u3002\u6211\u521A\u5F00\u59CB\u5DE5\u4F5C\u65F6\uFF0C\u7ECF\u5E38\u542C\u522B\u4EBA\u8BF4\u7A0B\u5E8F\u5458\u662F\u6709\u5E74\u9F84\u9650\u5236\u7684\uFF0C35 \u5C81\u662F\u7A0B\u5E8F\u5458\u7684\u7EC8\u7ED3\u5E74\u9F84\uFF0C\u90A3\u65F6\u8BF4\u5B9E\u8BDD\u6211\u5FC3\u91CC\u662F\u6709\u4E00\u4E9B\u5FD0\u5FD1\u7684\uFF0C\u53EF\u968F\u7740\u5E74\u9F84\u4E0D\u65AD\u589E\u957F\uFF0C\u6211\u770B\u5230\u8D8A\u6765\u8D8A\u591A\u7684\u4EBA\u5728 35 \u5C81\u4E4B\u540E\u8FD8\u5728\u884C\u4E1A\u4E2D\u5982\u9C7C\u5F97\u6C34\uFF0C\u6211\u60F3\uFF0C35 \u8FD9\u4E2A\u6570\u5B57\u5E76\u975E\u5F3A\u8C03\u4E2A\u4EBA\u7684\u5E74\u9F84\uFF0C\u800C\u662F\u6CDB\u6307\u4E00\u4E2A\u9636\u6BB5\uFF0C\u5F3A\u8C03\u5728\u90A3\u4E2A\u9636\u6BB5\uFF0C\u6211\u4EEC\u53EF\u80FD\u4F1A\u56E0\u4E3A\u4E2A\u4EBA\u7684\u79CD\u79CD\u539F\u56E0\u5B89\u4E8E\u73B0\u72B6\uFF0C\u4E0D\u518D\u66F4\u65B0\u81EA\u5DF1\u7684\u77E5\u8BC6\u5E93\uFF0C\u8FD9\u662F\u975E\u5E38\u9519\u8BEF\u7684\u3002</p><p><strong>\u5316\u7528\u300A\u793C\u8BB0\u300B\u4E2D\u7684\u8BDD\uFF0C\u9996\u5148\uFF0C\u6211\u4EEC\u8981\u535A\u5B66\u4E4B</strong>\u3002 \u4F60\u8981\u4E0D\u65AD\u9769\u65B0\u77E5\u8BC6\uFF0C\u6240\u8C13\u7684\u5929\u82B1\u677F\u5176\u5B9E\u66F4\u591A\u7684\u662F\u77E5\u8BC6\u6027\u7684\u5929\u82B1\u677F\uFF0C\u6D3B\u5230\u8001\u5B66\u5230\u8001\u624D\u662F\u4F60\u5728\u8FD9\u4E2A\u884C\u4E1A\u7684\u5FC5\u80DC\u6CD5\u5B9D\uFF0C\u6240\u4EE5\uFF0C\u6211\u4EEC\u5E94\u8BE5\u5229\u7528\u5404\u79CD\u4F18\u8D28\u5E73\u53F0\u4EE5\u53CA\u96F6\u6563\u7684\u65F6\u95F4\u5B66\u4E60\uFF0C\u4F46\u662F\u540C\u65F6\u4F60\u8981\u6CE8\u610F\uFF0C\u73B0\u5728\u7684\u77E5\u8BC6\u504F\u5411\u788E\u7247\u5316\uFF0C\u5982\u4F55\u6709\u6761\u7406\u3001\u7CFB\u7EDF\u5730\u5B66\u4E60\uFF0C\u5C06\u77E5\u8BC6\u68B3\u7406\u6210\u4F53\u7CFB\uFF0C\u5316\u4F5C\u81EA\u5DF1\u7684\u5185\u529F\uFF0C\u662F\u6BD4\u8F83\u5173\u952E\u548C\u56F0\u96BE\u7684\u3002<strong>\u5728\u8FD9\u91CC\u6211\u7ED9\u4F60\u51E0\u70B9\u5EFA\u8BAE\uFF1A</strong></p><ol><li>\u57FA\u7840\u77E5\u8BC6\u8981\u4F53\u7CFB\u5316\uFF0C\u8BFB\u4E66\u662F\u4E00\u79CD\u5F88\u597D\u7684\u83B7\u53D6\u4F53\u7CFB\u5316\u77E5\u8BC6\u7684\u9014\u5F84\uFF0C\u6BD4\u5982\u7814\u8BFB\u300A\u7B97\u6CD5\u5BFC\u8BBA\u300B\u63D0\u5347\u5BF9\u6570\u636E\u7ED3\u6784\u548C\u7B97\u6CD5\u7684\u7406\u89E3\uFF0C\u7814\u8BFB\u300ATCP/IP \u534F\u8BAE\u8BE6\u89E3\u300B\u6DF1\u5165\u7406\u89E3\u6211\u4EEC\u6700\u719F\u6089\u7684 TCP/IP \u534F\u8BAE\u6808\u7B49\u7B49\uFF1B</li><li>\u591A\u8BFB\u4E00\u4E9B\u7ECF\u5178\u9879\u76EE\u7684\u6E90\u4EE3\u7801\uFF0C\u6BD4\u5982 Dubbo\uFF0CSpring \u7B49\u7B49\uFF0C\u4ECE\u4E2D\u9886\u4F1A\u8BBE\u8BA1\u601D\u60F3\uFF0C\u4F60\u7684\u7F16\u7801\u80FD\u529B\u4F1A\u5F97\u5230\u6781\u5927\u7684\u63D0\u9AD8\uFF1B</li><li>\u591A\u5229\u7528\u788E\u7247\u5316\u7684\u65F6\u95F4\u8BFB\u4E00\u4E9B\u516C\u4F17\u53F7\u7684\u6587\u7AE0\uFF0C\u5F25\u8865\u4E66\u91CC\u6CA1\u6709\u5B9E\u8DF5\u6848\u4F8B\u7684\u4E0D\u8DB3\uFF0C\u501F\u6B64\u63D0\u5347\u6280\u672F\u89C6\u91CE\u3002</li></ol><p><strong>\u5176\u6B21\u8981\u614E\u601D\u4E4B\u3002</strong> \u8BDA\u7136\uFF0C\u770B\u4E66\u62D3\u5C55\u77E5\u8BC6\u7684\u8FC7\u7A0B\u4E2D\u6211\u4EEC\u9700\u8981\u601D\u8003\uFF0C\u5728\u5B9E\u9645\u5DE5\u4F5C\u4E2D\u6211\u4EEC\u4E5F\u9700\u8981\u6DF1\u5165\u601D\u8003\u3002\u6CA1\u6709\u4E00\u4E2A\u7406\u8BBA\u53EF\u4EE5\u9002\u5E94\u6240\u6709\u7684\u7A81\u53D1\u72B6\u51B5\uFF0C\u9AD8\u5E76\u53D1\u7CFB\u7EDF\u66F4\u662F\u5982\u6B64\u3002\u5B83\u72B6\u51B5\u767E\u51FA\uFF0C\u6211\u4EEC\u6700\u597D\u7684\u5E94\u5BF9\u65B9\u6CD5\u5C31\u662F\u5728\u7406\u8BBA\u7684\u6307\u5BFC\u4E0B\uFF0C\u5BF9\u6BCF\u4E00\u6B21\u7684\u7A81\u53D1\u72B6\u51B5\u90FD\u8FDB\u884C\u6DF1\u5165\u7684\u603B\u7ED3\u548C\u601D\u8003\u3002</p><p><strong>\u7136\u540E\u662F\u5BA1\u95EE\u4E4B\u3002</strong> \u8FD9\u79CD\u95EE\u65E2\u662F\u201C\u626A\u5FC3\u81EA\u95EE\u201D\uFF1A</p><ul><li>\u8FD9\u6B21\u7684\u7A81\u53D1\u95EE\u9898\u7684\u6839\u672C\u539F\u56E0\u662F\u4EC0\u4E48\uFF1F</li><li>\u4EE5\u540E\u5982\u4F55\u907F\u514D\u540C\u7C7B\u95EE\u9898\u7684\u518D\u6B21\u53D1\u751F\uFF1F</li><li>\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u6700\u4F18\u7684\u601D\u8DEF\u662F\u4EC0\u4E48\uFF1F</li></ul><p>\u540C\u65F6\uFF0C\u4E5F\u5E94\u8BE5\u662F\u4E00\u79CD\u4ED6\u95EE\uFF0C\u662F\u4E0E\u56E2\u961F\u5408\u4F5C\uFF0C\u5934\u8111\u98CE\u66B4\u4E4B\u540E\u7684\u4E00\u79CD\u8865\u5145\uFF0C\u6211\u4EEC\u8BF4\u4F60\u6709\u4E00\u4E2A\u82F9\u679C\uFF0C\u6211\u6709\u4E00\u4E2A\u82F9\u679C\u6211\u4EEC\u76F8\u4E92\u4EA4\u6362\uFF0C\u6BCF\u4E2A\u4EBA\u4F9D\u7136\u53EA\u6709\u4E00\u4E2A\u82F9\u679C\uFF0C\u4F46\u662F\u4F60\u6709\u4E00\u79CD\u601D\u60F3\uFF0C\u6211\u4E5F\u6709\u4E00\u79CD\u601D\u60F3\uFF0C\u6211\u4EEC\u76F8\u4E92\u4EA4\u6362\uFF0C\u6BCF\u4E2A\u4EBA\u5C31\u6709\u4E24\u79CD\u601D\u60F3\uFF0C\u6240\u4EE5\u4E0D\u65AD\u8FDB\u884C\u56E2\u961F\u4EA4\u6D41\u4E5F\u662F\u4E00\u79CD\u597D\u7684\u63D0\u5347\u81EA\u6211\u7684\u65B9\u5F0F\u3002</p><p><strong>\u63A5\u7740\u662F\u660E\u8FA8\u4E4B\u3002</strong> \u8FDB\u884C\u4E86\u5E7F\u6CDB\u7684\u9605\u8BFB\uFF0C\u79EF\u7D2F\u4E86\u5927\u91CF\u7684\u5DE5\u4F5C\u6848\u4F8B\uFF0C\u8FD8\u8981\u5C06\u8FD9\u4E9B\u5185\u5316\u4E8E\u5FC3\u7684\u77E5\u8BC6\u5F62\u6210\u6E05\u6670\u7684\u5224\u65AD\u529B\u3002\u67D0\u4E2A\u660E\u661F\u5FAE\u535A\u7684\u7A81\u7136\u6CA6\u9677\uFF0C\u793E\u533A\u7CFB\u7EDF\u7684\u7A81\u7136\u6302\u6389\uFF0C\u53EA\u662F\u5206\u5206\u949F\u7684\u4E8B\u60C5\uFF0C\u8981\u60F3\u6210\u4E3A\u4E00\u4E2A\u4F18\u79C0\u7684\u67B6\u6784\u5E08\uFF0C\u4F60\u5FC5\u987B\u8FD0\u7528\u81EA\u8EAB\u7684\u672C\u9886\u8FDB\u884C\u6E05\u6670\u7684\u5224\u65AD\uFF0C\u5FEB\u901F\u627E\u5230\u89E3\u51B3\u65B9\u6848\uFF0C\u53EA\u6709\u8FD9\u6837\u624D\u80FD\u628A\u635F\u5931\u63A7\u5236\u5728\u6700\u5C0F\u7684\u8303\u56F4\u5185\u3002\u800C\u8FD9\u79CD\u6E05\u6670\u7684\u5224\u65AD\u529B\u7EDD\u5BF9\u662F\u56E0\u4EBA\u800C\u5F02\u7684\uFF0C\u4F60\u6709\u600E\u6837\u7684\u77E5\u8BC6\u50A8\u5907\uFF0C\u6709\u600E\u6837\u7684\u6DF1\u5165\u601D\u8003\uFF0C\u5C31\u4F1A\u6709\u600E\u6837\u6E05\u6670\u7684\u5224\u65AD\u529B\u3002</p><p><strong>\u6700\u540E\u8981\u7B03\u884C\u4E4B\u3002</strong> \u5B66\u4E86\u518D\u591A\u7684\u7406\u8BBA\uFF0C\u505A\u4E86\u518D\u591A\u7684\u601D\u8003\uFF0C\u4E5F\u4E0D\u80FD\u786E\u4FDD\u80FD\u591F\u89E3\u51B3\u6240\u6709\u95EE\u9898\uFF0C\u5BF9\u4E8E\u9AD8\u5E76\u53D1\u95EE\u9898\uFF0C\u6211\u4EEC\u8FD8\u9700\u8981\u5728\u5B9E\u8DF5\u4E2D\u4E0D\u65AD\u63D0\u5347\u81EA\u5DF1\u7684\u80FD\u529B\u3002</p><p>\u76F8\u4FE1\u4F60\u7ECF\u5E38\u4F1A\u770B\u5230\u8FD9\u6837\u7684\u6BB5\u5B50\uFF0C\u6BD4\u5982\u5F88\u591A\u4EBA\u4F1A\u89C9\u5F97\u6211\u4EEC\u7684\u56FA\u5B9A\u5F62\u8C61\u5C31\u662F\u201C\u5E26\u7740\u773C\u955C\uFF0C\u7A7F\u7740\u683C\u5B50\u886C\u886B\uFF0C\u80CC\u7740\u53CC\u80A9\u5305\uFF0C\u53BB\u4F18\u8863\u5E93\u5C31\u662F\u4E00\u7B50\u7B50\u4E70\u8863\u670D\u201D\u3002\u8C03\u4F83\u5F52\u8C03\u4F83\uFF0C\u6211\u4EEC\u4E0D\u5FC5\u8BA4\u771F\uFF0C\u4E5F\u4E0D\u5FC5\u5BF9\u5916\u5728\u8FC7\u4E8E\u8FFD\u6C42\uFF0C\u56E0\u4E3A\u6700\u7EC8\u5F71\u54CD\u4F60\u804C\u4E1A\u751F\u6DAF\u7684\uFF0C\u662F\u601D\u8003\u3001\u662F\u5185\u6DB5\u3001\u662F\u77E5\u8BC6\u50A8\u5907\u3002<strong>\u90A3\u4E48\u5982\u4F55\u8BA9\u81EA\u5DF1\u66F4\u7CBE\u9510\u5462\uFF1F</strong></p><p>\u6211\u60F3\u9996\u5148\u8981\u6709\u68AF\u5EA6\u3002\u6211\u4EEC\u603B\u5E0C\u671B\u4EFB\u4F55\u5DE5\u4F5C\u90FD\u80FD\u6709\u4E2A\u8FDB\u5EA6\u6761\uFF0C\u6211\u4EEC\u7684\u804C\u4E1A\u751F\u6DAF\u4E5F\u5E94\u8BE5\u6709\u4E00\u4E2A\u6709\u68AF\u5EA6\u7684\u8FDB\u5EA6\u6761\uFF0C\u6BD4\u5982\uFF0C\u4ECE\u804C\u573A\u83DC\u9E1F\u5230\u5927\u795E\u518D\u5230\u8D22\u52A1\u81EA\u7531\uFF0C\u6BCF\u4E00\u6B65\u8981\u7528\u591A\u4E45\u7684\u65F6\u95F4\uFF0C\u5982\u4F55\u624D\u80FD\u4E00\u6B65\u4E00\u6B65\u4E0A\u5347\uFF0C\u5F53\u7136\uFF0C\u672A\u5FC5\u4EBA\u4EBA\u80FD\u591F\u5982\u9C7C\u5F97\u6C34\uFF0C\u4F46\u6709\u68A6\u60F3\u603B\u662F\u597D\u7684\uFF0C\u8FD9\u6837\u4F60\u624D\u6709\u76EE\u6807\uFF0C\u81EA\u5DF1\u7684\u751F\u6D3B\u624D\u4F1A\u6709\u5954\u5934\u3002</p><p>\u6709\u4E86\u68AF\u5EA6\u7684\u76EE\u6807\u4E4B\u540E\uFF0C\u63A5\u4E0B\u6765\u8981\u6709\u901F\u5EA6\uFF0C\u5C31\u50CF\u4EA7\u54C1\u903C\u8FEB\u4F60\u4E00\u6837\uFF0C\u4F60\u4E5F\u8981\u903C\u8FEB\u81EA\u5DF1\uFF0C\u8BA9\u81EA\u5DF1\u4E0D\u65AD\u5730\u52A0\u6CB9\uFF0C\u4E0D\u65AD\u5730\u66F4\u65B0\u3001\u63D0\u5347\u3001\u5B8C\u5584\uFF0C\u5C3D\u5FEB\u5B9E\u73B0\u81EA\u5DF1\u7684\u804C\u4E1A\u76EE\u6807\u3002</p><p>\u5177\u5907\u4E86\u8FD9\u4E24\u70B9\uFF0C\u5C31\u6709\u4E86\u4E00\u5B9A\u7684\u9AD8\u5EA6\uFF0C\u4F60\u662F\u7AD9\u5728\u4E00\u4E2A\u76EE\u6807\u9AD8\u5EA6\u4FEF\u89C6\u81EA\u5DF1\u7684\u751F\u6DAF\uFF0C\u662F\u9AD8\u5C4B\u5EFA\u74F4\uFF0C\u800C\u4E0D\u662F\u76F2\u76EE\u6500\u722C\u3002\u4E4B\u540E\u4F60\u9700\u8981\u505A\u5230\u7684\u662F\u6DF1\u5EA6\uFF0C\u6709\u7684\u670B\u53CB\u603B\u60F3\u6A2A\u5411\u62D3\u5C55\u81EA\u5DF1\u7684\u77E5\u8BC6\u9762\uFF0C\u60F3\u8981\u5B66\u4E60\u4E00\u4E9B\u65B0\u5947\u7684\u77E5\u8BC6\uFF0C\u8FD9\u4F1A\u63D0\u5347\u6280\u672F\u89C6\u91CE\uFF0C\u539F\u672C\u662F\u65E0\u53EF\u539A\u975E\u7684\uFF0C\u53EF\u5982\u679C\u56E0\u4E3A\u8FFD\u9010\u65B0\u7684\u6280\u672F\u800C\u653E\u5F03\u6DF1\u5165\u7406\u89E3\u57FA\u7840\u77E5\u8BC6\uFF0C\u90A3\u5C31\u6709\u4E9B\u5F97\u4E0D\u507F\u5931\u4E86\u3002\u8981\u77E5\u9053\uFF0C\u50CF\u662F\u7B97\u6CD5\u3001\u64CD\u4F5C\u7CFB\u7EDF\u3001\u7F51\u7EDC\u7B49\u57FA\u7840\u77E5\u8BC6\u5F88\u91CD\u8981\uFF0C\u53EA\u6709\u5728\u8FD9\u4E9B\u77E5\u8BC6\u5C42\u9762\u4E0A\u6709\u6DF1\u5165\u7684\u7406\u89E3\uFF0C\u624D\u80FD\u5728\u5B66\u4E60\u65B0\u6280\u672F\u7684\u65F6\u5019\u4E3E\u4E00\u53CD\u4E09\uFF0C\u52A0\u5FEB\u5B66\u4E60\u7684\u901F\u5EA6\uFF0C\u80FD\u591F\u5E2E\u52A9\u4F60\u66F4\u5FEB\u5730\u63D0\u5347\u5E7F\u5EA6\u3002</p><p>\u4F60\u8FD8\u8981\u6709\u70ED\u5EA6\u3002\u6211\u4EEC\u767D\u5929\u548C\u4EA7\u54C1\u7ECF\u7406\u201C\u76F8\u7231\u76F8\u6740\u201D\uFF0C\u665A\u4E0A\u62AB\u661F\u6234\u6708\u56DE\u5BB6\u4E0E\u5BB6\u4EBA\u201C\u76F8\u7231\u76F8\u6740\u201D\uFF0C\u5982\u679C\u6CA1\u6709\u8DB3\u591F\u7684\u5DE5\u4F5C\u70ED\u5EA6\uFF0C\u8FD9\u6837\u7684\u65E5\u5B50\u5FAA\u73AF\u5F80\u590D\uFF0C\u4F60\u600E\u4E48\u53EF\u80FD\u5403\u5F97\u6D88\uFF1F\u800C\u53EA\u6709\u5F53\u4F60\u5728\u81EA\u5DF1\u7684\u884C\u4E1A\u91CC\u89C4\u5212\u4E86\u68AF\u5EA6\u3001\u63D0\u5347\u4E86\u901F\u5EA6\u3001\u5F3A\u5316\u4E86\u6DF1\u5EA6\u3001\u62D3\u5BBD\u4E86\u5E7F\u5EA6\uFF0C\u624D\u4F1A\u6709\u8DB3\u591F\u7684\u81EA\u4FE1\u5EA6\uFF0C\u800C\u5F53\u4F60\u6709\u4E86\u81EA\u4FE1\uFF0C\u6709\u4E86\u8BDD\u8BED\u6743\uFF0C\u90A3\u65F6\u4F60\u5C31\u6709\u4E86\u5E78\u798F\u611F\uFF0C\u81EA\u7136\u4F1A\u4FDD\u6709\u70ED\u5EA6\u3002\u5728\u70ED\u5EA6\u7684\u70D8\u7119\u4E0B\uFF0C\u4F60\u53C8\u5F00\u59CB\u65B0\u4E00\u8F6E\u89C4\u5212\uFF0C\u5982\u6B64\u826F\u6027\u5FAA\u73AF\uFF0C\u4F60\u624D\u4F1A\u5728\u5DE5\u4F5C\u4E0A\u6E38\u5203\u6709\u4F59\uFF0C\u751F\u6D3B\u4E5F\u4F1A\u5E78\u798F\u5FEB\u4E50\u3002</p><p>\u5728\u6587\u7AE0\u7ED3\u5C3E\uFF0C\u6211\u4E3A\u4F60\u51C6\u5907\u4E86\u4E00\u4EFD\u8C03\u67E5\u95EE\u5377\uFF0C\u9898\u76EE\u4E0D\u591A\uFF0C\u5E0C\u671B\u4F60\u80FD\u62BD\u51FA\u4E24\u4E09\u5206\u949F\u586B\u5199\u4E00\u4E0B\u3002\u6211\u975E\u5E38\u5E0C\u671B\u542C\u542C\u4F60\u5BF9\u8FD9\u4E2A\u4E13\u680F\u7684\u610F\u89C1\u548C\u5EFA\u8BAE\uFF0C\u671F\u5F85\u4F60\u7684\u53CD\u9988\uFF01\u4E13\u680F\u7684\u7ED3\u675F\uFF0C\u4E5F\u662F\u53E6\u4E00\u79CD\u5F00\u59CB\uFF0C\u6211\u4F1A\u5C06\u5185\u5BB9\u8FDB\u884C\u8FED\u4EE3\uFF0C\u6BD4\u5982 11 \u6708\u4E2D\u65EC\u5230 12 \u6708\u672B\uFF0C\u6211\u6709\u4E3A\u671F\u4E00\u4E2A\u6708\u7684\u5C01\u95ED\u671F\uFF0C\u5728\u8FD9\u671F\u95F4\u6CA1\u6709\u6765\u5F97\u53CA\u56DE\u590D\u7684\u7559\u8A00\uFF0C\u6211\u4F1A\u82B1\u65F6\u95F4\u5904\u7406\u5B8C\uFF1B\u518D\u6BD4\u5982\uFF0C\u4F1A\u9488\u5BF9\u4E00\u4E9B\u540C\u5B66\u7684\u5171\u6027\u95EE\u9898\u7B56\u5212\u4E00\u671F\u7B54\u7591\u6216\u8005\u52A0\u9910\u3002</p><p>\u6700\u540E\uFF0C\u6211\u60F3\u518D\u6B21\u5F3A\u8C03\u4E00\u4E0B\u4E3A\u4EC0\u4E48\u8981\u52AA\u529B\u63D0\u5347\u81EA\u5DF1\uFF0C\u63D0\u5347\u4E1A\u52A1\u80FD\u529B\uFF0C<strong>\u76F4\u767D\u4E00\u70B9\u513F\u8BF4\uFF0C\u90A3\u662F\u5E0C\u671B\u6211\u4EEC\u90FD\u6709\u81EA\u4E3B\u9009\u62E9\u7684\u6743\u5229\uFF0C\u800C\u4E0D\u662F\u88AB\u8FEB\u8C0B\u751F\uFF1B\u6211\u6709\u8BDD\u8BED\u6743\uFF0C\u800C\u4E0D\u662F\u88AB\u8FEB\u6267\u884C\uFF0C\u968F\u7740\u5E74\u9F84\u7684\u589E\u52A0\uFF0C\u6211\u8D8A\u53D1\u89C9\u5F97\u6210\u5C31\u611F\u548C\u5C0A\u4E25\uFF0C\u80FD\u591F\u5E26\u7ED9\u6211\u4EEC\u5FEB\u4E50\u3002</strong></p><p>\u8877\u5FC3\u795D\u613F\u6211\u4EEC\u90FD\u80FD\u591F\u5FEB\u4E50\u5E78\u798F\u5730\u5DE5\u4F5C\uFF0C\u611F\u8C22\u4F60\u7684\u8046\u542C\uFF0C\u4E0E\u4F60\u540C\u5728\u3002</p>',19),e=[n];function _(a,i,l,c,d,g){return p(),o("div",null,e)}const f=t(s,[["render",_]]);export{u as __pageData,f as default};
