import{_ as e,o as i,c as a,a as l}from"./app.303cfecc.js";const m=JSON.parse('{"title":"\u5FAE\u670D\u52A1","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u4F20\u7EDF\u9879\u76EE\u8F6C\u578B","slug":"\u4F20\u7EDF\u9879\u76EE\u8F6C\u578B","link":"#\u4F20\u7EDF\u9879\u76EE\u8F6C\u578B","children":[]},{"level":2,"title":"\u8D70\u8FDBSpringCloud","slug":"\u8D70\u8FDBspringcloud","link":"#\u8D70\u8FDBspringcloud","children":[]}],"relativePath":"study/cloud/index.md","lastUpdated":1680763907000}'),n={name:"study/cloud/index.md"},t=l('<h1 id="\u5FAE\u670D\u52A1" tabindex="-1">\u5FAE\u670D\u52A1 <a class="header-anchor" href="#\u5FAE\u670D\u52A1" aria-hidden="true">#</a></h1><nav class="table-of-contents"><ul><li><a href="#\u4F20\u7EDF\u9879\u76EE\u8F6C\u578B">\u4F20\u7EDF\u9879\u76EE\u8F6C\u578B</a></li><li><a href="#\u8D70\u8FDBspringcloud">\u8D70\u8FDBSpringCloud</a></li></ul></nav><p>\u901A\u8FC7\u4F7F\u7528SpringBoot\u6846\u67B6\uFF0C\u6211\u4EEC\u7684\u9879\u76EE\u5F00\u53D1\u901F\u5EA6\u53EF\u4EE5\u8BF4\u662F\u5F97\u5230\u4E86\u8D28\u7684\u63D0\u5347\u3002\u540C\u65F6\uFF0C\u6211\u4EEC\u5BF9\u4E8E\u9879\u76EE\u7684\u7EF4\u62A4\u548C\u7406\u89E3\uFF0C\u4E5F\u4F1A\u66F4\u52A0\u7684\u8F7B\u677E\u3002\u53EF\u89C1\uFF0CSpringBoot\u4E3A\u6211\u4EEC\u7684\u5F00\u53D1\u5E26\u6765\u4E86\u5DE8\u5927\u4FBF\u6377\u3002</p><h2 id="\u4F20\u7EDF\u9879\u76EE\u8F6C\u578B" tabindex="-1">\u4F20\u7EDF\u9879\u76EE\u8F6C\u578B <a class="header-anchor" href="#\u4F20\u7EDF\u9879\u76EE\u8F6C\u578B" aria-hidden="true">#</a></h2><p>\u8981\u8BF4\u8FD1\u51E0\u5E74\u6700\u706B\u70ED\u7684\u8BDD\u9898\uFF0C\u90A3\u8FD8\u5F97\u662F\u5FAE\u670D\u52A1\uFF0C\u90A3\u4E48\u4EC0\u4E48\u662F\u5FAE\u670D\u52A1\u5462\uFF1F</p><p>\u6211\u4EEC\u53EF\u4EE5\u5148\u4ECE\u6280\u672F\u7684\u6F14\u53D8\u5F00\u59CB\u770B\u8D77\uFF0C\u5728\u6211\u4EEC\u5B66\u4E60JavaWeb\u4E4B\u540E\uFF0C\u4E00\u822C\u7684\u7F51\u7AD9\u5F00\u53D1\u6A21\u5F0F\u4E3AServlet+JSP\uFF0C\u4F46\u662F\u5B9E\u9645\u4E0A\u6211\u4EEC\u5728\u5B66\u4E60\u4E86SSM\u4E4B\u540E\uFF0C\u4F1A\u53D1\u73B0\u8FD9\u79CD\u6A21\u5F0F\u5DF2\u7ECF\u8FDC\u8FDC\u843D\u540E\u4E86\uFF0C\u7B2C\u4E00\uFF0C\u4E00\u4E2A\u516C\u53F8\u4E0D\u53EF\u80FD\u53BB\u62DB\u90A3\u4E48\u591A\u540C\u65F6\u4F1A\u524D\u7AEF+\u540E\u7AEF\u7684\u5F00\u53D1\u4EBA\u5458\uFF0C\u5C31\u7B97\u62DB\u5230\uFF0C\u4E5F\u5E76\u4E0D\u4E00\u5B9A\u80FD\u4FDD\u8BC1\u4E24\u4E2A\u65B9\u9762\u90FD\u6BD4\u8F83\u64C5\u957F\uFF0C\u76F8\u6BD4\u524D\u540E\u7AEF\u5206\u5F00\u5B66\u4E60\u7684\u5F00\u53D1\u4EBA\u5458\uFF0C\u663E\u7136\u540E\u8005\u7684\u5B66\u4E60\u6210\u672C\u66F4\u4F4E\uFF0C\u4E13\u6CE8\u5EA6\u66F4\u9AD8\u3002\u56E0\u6B64\u524D\u540E\u7AEF\u5206\u79BB\u6210\u4E3A\u4E86\u4E00\u79CD\u65B0\u7684\u8D8B\u52BF\u3002\u901A\u8FC7\u4F7F\u7528SpringBoot\uFF0C\u6211\u4EEC\u51E0\u4E4E\u53EF\u4EE5\u5F88\u5FEB\u901F\u5730\u5F00\u53D1\u4E00\u4E2A\u9AD8\u6027\u80FD\u7684\u5355\u4F53\u5E94\u7528\uFF0C\u53EA\u9700\u8981\u542F\u52A8\u4E00\u4E2A\u670D\u52A1\u7AEF\uFF0C\u6211\u4EEC\u6574\u4E2A\u9879\u76EE\u5C31\u5F00\u59CB\u8FD0\u884C\u4E86\uFF0C\u5404\u9879\u529F\u80FD\u878D\u4E8E\u4E00\u4F53\uFF0C\u5F00\u53D1\u8D77\u6765\u4E5F\u66F4\u52A0\u8F7B\u677E\u3002</p><p>\u4F46\u662F\u968F\u7740\u6211\u4EEC\u9879\u76EE\u7684\u4E0D\u65AD\u6269\u5927\uFF0C\u5355\u4F53\u5E94\u7528\u4F3C\u4E4E\u663E\u5F97\u6709\u70B9\u4E4F\u529B\u4E86\u3002</p><p>\u968F\u7740\u8D8A\u6765\u8D8A\u591A\u7684\u529F\u80FD\u4E0D\u65AD\u5730\u52A0\u5165\u5230\u4E00\u4E2ASpringBoot\u9879\u76EE\u4E2D\uFF0C\u968F\u7740\u63A5\u53E3\u4E0D\u65AD\u589E\u52A0\uFF0C\u6574\u4E2A\u7CFB\u7EDF\u5C31\u8981\u5728\u540C\u4E00\u65F6\u95F4\u5185\u54CD\u5E94\u66F4\u591A\u7C7B\u578B\u7684\u8BF7\u6C42\uFF0C\u663E\u7136\uFF0C\u8FD9\u79CD\u6269\u5C55\u65B9\u5F0F\u662F\u4E0D\u53EF\u80FD\u65E0\u9650\u4F7F\u7528\u4E0B\u53BB\u7684\uFF0C\u603B\u6709\u4E00\u5929\uFF0C\u8FD9\u4E2ASpringBoot\u9879\u76EE\u4F1A\u5E9E\u5927\u5230\u8FD0\u884C\u7F13\u6162\u3002\u5E76\u4E14\u6240\u6709\u7684\u529F\u80FD\u5982\u679C\u90FD\u96C6\u6210\u5728\u5355\u7AEF\u4E0A\uFF0C\u90A3\u4E48\u6240\u6709\u7684\u8BF7\u6C42\u90FD\u4F1A\u5168\u90E8\u6C47\u96C6\u5230\u4E00\u53F0\u670D\u52A1\u5668\u4E0A\uFF0C\u5BF9\u6B64\u670D\u52A1\u5668\u9020\u6210\u5DE8\u5927\u538B\u529B\u3002</p><p>\u4F20\u7EDF\u5355\u4F53\u67B6\u6784\u5E94\u7528\u968F\u7740\u9879\u76EE\u89C4\u6A21\u7684\u6269\u5927\uFF0C\u5B9E\u9645\u4E0A\u4F1A\u66B4\u9732\u8D8A\u6765\u8D8A\u591A\u7684\u95EE\u9898\uFF0C\u5C24\u5176\u662F\u4E00\u53F0\u670D\u52A1\u5668\u65E0\u6CD5\u627F\u53D7\u5E9E\u5927\u7684\u5355\u4F53\u5E94\u7528\u90E8\u7F72\uFF0C\u5E76\u4E14\u5355\u4F53\u5E94\u7528\u7684\u7EF4\u62A4\u4E5F\u4F1A\u8D8A\u6765\u8D8A\u56F0\u96BE\uFF0C\u6211\u4EEC\u5F97\u5BFB\u627E\u4E00\u79CD\u65B0\u7684\u5F00\u53D1\u67B6\u6784\u6765\u89E3\u51B3\u8FD9\u4E9B\u95EE\u9898\u4E86\u3002</p><blockquote><p>In short, the microservice architectural style is an approach to developing a single application as a suite of small services, each running in its own process and communicating with lightweight mechanisms, often an HTTP resource API. These services are built around business capabilities and independently deployable by fully automated deployment machinery. There is a bare minimum of centralized management of these services, which may be written in different programming languages and use different data storage technologies.</p></blockquote><p>Martin Fowler\u57282014\u5E74\u63D0\u51FA\u4E86\u201C\u5FAE\u670D\u52A1\u201D\u67B6\u6784\uFF0C\u5B83\u662F\u4E00\u79CD\u5168\u65B0\u7684\u67B6\u6784\u98CE\u683C\u3002</p><ul><li>\u5FAE\u670D\u52A1\u628A\u4E00\u4E2A\u5E9E\u5927\u7684\u5355\u4F53\u5E94\u7528\u62C6\u5206\u4E3A\u4E00\u4E2A\u4E2A\u7684\u5C0F\u578B\u670D\u52A1\uFF0C\u72EC\u7ACB\u8FD0\u884C\u3002</li><li>\u6BCF\u4E2A\u5C0F\u578B\u7684\u5FAE\u670D\u52A1\uFF0C\u90FD\u53EF\u4EE5\u72EC\u7ACB\u90E8\u7F72\u548C\u5347\u7EA7\uFF0C\u8FD9\u6837\uFF0C\u5C31\u7B97\u6574\u4E2A\u7CFB\u7EDF\u5D29\u6E83\uFF0C\u90A3\u4E48\u4E5F\u53EA\u4F1A\u5F71\u54CD\u4E00\u4E2A\u670D\u52A1\u7684\u8FD0\u884C\u3002</li><li>\u5FAE\u670D\u52A1\u4E4B\u95F4\u4F7F\u7528HTTP\u8FDB\u884C\u6570\u636E\u4EA4\u4E92\uFF0C\u4E0D\u518D\u662F\u5355\u4F53\u5E94\u7528\u5185\u90E8\u4EA4\u4E92\u4E86\uFF0C\u867D\u7136\u8FD9\u6837\u4F1A\u663E\u5F97\u66F4\u9EBB\u70E6\uFF0C\u4F46\u662F\u5E26\u6765\u7684\u597D\u5904\u4E5F\u662F\u5F88\u76F4\u63A5\u7684\uFF0C\u751A\u81F3\u80FD\u7A81\u7834\u8BED\u8A00\u9650\u5236\uFF0C\u4F7F\u7528\u4E0D\u540C\u7684\u7F16\u7A0B\u8BED\u8A00\u8FDB\u884C\u5FAE\u670D\u52A1\u5F00\u53D1\uFF0C\u53EA\u9700\u8981\u4F7F\u7528HTTP\u8FDB\u884C\u6570\u636E\u4EA4\u4E92\u5373\u53EF\u3002</li><li>\u6211\u4EEC\u53EF\u4EE5\u540C\u65F6\u8D2D\u4E70\u591A\u53F0\u4E3B\u673A\u6765\u5206\u522B\u90E8\u7F72\u8FD9\u4E9B\u5FAE\u670D\u52A1\uFF0C\u8FD9\u6837\uFF0C\u5355\u673A\u7684\u538B\u529B\u5C31\u88AB\u5206\u6563\u5230\u591A\u53F0\u673A\u5668\uFF0C\u5E76\u4E14\u6BCF\u53F0\u673A\u5668\u7684\u914D\u7F6E\u4E0D\u4E00\u5B9A\u9700\u8981\u592A\u9AD8\uFF0C\u8FD9\u6837\u5C31\u80FD\u8282\u7701\u5927\u91CF\u7684\u6210\u672C\uFF0C\u540C\u65F6\u5B89\u5168\u6027\u4E5F\u5F97\u5230\u5F88\u5927\u7684\u4FDD\u8BC1\u3002</li><li>\u751A\u81F3\u540C\u4E00\u4E2A\u5FAE\u670D\u52A1\u53EF\u4EE5\u540C\u65F6\u5B58\u5728\u591A\u4E2A\uFF0C\u8FD9\u6837\u5F53\u5176\u4E2D\u4E00\u4E2A\u670D\u52A1\u5668\u51FA\u73B0\u95EE\u9898\u65F6\uFF0C\u5176\u4ED6\u670D\u52A1\u5668\u4E5F\u5728\u8FD0\u884C\u540C\u6837\u7684\u5FAE\u670D\u52A1\uFF0C\u8FD9\u6837\u5C31\u53EF\u4EE5\u4FDD\u8BC1\u4E00\u4E2A\u5FAE\u670D\u52A1\u7684\u9AD8\u53EF\u7528\u3002</li></ul><p>\u53EF\u89C1\uFF0C\u91C7\u7528\u5FAE\u670D\u52A1\u67B6\u6784\uFF0C\u66F4\u52A0\u80FD\u591F\u5E94\u5BF9\u5F53\u4ECA\u65F6\u4EE3\u4E0B\u7684\u79CD\u79CD\u8003\u9A8C\uFF0C\u4F20\u7EDF\u9879\u76EE\u7684\u5F00\u53D1\u6A21\u5F0F\uFF0C\u9700\u8981\u8FDB\u884C\u67B6\u6784\u4E0A\u7684\u5347\u7EA7\u3002</p><h2 id="\u8D70\u8FDBspringcloud" tabindex="-1">\u8D70\u8FDBSpringCloud <a class="header-anchor" href="#\u8D70\u8FDBspringcloud" aria-hidden="true">#</a></h2><p>\u524D\u9762\u4ECB\u7ECD\u4E86\u5FAE\u670D\u52A1\u67B6\u6784\u7684\u4F18\u70B9\uFF0C\u90A3\u4E48\u540C\u6837\u7684\uFF0C\u8FD9\u4E9B\u4F18\u70B9\u7684\u80CC\u540E\u4E5F\u5B58\u5728\u7740\u8BF8\u591A\u7684\u95EE\u9898\uFF1A</p><ul><li>\u8981\u5B9E\u73B0\u5FAE\u670D\u52A1\u5E76\u4E0D\u662F\u8BF4\u53EA\u9700\u8981\u7B80\u5355\u5730\u5C06\u9879\u76EE\u8FDB\u884C\u62C6\u5206\uFF0C\u6211\u4EEC\u8FD8\u9700\u8981\u8003\u8651\u5BF9\u5404\u4E2A\u5FAE\u670D\u52A1\u8FDB\u884C\u7BA1\u7406\u3001\u76D1\u63A7\u7B49\uFF0C\u8FD9\u6837\u6211\u4EEC\u624D\u80FD\u591F\u53CA\u65F6\u5730\u5BFB\u627E\u548C\u6392\u67E5\u95EE\u9898\u3002\u56E0\u6B64\u5FAE\u670D\u52A1\u5F80\u5F80\u9700\u8981\u7684\u662F\u4E00\u6574\u5957\u89E3\u51B3\u65B9\u6848\uFF0C\u5305\u62EC\u670D\u52A1\u6CE8\u518C\u548C\u53D1\u73B0\u3001\u5BB9\u707E\u5904\u7406\u3001\u8D1F\u8F7D\u5747\u8861\u3001\u914D\u7F6E\u7BA1\u7406\u7B49\u3002</li><li>\u5B83\u4E0D\u50CF\u5355\u4F53\u67B6\u6784\u90A3\u79CD\u65B9\u4FBF\u7EF4\u62A4\uFF0C\u7531\u4E8E\u90E8\u7F72\u5728\u591A\u4E2A\u670D\u52A1\u5668\uFF0C\u6211\u4EEC\u4E0D\u5F97\u4E0D\u53BB\u4FDD\u8BC1\u5404\u4E2A\u5FAE\u670D\u52A1\u80FD\u591F\u7A33\u5B9A\u8FD0\u884C\uFF0C\u5728\u7BA1\u7406\u96BE\u5EA6\u4E0A\u80AF\u5B9A\u662F\u9AD8\u4E8E\u4F20\u7EDF\u5355\u4F53\u5E94\u7528\u7684\u3002</li><li>\u5728\u5206\u5E03\u5F0F\u7684\u73AF\u5883\u4E0B\uFF0C\u5355\u4F53\u5E94\u7528\u7684\u67D0\u4E9B\u529F\u80FD\u53EF\u80FD\u4F1A\u53D8\u5F97\u6BD4\u8F83\u9EBB\u70E6\uFF0C\u6BD4\u5982\u5206\u5E03\u5F0F\u4E8B\u52A1\u3002</li></ul><p>\u6240\u4EE5\uFF0C\u4E3A\u4E86\u66F4\u597D\u5730\u89E3\u51B3\u8FD9\u4E9B\u95EE\u9898\uFF0CSpringCloud\u6B63\u5F0F\u767B\u573A\u3002</p><p>SpringCloud\u662FSpring\u63D0\u4F9B\u7684\u4E00\u5957\u5206\u5E03\u5F0F\u89E3\u51B3\u65B9\u6848\uFF0C\u96C6\u5408\u4E86\u4E00\u4E9B\u5927\u578B\u4E92\u8054\u7F51\u516C\u53F8\u7684\u5F00\u6E90\u4EA7\u54C1\uFF0C\u5305\u62EC\u8BF8\u591A\u7EC4\u4EF6\uFF0C\u5171\u540C\u7EC4\u6210SpringCloud\u6846\u67B6\u3002\u5E76\u4E14\uFF0C\u5B83\u5229\u7528Spring Boot\u7684\u5F00\u53D1\u4FBF\u5229\u6027\u5DE7\u5999\u5730\u7B80\u5316\u4E86\u5206\u5E03\u5F0F\u7CFB\u7EDF\u57FA\u7840\u8BBE\u65BD\u7684\u5F00\u53D1\uFF0C\u5982\u670D\u52A1\u53D1\u73B0\u6CE8\u518C\u3001\u914D\u7F6E\u4E2D\u5FC3\u3001\u6D88\u606F\u603B\u7EBF\u3001\u8D1F\u8F7D\u5747\u8861\u3001\u7194\u65AD\u673A\u5236\u3001\u6570\u636E\u76D1\u63A7\u7B49\uFF0C\u90FD\u53EF\u4EE5\u7528Spring Boot\u7684\u5F00\u53D1\u98CE\u683C\u505A\u5230\u4E00\u952E\u542F\u52A8\u548C\u90E8\u7F72\u3002</p><p>\u7531\u4E8E\u4E2D\u5C0F\u578B\u516C\u53F8\u6CA1\u6709\u72EC\u7ACB\u5F00\u53D1\u81EA\u5DF1\u7684\u5206\u5E03\u5F0F\u57FA\u7840\u8BBE\u65BD\u7684\u80FD\u529B\uFF0C\u4F7F\u7528SpringCloud\u89E3\u51B3\u65B9\u6848\u80FD\u591F\u4EE5\u6700\u4F4E\u7684\u6210\u672C\u5E94\u5BF9\u5F53\u524D\u65F6\u4EE3\u7684\u4E1A\u52A1\u53D1\u5C55\u3002</p><p><img src="https://tva1.sinaimg.cn/large/e6c9d24egy1h0ig93rk52j21ss0nytbj.jpg" alt="image-20220322102706256"></p><p>\u53EF\u4EE5\u770B\u5230\uFF0CSpringCloud\u6574\u4F53\u67B6\u6784\u7684\u4EAE\u70B9\u662F\u975E\u5E38\u660E\u663E\u7684\uFF0C\u5206\u5E03\u5F0F\u67B6\u6784\u4E0B\u7684\u5404\u4E2A\u573A\u666F\uFF0C\u90FD\u6709\u5BF9\u5E94\u7684\u7EC4\u4EF6\u6765\u5904\u7406\uFF0C\u6BD4\u5982\u57FA\u4E8ENetflix\uFF08\u5948\u98DE\uFF09\u7684\u5F00\u6E90\u5206\u5E03\u5F0F\u89E3\u51B3\u65B9\u6848\u63D0\u4F9B\u7684\u7EC4\u4EF6\uFF1A</p><ul><li>Eureka - \u5B9E\u73B0\u670D\u52A1\u6CBB\u7406\uFF08\u670D\u52A1\u6CE8\u518C\u4E0E\u53D1\u73B0\uFF09\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5BF9\u6240\u6709\u7684\u5FAE\u670D\u52A1\u8FDB\u884C\u96C6\u4E2D\u7BA1\u7406\uFF0C\u5305\u62EC\u4ED6\u4EEC\u7684\u8FD0\u884C\u72B6\u6001\u3001\u4FE1\u606F\u7B49\u3002</li><li>Ribbon - \u4E3A\u670D\u52A1\u4E4B\u95F4\u76F8\u4E92\u8C03\u7528\u63D0\u4F9B\u8D1F\u8F7D\u5747\u8861\u7B97\u6CD5\uFF08\u73B0\u5728\u88ABSpringCloudLoadBalancer\u53D6\u4EE3\uFF09</li><li>Hystrix - \u65AD\u8DEF\u5668\uFF0C\u4FDD\u62A4\u7CFB\u7EDF\uFF0C\u63A7\u5236\u6545\u969C\u8303\u56F4\u3002\u6682\u65F6\u53EF\u4EE5\u8DDF\u5BB6\u91CC\u7535\u95F8\u7684\u4FDD\u9669\u4E1D\u7C7B\u6BD4\uFF0C\u5F53\u89E6\u7535\u5371\u9669\u53D1\u751F\u65F6\u80FD\u591F\u9632\u6B62\u8FDB\u4E00\u6B65\u7684\u53D1\u5C55\u3002</li><li>Zuul - api\u7F51\u5173\uFF0C\u8DEF\u7531\uFF0C\u8D1F\u8F7D\u5747\u8861\u7B49\u591A\u79CD\u4F5C\u7528\uFF0C\u5C31\u50CF\u6211\u4EEC\u7684\u8DEF\u7531\u5668\uFF0C\u53EF\u80FD\u6709\u5F88\u591A\u4E2A\u8BBE\u5907\u90FD\u8FDE\u63A5\u4E86\u8DEF\u7531\u5668\uFF0C\u4F46\u662F\u6570\u636E\u5305\u8981\u8F6C\u53D1\u7ED9\u8C01\u5219\u662F\u7531\u8DEF\u7531\u5668\u5728\u8FDB\u884C\uFF08\u5DF2\u7ECF\u88ABSpringCloudGateway\u53D6\u4EE3\uFF09</li><li>Config - \u914D\u7F6E\u7BA1\u7406\uFF0C\u53EF\u4EE5\u5B9E\u73B0\u914D\u7F6E\u6587\u4EF6\u96C6\u4E2D\u7BA1\u7406</li></ul><p>\u5F53\u7136\uFF0C\u8FD9\u91CC\u53EA\u662F\u8FDB\u884C\u7B80\u5355\u7684\u4E86\u89E3\u5373\u53EF\uFF0C\u5B9E\u9645\u4E0A\u5FAE\u670D\u52A1\u7684\u73A9\u6CD5\u975E\u5E38\u591A\uFF0C\u540E\u9762\u5B66\u4E60\u4E2D\u5C06\u4F1A\u9010\u6B65\u8FDB\u884C\u63A2\u7D22\u3002</p><p><a href="./../../cloud/netflix/eureka.html">\u5F00\u59CB\u5B66\u4E60</a></p>',24),r=[t];function o(p,s,d,c,u,g){return i(),a("div",null,r)}const S=e(n,[["render",o]]);export{m as __pageData,S as default};
